import axios from 'axios';
import { 
  Persona, PersonaResponse, Conversation, GlobalMessage, ApiResponse,
  InterpretationStatus, InterpretationResponse, InterpretationRequest,
  ProceedRequest, ProceedResponse, SentenceRequest, SentenceResponse,
  TrainingThread, InteractionRecord, ThreadChatRequest, ThreadChatResponse,
  GenerateWithExtrasResponse
} from '../types';

export const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

export const chatApi = {
  // 페르소나 정보 가져오기
  async getPersonas(): Promise<ApiResponse<Record<string, Persona>>> {
    try {
      const response = await api.get('/personas');
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '페르소나 정보를 가져오는데 실패했습니다.' };
    }
  },

  // 해석 상태 조회
  async getInterpretationStatus(): Promise<ApiResponse<InterpretationStatus[]>> {
    try {
      const response = await api.get('/interpretations/status');
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '해석 상태 조회에 실패했습니다.' };
    }
  },

  // 해석 생성
  async generateInterpretation(request: InterpretationRequest): Promise<ApiResponse<InterpretationResponse>> {
    try {
      const response = await api.post('/interpretations/generate', request);
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '해석 생성에 실패했습니다.' };
    }
  },

  // 해석 조회
  async getInterpretation(personaId: string): Promise<ApiResponse<InterpretationResponse>> {
    try {
      const response = await api.get(`/interpretations/${personaId}`);
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '해석 조회에 실패했습니다.' };
    }
  },

  // 초기 채팅 (모든 페르소나 응답)
  async sendInitialMessage(content: string): Promise<ApiResponse<{
    message_id: string;
    responses: PersonaResponse[];
  }>> {
    try {
      const response = await api.post('/chat/initial', { content });
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '메시지 전송에 실패했습니다.' };
    }
  },

  // 특정 페르소나와 대화 이어가기
  async continueConversation(
    persona_id: string,
    user_message: string,
    conversation_id: string
  ): Promise<ApiResponse<PersonaResponse>> {
    try {
      const response = await api.post('/chat/continue', {
        persona_id,
        user_message,
        conversation_id,
      });
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '대화 이어가기에 실패했습니다.' };
    }
  },

  // 특정 대화 기록 가져오기
  async getConversation(conversation_id: string): Promise<ApiResponse<Conversation>> {
    try {
      const response = await api.get(`/conversations/${conversation_id}`);
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '대화 기록을 가져오는데 실패했습니다.' };
    }
  },

  // 모든 대화 기록 가져오기
  async getAllConversations(): Promise<ApiResponse<Conversation[]>> {
    try {
      const response = await api.get('/conversations');
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '대화 목록을 가져오는데 실패했습니다.' };
    }
  },

  // 전체 메시지 기록 가져오기
  async getGlobalMessages(): Promise<ApiResponse<GlobalMessage[]>> {
    try {
      const response = await api.get('/messages');
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '메시지 기록을 가져오는데 실패했습니다.' };
    }
  },

  // 메시지 수정
  async editMessage(
    conversationId: string,
    messageIndex: number,
    newContent: string
  ): Promise<ApiResponse<{ 
    success: boolean; 
    message: string; 
    updated_conversation?: Conversation;
    new_response?: PersonaResponse;
  }>> {
    try {
      const response = await api.put(`/conversations/${conversationId}/messages/${messageIndex}`, {
        new_content: newContent
      });
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '메시지 수정에 실패했습니다.' };
    }
  },

  // 모든 페르소나의 자동 해석 생성
  async generateAllInterpretations(): Promise<ApiResponse<{
    message: string;
    interpretations: InterpretationResponse[];
    total_count: number;
  }>> {
    try {
      const response = await api.post('/interpretations/generate-all');
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '전체 해석 생성에 실패했습니다.' };
    }
  },

  // 해석된 내용을 기반으로 페르소나와 대화
  async chatWithInterpretation(
    personaId: string,
    message: string
  ): Promise<ApiResponse<{
    persona_id: string;
    persona_name: string;
    user_message: string;
    response: string;
    interpretation_used: boolean;
    timestamp: string;
  }>> {
    try {
      const response = await api.post('/interpretations/chat-with-interpretation', {
        persona_id: personaId,
        user_message: message
      });
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '해석 기반 채팅에 실패했습니다.' };
    }
  },

  // 컨텍스트 기반 범용 채팅 (해석/나아가기/문장 모두 지원)
  async chatWithContext(
    personaId: string,
    message: string,
    contextType: 'interpretation' | 'proceed' | 'sentence',
    contextContent?: string,
    threadId?: string
  ): Promise<ApiResponse<{
    persona_id: string;
    persona_name: string;
    user_message: string;
    response: string;
    context_type: string;
    context_used: boolean;
    is_first_message: boolean;
    conversation_id: string;
    timestamp: string;
  }>> {
    try {
      const response = await api.post('/chat/with-context', {
        persona_id: personaId,
        user_message: message,
        context_type: contextType,
        context_content: contextContent,
        thread_id: threadId
      });
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '컨텍스트 기반 채팅에 실패했습니다.' };
    }
  },

  // ========== 새로운 기능: 나아가기, 문장, 상호작용 기록 ==========

  // 나아가기 생성
  async generateProceed(request: ProceedRequest): Promise<ApiResponse<ProceedResponse>> {
    try {
      const response = await api.post('/proceed/generate', request);
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '나아가기 생성에 실패했습니다.' };
    }
  },

  // 문장 생성
  async generateSentence(request: SentenceRequest): Promise<ApiResponse<SentenceResponse>> {
    try {
      const response = await api.post('/sentence/generate', request);
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '문장 생성에 실패했습니다.' };
    }
  },

  // 나아가기 내용 조회
  async getProceedContent(personaId: string): Promise<ApiResponse<{ proceed_content: string }>> {
    const response = await fetch(`${API_BASE_URL}/proceed/${personaId}`);

    if (response.ok) {
      const data = await response.json();
      return { data };
    } else {
      const errorText = await response.text();
      return { error: errorText };
    }
  },

  // 문장 내용 조회
  async getSentenceContent(personaId: string): Promise<ApiResponse<{ sentence_content: string }>> {
    const response = await fetch(`${API_BASE_URL}/sentence/${personaId}`);

    if (response.ok) {
      const data = await response.json();
      return { data };
    } else {
      const errorText = await response.text();
      return { error: errorText };
    }
  },

  // 특정 페르소나의 모든 스레드 조회
  async getPersonaThreads(personaId: string): Promise<ApiResponse<TrainingThread[]>> {
    try {
      const response = await api.get(`/threads/${personaId}`);
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '스레드 조회에 실패했습니다.' };
    }
  },

  // 특정 페르소나의 특정 타입 스레드 조회
  async getPersonaThreadsByType(personaId: string, threadType: string): Promise<ApiResponse<TrainingThread[]>> {
    try {
      const response = await api.get(`/threads/${personaId}/${threadType}`);
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '스레드 조회에 실패했습니다.' };
    }
  },

  // 스레드와 채팅
  async chatWithThread(request: ThreadChatRequest): Promise<ApiResponse<ThreadChatResponse>> {
    try {
      const response = await api.post('/threads/chat', request);
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '스레드 채팅에 실패했습니다.' };
    }
  },

  // 모든 상호작용 기록 조회
  async getAllInteractionRecords(): Promise<ApiResponse<InteractionRecord[]>> {
    try {
      const response = await api.get('/interaction-records');
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '상호작용 기록 조회에 실패했습니다.' };
    }
  },

  // 특정 페르소나의 상호작용 기록 조회
  async getInteractionRecord(personaId: string): Promise<ApiResponse<InteractionRecord>> {
    try {
      const response = await api.get(`/interaction-records/${personaId}`);
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '상호작용 기록 조회에 실패했습니다.' };
    }
  },

  // 해석 생성과 동시에 나아가기, 문장도 자동 생성
  async generateInterpretationWithExtras(request: InterpretationRequest): Promise<ApiResponse<GenerateWithExtrasResponse>> {
    try {
      const response = await api.post('/interpretations/generate-with-extras', request);
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '해석 및 추가 기능 생성에 실패했습니다.' };
    }
  },

  async deleteThread(threadId: string): Promise<ApiResponse<{ success: boolean; message: string }>> {
    const response = await fetch(`${API_BASE_URL}/threads/${threadId}`, {
      method: 'DELETE',
    });

    if (response.ok) {
      const data = await response.json();
      return { data };
    } else {
      const errorText = await response.text();
      return { error: errorText };
    }
  },

  // 현재 내용을 나아가기로 저장 (생성하지 않고 그대로 저장)
  async saveCurrentAsProceed(personaId: string, content: string): Promise<ApiResponse<ProceedResponse>> {
    try {
      const response = await api.post('/proceed/save', {
        persona_id: personaId,
        content: content
      });
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '나아가기 저장에 실패했습니다.' };
    }
  },

  // 현재 내용을 문장으로 저장 (생성하지 않고 그대로 저장)
  async saveCurrentAsSentence(personaId: string, content: string): Promise<ApiResponse<SentenceResponse>> {
    try {
      const response = await api.post('/sentence/save', {
        persona_id: personaId,
        content: content
      });
      return { data: response.data };
    } catch (error: any) {
      return { error: error.response?.data?.detail || '문장 저장에 실패했습니다.' };
    }
  },
};
