import React from 'react';
import {
  Sidebar,
  SidebarHeader,
  SidebarTitle,
  SidebarContent,
  SidebarMenuContent,
  SidebarToggleButton,
  ConversationMenuItem,
} from '../../shared/styles/GlobalStyle';

interface VaultSidebarProps {
  selectedItem: string;
  onSelectItem: (item: string) => void;
  isCollapsed: boolean;
  onToggle: () => void;
}

const VaultSidebar: React.FC<VaultSidebarProps> = ({
  selectedItem,
  onSelectItem,
  isCollapsed,
  onToggle
}) => {
  const menuItems = [
    { id: 'sentences', label: '문장' },
    { id: 'memos', label: '메모' },
    // '편지'는 현재 구현되지 않았으므로 주석 처리 또는 제거
    // { id: 'letter', label: '편지' },
  ];

  return (
    <Sidebar width="220px" $isCollapsed={isCollapsed} $variant="vault">
      <SidebarHeader>
        <SidebarTitle $isCollapsed={isCollapsed}>저장고</SidebarTitle>
        <SidebarToggleButton onClick={onToggle}>
          {isCollapsed ? '→' : '←'}
        </SidebarToggleButton>
      </SidebarHeader>

      <SidebarContent>
        <SidebarMenuContent>
          {menuItems.map(item => (
            <ConversationMenuItem
              key={item.id}
              $isSelected={selectedItem === item.id}
              onClick={() => onSelectItem(item.id)}
            >
              <span className="text" style={{ display: isCollapsed ? 'none' : 'inline' }}>
                {item.label}
              </span>
            </ConversationMenuItem>
          ))}
        </SidebarMenuContent>
      </SidebarContent>
    </Sidebar>
  );
};

export default VaultSidebar;
