import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import MessageInput, { MessageInputRef } from './MessageInput';
import Message from './Message';
import LoadingMessage from './LoadingMessage';
import { chatApi } from '../api/chatApi';
import { TrainingThread, ThreadChatResponse } from '../types';

interface ProceedViewProps {
  personaId: string;
  personaName: string;
  proceedContent: string;
  threads: TrainingThread[];
  onThreadUpdate: (threads: TrainingThread[]) => void;
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  background: white;
`;

const ProceedSection = styled.div`
  padding: 24px;
  border-bottom: 2px solid #e0e0e0;
  background: linear-gradient(135deg, #fff3e0 0%, #ffe0b2 100%);
`;

const ProceedTitle = styled.h2`
  font-size: 20px;
  font-weight: 600;
  color: #333;
  margin-bottom: 16px;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const ProceedContent = styled.div`
  background: white;
  border: 1px solid #e0e0e0;
  border-radius: 12px;
  padding: 20px;
  line-height: 1.6;
  color: #555;
  font-size: 14px;
  white-space: pre-line;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);
`;

const ThreadsSection = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
`;

const ThreadsList = styled.div`
  padding: 20px;
  border-bottom: 1px solid #e0e0e0;
  background: #f8f9fa;
`;

const ThreadItem = styled.div<{ isSelected: boolean }>`
  padding: 12px 16px;
  border: 1px solid ${props => props.isSelected ? '#ff9800' : '#e0e0e0'};
  border-radius: 8px;
  margin-bottom: 8px;
  cursor: pointer;
  background: ${props => props.isSelected ? '#fff3e0' : 'white'};
  transition: all 0.2s ease;

  &:hover {
    border-color: #ff9800;
    background: #fff3e0;
  }
`;

const ThreadTitle = styled.div`
  font-weight: 500;
  color: #333;
  margin-bottom: 4px;
`;

const ThreadTime = styled.div`
  font-size: 12px;
  color: #999;
`;

const ChatSection = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
`;

const ChatMessages = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const ChatInputSection = styled.div`
  padding: 16px 20px;
  border-top: 1px solid #e0e0e0;
  background: #ffffff;
`;

const EmptyChat = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: #999;
  font-size: 14px;
  text-align: center;
`;

const ProceedView: React.FC<ProceedViewProps> = ({
  personaId,
  personaName,
  proceedContent,
  threads,
  onThreadUpdate,
}) => {
  const [selectedThread, setSelectedThread] = useState<TrainingThread | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [localThreads, setLocalThreads] = useState<TrainingThread[]>(threads);
  const chatMessagesRef = useRef<HTMLDivElement>(null);
  const messageInputRef = useRef<MessageInputRef>(null);

  // ÎÇòÏïÑÍ∞ÄÍ∏∞ ÌÉÄÏûÖÏùò Ïä§Î†àÎìúÎßå ÌïÑÌÑ∞ÎßÅ
  const proceedThreads = localThreads.filter(thread => thread.thread_type === 'proceed');

  useEffect(() => {
    setLocalThreads(threads);
    // Ï≤´ Î≤àÏß∏ ÎÇòÏïÑÍ∞ÄÍ∏∞ Ïä§Î†àÎìúÎ•º ÏûêÎèô ÏÑ†ÌÉù
    const firstProceedThread = threads.find(t => t.thread_type === 'proceed');
    if (firstProceedThread && !selectedThread) {
      setSelectedThread(firstProceedThread);
    }
  }, [threads]);

  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [selectedThread?.messages]);

  const handleThreadSelect = (thread: TrainingThread) => {
    setSelectedThread(thread);
  };

  const handleSendMessage = async (message: string): Promise<boolean> => {
    if (!selectedThread) return false;

    setIsLoading(true);
    try {
      const response = await chatApi.chatWithThread({
        thread_id: selectedThread.id,
        user_message: message
      });

      if (response.data && response.data.response) {
        // Ïä§Î†àÎìú ÏóÖÎç∞Ïù¥Ìä∏
        const updatedThreads = localThreads.map(t => 
          t.id === selectedThread.id 
            ? {
                ...t,
                messages: [...t.messages, 
                  {
                    role: 'user' as const,
                    content: message,
                    timestamp: new Date().toISOString()
                  },
                  response.data!.response
                ],
                updated_at: new Date().toISOString()
              }
            : t
        );

        setLocalThreads(updatedThreads);
        onThreadUpdate(updatedThreads);

        // ÏÑ†ÌÉùÎêú Ïä§Î†àÎìú ÏóÖÎç∞Ïù¥Ìä∏
        const updatedThread = updatedThreads.find(t => t.id === selectedThread.id);
        if (updatedThread) {
          setSelectedThread(updatedThread);
        }

        setIsLoading(false);
        return true;
      } else {
        setIsLoading(false);
        return false;
      }
    } catch (error) {
      console.error('ÎÇòÏïÑÍ∞ÄÍ∏∞ Ï±ÑÌåÖ Ïò§Î•ò:', error);
      setIsLoading(false);
      return false;
    }
  };

  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString('ko-KR', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  return (
    <Container>
      <ProceedSection>
        <ProceedTitle>
          üéØ ÎÇòÏïÑÍ∞ÄÍ∏∞ - {personaName}
        </ProceedTitle>
        <ProceedContent>
          {proceedContent}
        </ProceedContent>
      </ProceedSection>

      <ThreadsSection>
        <ThreadsList>
          <h3 style={{ marginBottom: '12px', fontSize: '16px', color: '#333' }}>
            ÎÇòÏïÑÍ∞ÄÍ∏∞ Ïä§Î†àÎìúÎì§
          </h3>
          {proceedThreads.map((thread) => (
            <ThreadItem
              key={thread.id}
              isSelected={selectedThread?.id === thread.id}
              onClick={() => handleThreadSelect(thread)}
            >
              <ThreadTitle>
                ÎÇòÏïÑÍ∞ÄÍ∏∞ #{proceedThreads.indexOf(thread) + 1}
              </ThreadTitle>
              <ThreadTime>
                {formatTime(thread.created_at)} ‚Ä¢ {thread.messages.length}Í∞ú Î©îÏãúÏßÄ
              </ThreadTime>
            </ThreadItem>
          ))}
        </ThreadsList>

        <ChatSection>
          {selectedThread ? (
            <>
              <ChatMessages ref={chatMessagesRef}>
                {selectedThread.messages.map((message, index) => (
                  <div key={index} style={{ 
                    padding: '12px', 
                    border: '1px solid #e0e0e0', 
                    borderRadius: '8px',
                    background: message.role === 'user' ? '#f0f0f0' : 'white',
                    marginBottom: '8px'
                  }}>
                    <div style={{ fontWeight: 'bold', marginBottom: '8px', color: '#ff9800' }}>
                      {message.role === 'user' ? 'ÏÇ¨Ïö©Ïûê' : message.persona_name}
                    </div>
                    <div style={{ whiteSpace: 'pre-line', lineHeight: '1.6' }}>
                      {message.content}
                    </div>
                    <div style={{ fontSize: '12px', color: '#999', marginTop: '8px' }}>
                      {formatTime(message.timestamp)}
                    </div>
                  </div>
                ))}
                {isLoading && <LoadingMessage />}
              </ChatMessages>

              <ChatInputSection>
                <MessageInput
                  ref={messageInputRef}
                  onSendMessage={handleSendMessage}
                  disabled={isLoading}
                  placeholder="ÎÇòÏïÑÍ∞ÄÍ∏∞Ïóê ÎåÄÌï¥ Îçî ÏûêÏÑ∏Ìûà ÏßàÎ¨∏Ìï¥Î≥¥ÏÑ∏Ïöî..."
                />
              </ChatInputSection>
            </>
          ) : (
            <EmptyChat>
              <div>ÎÇòÏïÑÍ∞ÄÍ∏∞ Ïä§Î†àÎìúÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî</div>
              <div style={{ marginTop: '8px', fontSize: '12px' }}>
                Ïä§Î†àÎìúÎ•º ÌÅ¥Î¶≠ÌïòÏó¨ ÎåÄÌôîÎ•º ÏãúÏûëÌï† Ïàò ÏûàÏäµÎãàÎã§
              </div>
            </EmptyChat>
          )}
        </ChatSection>
      </ThreadsSection>
    </Container>
  );
};

export default ProceedView;
