import React from 'react';
import styled from 'styled-components';

interface ConfirmationModalProps {
  isOpen: boolean;
  title: string;
  message: string;
  onConfirm: () => void;
  onCancel: () => void;
  confirmText?: string;
  cancelText?: string;
  isDangerous?: boolean;
}

const ModalOverlay = styled.div<{ $show: boolean }>`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: ${props => props.$show ? 'flex' : 'none'};
  align-items: center;
  justify-content: center;
  z-index: 2000;
`;

const ModalContainer = styled.div`
  background: #fff;
  border-radius: 12px;
  padding: 24px;
  max-width: 400px;
  width: 90%;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.15);
`;

const ModalTitle = styled.h3`
  margin: 0 0 16px 0;
  font-size: 18px;
  font-weight: 600;
  color: #333;
  text-align: center;
`;

const ModalContent = styled.div`
  margin-bottom: 24px;
  font-size: 14px;
  line-height: 1.5;
  color: #666;
  text-align: center;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 12px;
  justify-content: center;
`;

const Button = styled.button<{ $variant: 'primary' | 'secondary' | 'danger' }>`
  padding: 10px 20px;
  border: none;
  border-radius: 8px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  
  ${props => {
    switch (props.$variant) {
      case 'danger':
        return `
          background: #dc3545;
          color: #fff;
          
          &:hover {
            background: #c82333;
          }
        `;
      case 'primary':
        return `
          background: #007bff;
          color: #fff;
          
          &:hover {
            background: #0056b3;
          }
        `;
      default:
        return `
          background: #f8f9fa;
          color: #333;
          border: 1px solid #dee2e6;
          
          &:hover {
            background: #e9ecef;
          }
        `;
    }
  }}
`;

const ConfirmationModal: React.FC<ConfirmationModalProps> = ({
  isOpen,
  title,
  message,
  onConfirm,
  onCancel,
  confirmText = '확인',
  cancelText = '취소',
  isDangerous = false
}) => {
  return (
    <ModalOverlay $show={isOpen}>
      <ModalContainer>
        <ModalTitle>{title}</ModalTitle>
        <ModalContent>
          {message.split('\n').map((line, index) => (
            <div key={index}>{line}</div>
          ))}
        </ModalContent>
        <ButtonGroup>
          <Button $variant="secondary" onClick={onCancel}>
            {cancelText}
          </Button>
          <Button $variant={isDangerous ? 'danger' : 'primary'} onClick={onConfirm}>
            {confirmText}
          </Button>
        </ButtonGroup>
      </ModalContainer>
    </ModalOverlay>
  );
};

export default ConfirmationModal;
