import React, { useState, useEffect } from 'react';
import TrainingChatView from '../../shared/components/TrainingChatView';
import { chatApi } from '../../shared/api/chatApi';
import { TrainingThread } from '../../shared/types';

interface SentenceViewProps {
  personaId: string;
  personaName: string;
  sentenceContent: string;
  threads: TrainingThread[];
  onThreadUpdate: (threads: TrainingThread[]) => void;
  onSwitchToMode?: (mode: 'interpretation' | 'proceed') => void;
  onGenerateNewInterpretation?: () => void;
  selectedThread?: TrainingThread | null;
}

const SentenceView: React.FC<SentenceViewProps> = ({
  personaId,
  personaName,
  sentenceContent,
  threads,
  onThreadUpdate,
  onSwitchToMode,
  onGenerateNewInterpretation,
  selectedThread: propSelectedThread,
}) => {
  const [selectedThread, setSelectedThread] = useState<TrainingThread | null>(propSelectedThread || null);
  const [isLoading, setIsLoading] = useState(false);
  const [localThreads, setLocalThreads] = useState<TrainingThread[]>(threads);

  // 문장 타입의 스레드만 필터링
  const sentenceThreads = localThreads.filter(thread => thread.thread_type === 'sentence');

  useEffect(() => {
    setLocalThreads(threads);
  }, [threads]);

  // propSelectedThread가 변경되면 선택된 스레드 업데이트
  useEffect(() => {
    if (propSelectedThread) {
      setSelectedThread(propSelectedThread);
    } else {
      // 첫 번째 문장 스레드를 자동 선택
      const firstSentenceThread = threads.find(t => t.thread_type === 'sentence');
      if (firstSentenceThread && !selectedThread) {
        setSelectedThread(firstSentenceThread);
      }
    }
  }, [propSelectedThread, threads]);

  const handleSendMessage = async (message: string): Promise<boolean> => {
    if (!selectedThread) return false;

    setIsLoading(true);
    try {
      const response = await chatApi.chatWithThread({
        thread_id: selectedThread.id,
        user_message: message
      });

      if (response.data && response.data.response) {
        // 스레드 업데이트
        const updatedThreads = localThreads.map(t => 
          t.id === selectedThread.id 
            ? {
                ...t,
                messages: [...t.messages, 
                  {
                    role: 'user' as const,
                    content: message,
                    timestamp: new Date().toISOString()
                  },
                  response.data!.response
                ],
                updated_at: new Date().toISOString()
              }
            : t
        );

        setLocalThreads(updatedThreads);
        onThreadUpdate(updatedThreads);

        // 선택된 스레드 업데이트
        const updatedThread = updatedThreads.find(t => t.id === selectedThread.id);
        if (updatedThread) {
          setSelectedThread(updatedThread);
        }

        setIsLoading(false);
        return true;
      } else {
        setIsLoading(false);
        return false;
      }
    } catch (error) {
      console.error('문장 채팅 오류:', error);
      setIsLoading(false);
      return false;
    }
  };

  const handleEditMessage = async (messageIndex: number, newContent: string): Promise<boolean> => {
    if (!selectedThread) return false;

    setIsLoading(true);
    try {
      // 메시지 수정 후 새로운 응답 생성
      const updatedMessages = [...selectedThread.messages];
      if (messageIndex >= 0 && messageIndex < updatedMessages.length) {
        // 수정된 메시지 업데이트
        updatedMessages[messageIndex] = {
          ...updatedMessages[messageIndex],
          content: newContent,
          timestamp: new Date().toISOString(),
        };

        // 수정된 메시지 이후의 모든 메시지들 제거
        const filteredMessages = updatedMessages.slice(0, messageIndex + 1);

        // 새로운 AI 응답 생성
        const response = await chatApi.chatWithThread({
          thread_id: selectedThread.id,
          user_message: newContent
        });

        if (response.data && response.data.response) {
          const newMessages = [...filteredMessages, response.data.response];

          // 스레드 업데이트
          const updatedThreads = localThreads.map(t => 
            t.id === selectedThread.id 
              ? { ...t, messages: newMessages, updated_at: new Date().toISOString() }
              : t
          );

          setLocalThreads(updatedThreads);
          onThreadUpdate(updatedThreads);

          // 선택된 스레드 업데이트
          const updatedThread = updatedThreads.find(t => t.id === selectedThread.id);
          if (updatedThread) {
            setSelectedThread(updatedThread);
          }

          setIsLoading(false);
          return true;
        }
      }

      setIsLoading(false);
      return false;
    } catch (error) {
      console.error('문장 메시지 수정 실패:', error);
      setIsLoading(false);
      return false;
    }
  };

  const handleGenerateNewSentence = async () => {
    if (!selectedThread || selectedThread.messages.length === 0) {
      if (onGenerateNewInterpretation) {
        onGenerateNewInterpretation();
      }
      return;
    }

    setIsLoading(true);
    try {
      // 현재 스레드의 최신 assistant 메시지를 기반으로 새로운 문장 생성
      const latestAssistantMessage = [...selectedThread.messages]
        .reverse()
        .find(msg => msg.role === 'assistant');

      if (latestAssistantMessage) {
        const response = await chatApi.generateSentence({
          persona_id: personaId,
          interpretation_content: latestAssistantMessage.content
        });

        if (response.data && response.data.sentence_content) {
          // 스레드 목록 새로고침
          try {
            const threadsResponse = await chatApi.getPersonaThreads(personaId);
            if (threadsResponse.data) {
              const updatedThreads = threadsResponse.data;
              setLocalThreads(updatedThreads);
              onThreadUpdate(updatedThreads);

              // 새로 생성된 문장 스레드 선택
              const newSentenceThread = updatedThreads
                .filter(t => t.thread_type === 'sentence')
                .sort((a, b) => new Date(b.created_at).getTime() - new Date(a.created_at).getTime())[0];

              if (newSentenceThread) {
                setSelectedThread(newSentenceThread);
              }
            }
          } catch (refreshError) {
            console.error('스레드 새로고침 실패:', refreshError);
          }
        }
      }
    } catch (error) {
      console.error('문장 생성 오류:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSwitchToMode = (mode: 'interpretation' | 'proceed' | 'sentence') => {
    if (onSwitchToMode) {
      onSwitchToMode(mode as 'interpretation' | 'proceed');
    }
  };

  // 현재 선택된 스레드의 메시지들 또는 빈 배열
  const currentMessages = selectedThread?.messages || [];

  // 첫 번째 문장 내용 (스레드가 없거나 메시지가 없는 경우)
  const displayContent = selectedThread?.messages.find(m => m.role === 'assistant')?.content || sentenceContent;

  return (
    <TrainingChatView
      personaId={personaId}
      personaName={personaName}
      contextContent={displayContent}
      contextType="sentence"
      messages={currentMessages}
      isLoading={isLoading}
      onSendMessage={handleSendMessage}
      onEditMessage={handleEditMessage}
      onGenerateNewContent={handleGenerateNewSentence}
      onSwitchToMode={handleSwitchToMode}
    />
  );
};

export default SentenceView;
