import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import MessageInput, { MessageInputRef } from '../../shared/components/MessageInput';
import Message from '../../shared/components/Message';
import SelectableMessage from './SelectableMessage';
import FloatingActionButton from '../../shared/components/FloatingActionButton';
import LoadingMessage from '../../shared/components/LoadingMessage';
import { sentenceApi } from '../api/sentenceApi';
import { chatApi } from '../../shared/api/chatApi';
import { TrainingThread, InteractionRecord } from '../../shared/types';

interface InteractionHistoryViewProps {
  personaId: string;
  personaName: string;
  allInteractionRecords: InteractionRecord[];
  onThreadSelect: (thread: TrainingThread) => void;
  onSwitchToMode?: (mode: 'interpretation' | 'proceed' | 'sentence') => void;
  onGenerateNewInterpretation?: () => void;
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  background: white;
`;

const RecordsList = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const RecordItem = styled.div`
  border: 1px solid #e0e0e0;
  border-radius: 12px;
  background: white;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);
`;

const RecordHeader = styled.div`
  padding: 16px 20px;
  background: linear-gradient(135deg, #f8f9fa 0%, #f1f3f4 100%);
  border-bottom: 1px solid #e0e0e0;
  cursor: pointer;
  display: flex;
  justify-content: space-between;
  align-items: center;
  
  &:hover {
    background: linear-gradient(135deg, #f1f3f4 0%, #e9ecef 100%);
  }
`;

const RecordTitle = styled.h3`
  font-size: 16px;
  font-weight: 600;
  color: #333;
  margin: 0;
`;

const RecordInfo = styled.div`
  font-size: 12px;
  color: #666;
  text-align: right;
`;

const ThreadsList = styled.div<{ isExpanded: boolean }>`
  max-height: ${props => props.isExpanded ? '400px' : '0'};
  overflow-y: auto;
  transition: max-height 0.3s ease;
  background: #fafafa;
`;

const ThreadItem = styled.div`
  padding: 12px 20px;
  border-bottom: 1px solid #e0e0e0;
  cursor: pointer;
  display: flex;
  justify-content: space-between;
  align-items: center;
  
  &:hover {
    background: #f0f0f0;
  }
  
  &:last-child {
    border-bottom: none;
  }
`;

const ThreadInfo = styled.div`
  flex: 1;
`;

const ThreadType = styled.span<{ type: string }>`
  display: inline-block;
  padding: 4px 8px;
  border-radius: 6px;
  font-size: 10px;
  font-weight: 600;
  text-transform: uppercase;
  margin-right: 8px;
  
  background-color: ${props => {
    switch (props.type) {
      case 'interpretation': return '#6c757d';
      case 'proceed': return '#ff9800';
      case 'sentence': return '#4caf50';
      default: return '#6c757d';
    }
  }};
  color: white;
`;

const ThreadTitle = styled.div`
  font-size: 14px;
  color: #333;
  margin-bottom: 4px;
`;

const ThreadMeta = styled.div`
  font-size: 12px;
  color: #666;
`;

const EmptyState = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: #999;
  font-size: 14px;
  text-align: center;
  padding: 40px 20px;
`;

const ExpandIcon = styled.span<{ isExpanded: boolean }>`
  font-size: 12px;
  color: #666;
  transform: ${props => props.isExpanded ? 'rotate(90deg)' : 'rotate(0deg)'};
  transition: transform 0.2s ease;
`;

const InteractionHistoryView: React.FC<InteractionHistoryViewProps> = ({
  personaId,
  personaName,
  allInteractionRecords,
  onThreadSelect,
  onSwitchToMode,
  onGenerateNewInterpretation,
}) => {
  const [expandedRecords, setExpandedRecords] = useState<Set<string>>(new Set());

  const handleRecordToggle = (recordId: string) => {
    setExpandedRecords(prev => {
      const newSet = new Set(prev);
      if (newSet.has(recordId)) {
        newSet.delete(recordId);
      } else {
        newSet.add(recordId);
      }
      return newSet;
    });
  };

  const handleThreadClick = (thread: TrainingThread) => {
    onThreadSelect(thread);
  };

  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString('ko-KR', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const formatDate = (timestamp: string) => {
    return new Date(timestamp).toLocaleDateString('ko-KR', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getThreadTypeLabel = (type: string) => {
    switch (type) {
      case 'interpretation': return 'Ìï¥ÏÑù';
      case 'proceed': return 'ÎÇòÏïÑÍ∞ÄÍ∏∞';
      case 'sentence': return 'Î¨∏Ïû•';
      default: return type;
    }
  };

  const getThreadContent = (thread: TrainingThread) => {
    if (thread.messages && thread.messages.length > 0) {
      const firstMessage = thread.messages[0];
      return firstMessage.content.length > 60 
        ? firstMessage.content.substring(0, 60) + '...' 
        : firstMessage.content;
    }
    return thread.content?.length > 60 
      ? thread.content.substring(0, 60) + '...' 
      : thread.content || 'ÎÇ¥Ïö© ÏóÜÏùå';
  };

  // ÌòÑÏû¨ ÏÑ†ÌÉùÎêú ÌéòÎ•¥ÏÜåÎÇòÏùò Í∏∞Î°ùÎßå ÌïÑÌÑ∞ÎßÅ
  const currentPersonaRecord = allInteractionRecords.find(
    record => record.persona_id === personaId
  );

  // Î™®Îì† Í∏∞Î°ùÏùÑ ÏµúÏã†ÏàúÏúºÎ°ú Ï†ïÎ†¨
  const sortedRecords = allInteractionRecords.sort((a, b) => 
    new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime()
  );

  return (
    <Container>
      <RecordsList>
        {sortedRecords.length === 0 ? (
          <EmptyState>
            <div style={{ fontSize: '24px', marginBottom: '8px' }}>üìä</div>
            <div>ÏïÑÏßÅ ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ùÏù¥ ÏóÜÏäµÎãàÎã§</div>
            <div style={{ fontSize: '12px', marginTop: '4px' }}>
              Ìï¥ÏÑùÏùÑ ÏÉùÏÑ±ÌïòÎ©¥ ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ùÏù¥ ÎßåÎì§Ïñ¥ÏßëÎãàÎã§
            </div>
          </EmptyState>
        ) : (
          sortedRecords.map((record) => (
            <RecordItem key={record.id}>
              <RecordHeader onClick={() => handleRecordToggle(record.id)}>
                <RecordTitle>
                  {record.persona_name}
                  <ExpandIcon isExpanded={expandedRecords.has(record.id)}>
                    ‚ñ∂
                  </ExpandIcon>
                </RecordTitle>
                <RecordInfo>
                  <div>Ïä§Î†àÎìú {record.threads.length}Í∞ú</div>
                  <div>{formatDate(record.updated_at)}</div>
                </RecordInfo>
              </RecordHeader>
              
              <ThreadsList isExpanded={expandedRecords.has(record.id)}>
                {record.threads.map((thread) => (
                  <ThreadItem 
                    key={thread.id} 
                    onClick={() => handleThreadClick(thread)}
                  >
                    <ThreadInfo>
                      <ThreadTitle>
                        <ThreadType type={thread.thread_type}>
                          {getThreadTypeLabel(thread.thread_type)}
                        </ThreadType>
                        {getThreadContent(thread)}
                      </ThreadTitle>
                      <ThreadMeta>
                        {formatDate(thread.created_at)} ‚Ä¢ Î©îÏãúÏßÄ {thread.messages?.length || 1}Í∞ú
                      </ThreadMeta>
                    </ThreadInfo>
                  </ThreadItem>
                ))}
              </ThreadsList>
            </RecordItem>
          ))
        )}
      </RecordsList>
    </Container>
  );
};

export default InteractionHistoryView;
