import React, { useState, useEffect } from 'react';
import {
  Sidebar,
  SidebarHeader,
  SidebarTitle,
  SidebarContent,
  SidebarMenuContent,
  SidebarToggleButton,
  ConversationMenuItem,
} from '../styles/GlobalStyle';
import { chatApi } from '../api/chatApi';
import { TrainingThread, InteractionRecord } from '../types';
import styled from 'styled-components';

interface ConversationSidebarProps {
  selectedItem: string | null;
  onSelectItem: (itemId: string | null) => void;
  isCollapsed?: boolean;
  onToggle?: () => void;
  selectedPersonaName?: string;
  selectedPersonaId?: string;
  refreshTrigger?: number;
}

const AccordionContainer = styled.div`
  width: 100%;
`;

const AccordionItem = styled.div`
  border-bottom: 1px solid #f0f0f0;
`;

const AccordionHeader = styled.div<{ $isSelected: boolean; $isExpanded: boolean }>`
  padding: 12px 16px;
  cursor: pointer;
  background: ${props => props.$isSelected ? 'linear-gradient(135deg, #6c757d 0%, #5a6268 100%)' : 'transparent'};
  color: ${props => props.$isSelected ? 'white' : '#333'};
  font-weight: ${props => props.$isSelected ? '600' : '500'};
  border-radius: ${props => props.$isSelected ? '8px' : '0'};
  margin: ${props => props.$isSelected ? '4px 8px' : '0'};
  transition: all 0.2s ease;
  display: flex;
  justify-content: space-between;
  align-items: center;

  &:hover {
    background: ${props => props.$isSelected ? 'linear-gradient(135deg, #6c757d 0%, #5a6268 100%)' : '#f8f9fa'};
  }
`;

const ThreadsList = styled.div<{ $isExpanded: boolean }>`
  max-height: ${props => props.$isExpanded ? '300px' : '0'};
  overflow: hidden;
  transition: max-height 0.3s ease;
  background: #f8f9fa;
`;

const InteractionRecordsList = styled.div<{ $isExpanded: boolean }>`
  max-height: ${props => props.$isExpanded ? '400px' : '0'};
  overflow-y: auto;
  transition: max-height 0.3s ease;
  background: #f8f9fa;
`;

const RecordItem = styled.div`
  padding: 8px 32px;
  border-bottom: 1px solid #e0e0e0;
`;

const RecordHeader = styled.div<{ $isExpanded: boolean }>`
  display: flex;
  justify-content: space-between;
  align-items: center;
  cursor: pointer;
  padding: 8px 0;

  &:hover {
    color: #6c757d;
  }
`;

const RecordTitle = styled.div`
  font-size: 14px;
  font-weight: 500;
`;

const RecordMeta = styled.div`
  font-size: 12px;
  color: #666;
`;

const RecordExpandIcon = styled.span<{ $isExpanded: boolean }>`
  font-size: 10px;
  transform: ${props => props.$isExpanded ? 'rotate(90deg)' : 'rotate(0deg)'};
  transition: transform 0.2s ease;
`;

const RecordThreadsList = styled.div<{ $isExpanded: boolean }>`
  max-height: ${props => props.$isExpanded ? '200px' : '0'};
  overflow: hidden;
  transition: max-height 0.3s ease;
  margin-left: 16px;
`;

const RecordThreadItem = styled.div<{ $isSelected: boolean }>`
  padding: 6px 12px;
  cursor: pointer;
  color: ${props => props.$isSelected ? '#6c757d' : '#666'};
  font-size: 13px;
  border-left: 2px solid ${props => props.$isSelected ? '#6c757d' : 'transparent'};
  background: ${props => props.$isSelected ? '#f0f0f0' : 'transparent'};
  transition: all 0.2s ease;
  margin: 2px 0;
  border-radius: 4px;

  &:hover {
    background: #f0f0f0;
    color: #6c757d;
  }
`;

const ThreadTypeTag = styled.span<{ type: string }>`
  display: inline-block;
  padding: 2px 6px;
  border-radius: 3px;
  font-size: 9px;
  font-weight: 600;
  text-transform: uppercase;
  margin-right: 6px;

  background-color: ${props => {
    switch (props.type) {
      case 'interpretation': return '#6c757d';
      case 'proceed': return '#ff9800';
      case 'sentence': return '#4caf50';
      default: return '#6c757d';
    }
  }};
  color: white;
`;

const ThreadItem = styled.div<{ $isSelected: boolean }>`
  padding: 8px 32px 8px 32px;
  cursor: pointer;
  color: ${props => props.$isSelected ? '#6c757d' : '#666'};
  font-size: 14px;
  border-left: 3px solid ${props => props.$isSelected ? '#6c757d' : 'transparent'};
  background: ${props => props.$isSelected ? '#f8f9fa' : 'transparent'};
  transition: all 0.2s ease;
  display: flex;
  justify-content: space-between;
  align-items: center;

  &:hover {
    background: #f8f9fa;
    color: #6c757d;
  }
`;

const ThreadItemContent = styled.div`
  flex: 1;
`;

const DeleteThreadButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  color: #999;
  font-size: 14px;
  padding: 4px;
  border-radius: 4px;
  opacity: 0;
  transition: all 0.2s ease;

  &:hover {
    background: #e9ecef;
    color: #dc3545;
  }

  ${ThreadItem}:hover & {
    opacity: 1;
  }
`;

const ThreadTitle = styled.div`
  font-weight: 500;
  margin-bottom: 2px;
`;

const ThreadTime = styled.div`
  font-size: 12px;
  opacity: 0.7;
`;

const ConversationSidebar: React.FC<ConversationSidebarProps> = ({
  selectedItem,
  onSelectItem,
  isCollapsed = false,
  onToggle,
  selectedPersonaName,
  selectedPersonaId,
  refreshTrigger,
}) => {
  const [expandedItems, setExpandedItems] = useState<Set<string>>(new Set());
  const [expandedRecords, setExpandedRecords] = useState<Set<string>>(new Set());
  const [threads, setThreads] = useState<Record<string, TrainingThread[]>>({
    interpretation: [],
    proceed: [],
    sentence: []
  });
  const [interactionRecords, setInteractionRecords] = useState<InteractionRecord[]>([]);
  const [selectedThreadId, setSelectedThreadId] = useState<string | null>(null);

  const menuItems = [
    { id: 'interpretation', name: 'Ìï¥ÏÑù' },
    { id: 'proceed', name: 'ÎÇòÏïÑÍ∞ÄÍ∏∞' },
    { id: 'sentence', name: 'Î¨∏Ïû•' },
    { id: 'interaction_history', name: 'ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ù' },
  ];

  useEffect(() => {
    const loadData = async () => {
      if (!selectedPersonaId) return;

      try {
        // Ïä§Î†àÎìú Î°úÎìú
        const threadsResponse = await chatApi.getPersonaThreads(selectedPersonaId);
        if (threadsResponse.data) {
          const threadsByType = {
            interpretation: threadsResponse.data.filter((t: any) => t.thread_type === 'interpretation'),
            proceed: threadsResponse.data.filter((t: any) => t.thread_type === 'proceed'),
            sentence: threadsResponse.data.filter((t: any) => t.thread_type === 'sentence')
          };
          setThreads(threadsByType);
        }

        // ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ù Î°úÎìú
        const recordsResponse = await chatApi.getAllInteractionRecords();
        if (recordsResponse.data) {
          setInteractionRecords(recordsResponse.data);
        }
      } catch (error) {
        console.error('Îç∞Ïù¥ÌÑ∞ Î°úÎìú Ïã§Ìå®:', error);
      }
    };

    loadData();
  }, [selectedPersonaId, refreshTrigger]);

  const handleAccordionToggle = (itemId: string) => {
    const newExpanded = new Set(expandedItems);
    if (newExpanded.has(itemId)) {
      newExpanded.delete(itemId);
    } else {
      newExpanded.add(itemId);
    }
    setExpandedItems(newExpanded);

    if (itemId === 'interaction_history') {
      onSelectItem(itemId);
    } else {
      const itemThreads = threads[itemId as keyof typeof threads] || [];
      if (itemThreads.length === 0) {
        onSelectItem(itemId);
      }
    }
  };

  const handleRecordToggle = (recordId: string) => {
    const newExpanded = new Set(expandedRecords);
    if (newExpanded.has(recordId)) {
      newExpanded.delete(recordId);
    } else {
      newExpanded.add(recordId);
    }
    setExpandedRecords(newExpanded);
  };

  const handleThreadSelect = (threadId: string, itemType: string) => {
    setSelectedThreadId(threadId);
    onSelectItem(`${itemType}-thread-${threadId}`);
  };

  const handleRecordThreadSelect = (thread: TrainingThread) => {
    setSelectedThreadId(thread.id);
    onSelectItem(`${thread.thread_type}-thread-${thread.id}`);
  };

  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString('ko-KR', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const formatDate = (timestamp: string) => {
    return new Date(timestamp).toLocaleDateString('ko-KR', {
      month: 'short',
      day: 'numeric',
    });
  };

  const handleDeleteThread = async (threadId: string, e: React.MouseEvent) => {
    e.stopPropagation();
    if (window.confirm('Ïù¥ Ïä§Î†àÎìúÎ•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      try {
        const result = await chatApi.deleteThread(threadId);
        if (result.data) {
          const updatedThreads = { ...threads };
          Object.keys(updatedThreads).forEach(key => {
            updatedThreads[key as keyof typeof threads] = updatedThreads[key as keyof typeof threads].filter((t: any) => t.id !== threadId);
          });
          setThreads(updatedThreads);

          if (selectedThreadId === threadId) {
            setSelectedThreadId(null);
          }
        }
      } catch (error) {
        console.error('Ïä§Î†àÎìú ÏÇ≠Ï†ú Ïã§Ìå®:', error);
        alert('Ïä§Î†àÎìú ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
      }
    }
  };

  const getThreadTypeLabel = (type: string) => {
    switch (type) {
      case 'interpretation': return 'Ìï¥ÏÑù';
      case 'proceed': return 'ÎÇòÏïÑÍ∞ÄÍ∏∞';
      case 'sentence': return 'Î¨∏Ïû•';
      default: return type;
    }
  };

  const getThreadContent = (thread: TrainingThread) => {
    if (thread.messages && thread.messages.length > 0) {
      const firstMessage = thread.messages[0];
      return firstMessage.content.length > 30 
        ? firstMessage.content.substring(0, 30) + '...' 
        : firstMessage.content;
    }
    return thread.content?.length > 30 
      ? thread.content.substring(0, 30) + '...' 
      : thread.content || 'ÎÇ¥Ïö© ÏóÜÏùå';
  };

  const renderThreads = (itemId: string) => {
    const itemThreads = threads[itemId as keyof typeof threads] || [];
    const isExpanded = expandedItems.has(itemId);

    return (
      <ThreadsList $isExpanded={isExpanded}>
        {itemThreads.map((thread: any, index: number) => (
          <ThreadItem
            key={thread.id}
            $isSelected={selectedThreadId === thread.id}
            onClick={() => handleThreadSelect(thread.id, itemId)}
          >
            <ThreadItemContent>
              <ThreadTitle>
                {itemId === 'interpretation' && `Ìï¥ÏÑù #${index + 1}`}
                {itemId === 'proceed' && `ÎÇòÏïÑÍ∞ÄÍ∏∞ #${index + 1}`}
                {itemId === 'sentence' && `Î¨∏Ïû• #${index + 1}`}
              </ThreadTitle>
              <ThreadTime>
                {formatTime(thread.created_at)} ‚Ä¢ {thread.messages.length}Í∞ú Î©îÏãúÏßÄ
              </ThreadTime>
            </ThreadItemContent>
            <DeleteThreadButton
              onClick={(e) => handleDeleteThread(thread.id, e)}
              title="Ïä§Î†àÎìú ÏÇ≠Ï†ú"
            >
              üóëÔ∏è
            </DeleteThreadButton>
          </ThreadItem>
        ))}
        {itemThreads.length === 0 && (
          <div style={{ padding: '16px 32px', fontSize: '14px', color: '#999' }}>
            Ïä§Î†àÎìúÍ∞Ä ÏóÜÏäµÎãàÎã§
          </div>
        )}
      </ThreadsList>
    );
  };

  const renderInteractionRecords = () => {
    const isExpanded = expandedItems.has('interaction_history');

    // ÌòÑÏû¨ ÏÑ†ÌÉùÎêú ÌéòÎ•¥ÏÜåÎÇòÏùò Í∏∞Î°ùÎßå ÌïÑÌÑ∞ÎßÅ
    const currentPersonaRecord = interactionRecords.find(
      record => record.persona_id === selectedPersonaId
    );

    return (
      <InteractionRecordsList $isExpanded={isExpanded}>
        {currentPersonaRecord ? (
          <RecordItem key={currentPersonaRecord.id}>
            <RecordHeader 
              $isExpanded={expandedRecords.has(currentPersonaRecord.id)}
              onClick={() => handleRecordToggle(currentPersonaRecord.id)}
            >
              <div>
                <RecordTitle>{currentPersonaRecord.persona_name}Ïùò Í∏∞Î°ù</RecordTitle>
                <RecordMeta>
                  {formatDate(currentPersonaRecord.updated_at)} ‚Ä¢ {currentPersonaRecord.threads.length}Í∞ú Ïä§Î†àÎìú
                </RecordMeta>
              </div>
              <RecordExpandIcon $isExpanded={expandedRecords.has(currentPersonaRecord.id)}>
                ‚ñ∂
              </RecordExpandIcon>
            </RecordHeader>

            <RecordThreadsList $isExpanded={expandedRecords.has(currentPersonaRecord.id)}>
              {currentPersonaRecord.threads.map((thread) => (
                <RecordThreadItem
                  key={thread.id}
                  $isSelected={selectedThreadId === thread.id}
                  onClick={() => handleRecordThreadSelect(thread)}
                >
                  <div>
                    <ThreadTypeTag type={thread.thread_type}>
                      {getThreadTypeLabel(thread.thread_type)}
                    </ThreadTypeTag>
                    {getThreadContent(thread)}
                  </div>
                  <div style={{ fontSize: '11px', opacity: 0.7, marginTop: '2px' }}>
                    {formatTime(thread.created_at)}
                  </div>
                </RecordThreadItem>
              ))}
            </RecordThreadsList>
          </RecordItem>
        ) : (
          <div style={{ padding: '16px 32px', fontSize: '14px', color: '#999' }}>
            Ïù¥ ÌéòÎ•¥ÏÜåÎÇòÏùò ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ùÏù¥ ÏóÜÏäµÎãàÎã§
          </div>
        )}
      </InteractionRecordsList>
    );
  };

  return (
    <Sidebar width="250px" $isCollapsed={isCollapsed} $variant="conversation">
      {onToggle && (
        <SidebarToggleButton onClick={onToggle}>
          {isCollapsed ? '‚ñ∑' : '‚óÅ'}
        </SidebarToggleButton>
      )}

      <SidebarHeader $isCollapsed={isCollapsed}>
        {selectedPersonaName && (
          <SidebarTitle $isCollapsed={isCollapsed}>
            {selectedPersonaName}
          </SidebarTitle>
        )}
      </SidebarHeader>

      <SidebarContent $isCollapsed={isCollapsed}>
        <SidebarMenuContent $variant="conversation">
          <AccordionContainer>
            {menuItems.map((item) => (
              <AccordionItem key={item.id}>
                <AccordionHeader
                  $isSelected={selectedItem === item.id}
                  $isExpanded={expandedItems.has(item.id)}
                  onClick={() => handleAccordionToggle(item.id)}
                >
                  <span>{item.name}</span>
                </AccordionHeader>

                {item.id === 'interaction_history' 
                  ? renderInteractionRecords()
                  : renderThreads(item.id)
                }
              </AccordionItem>
            ))}
          </AccordionContainer>
        </SidebarMenuContent>
      </SidebarContent>
    </Sidebar>
  );
};

export default ConversationSidebar;
