                  <SubItemsContainer>
                    {section.sub_items.map((subItem, subIndex) => {
                      const subItemKey = `${section.title}-${subIndex}`;
                      const isSubExpanded = expandedSubItems.has(subItemKey);
                      
                      return (
                        <SubItem key={subItemKey} $isExpanded={isSubExpanded}>
                          <SubItemHeader
