                            <ToggleIcon $isExpanded={isSubExpanded}>‚ñ∂</ToggleIcon>
                          </SubItemHeader>
                          <SubItemContent $isExpanded={isSubExpanded}>
                            {subItem.content}
                          </SubItemContent>
                        </SubItem>
                      );
                    })}
                  </SubItemsContainer>
                )}
              </SectionContent>
            </SectionContainer>
          );
        })}
      </ContentSection>
    </Container>
  );
};

