import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

// ÌÉÄÏûÖ Ï†ïÏùò
interface StimulusQuote {
  id: string;
  content: string;
  order: number;
}

interface StimulusQuoteSet {
  quotes: StimulusQuote[]; // 3Í∞úÏî© Î¨∂Ïù∏ Í≤©Ïñ∏Îì§
}

interface PersonaInfo {
  name: string;
  theme: string;
  summary: string;
}

interface DailyStimulusItem {
  id: string;
  date: string;
  persona_name: string;
  main_theme: string;
  quote_sets: StimulusQuoteSet[]; // 3Í∞úÏùò ÏÑ∏Ìä∏ (Ï¥ù 9Í∞ú Í≤©Ïñ∏)
  summary_quote: string;
  persona_info?: PersonaInfo[]; // Í∞Å ÏÑ∏Ìä∏Î≥Ñ Ïã¨Î¶¨ÌïôÏûê Ï†ïÎ≥¥
  created_at: string;
}

interface StimulusViewProps {
  personas?: Record<string, any>;
}

// Ïä§ÌÉÄÏùº Ïª¥Ìè¨ÎÑåÌä∏Îì§
const StimulusContainer = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  position: relative;
  overflow: hidden;
`;

const ContentArea = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 40px;
  position: relative;
`;

const StimulusCard = styled.div`
  background: linear-gradient(180deg, #ffffff 0%, #f8f9fa 100%);
  border-radius: 24px;
  padding: 80px 70px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.1);
  max-width: 900px;
  width: 100%;
  position: relative;
  backdrop-filter: blur(10px);
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 50px;
  gap: 16px;
`;

const DateDisplay = styled.div`
  font-size: 18px;
  color: #666;
  font-weight: 500;
`;

const PersonaName = styled.div`
  font-size: 18px;
  color: #333;
  font-weight: 600;
`;

const MainThemeBox = styled.div`
  background: linear-gradient(135deg, #ffffff 0%, #f0f0f0 100%);
  border-radius: 16px;
  padding: 28px;
  margin-bottom: 30px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.08);
  position: relative;
  z-index: 2;
`;

const MainTheme = styled.h1`
  font-size: 28px;
  font-weight: bold;
  color: #222;
  text-align: center;
  margin: 0;
  line-height: 1.4;
`;

const QuotesContainer = styled.div`
  position: relative;
  margin-bottom: 30px;
`;

const VerticalLine = styled.div`
  position: absolute;
  left: 50%;
  top: 0;
  bottom: 0;
  width: 2px;
  background: #ddd;
  transform: translateX(-50%);
  z-index: 1;
`;

const QuoteSetContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0;
`;

const QuoteBox = styled.div<{ $isVisible: boolean; $isFirst?: boolean; $isLast?: boolean }>`
  background: linear-gradient(135deg, #ffffff 0%, #f0f0f0 100%);
  border-radius: ${props => {
    if (props.$isFirst) return '16px 16px 0 0';
    if (props.$isLast) return '0 0 16px 16px';
    return '0';
  }};
  padding: 24px 28px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.08);
  position: relative;
  z-index: 2;
  transition: all 0.3s ease;
  opacity: ${props => props.$isVisible ? 1 : 0};
  transform: translateY(${props => props.$isVisible ? '0' : '20px'});
  border: 1px solid #e8e8e8;
  border-top: ${props => !props.$isFirst ? 'none' : '1px solid #e8e8e8'};

  &:hover {
    box-shadow: 0 12px 32px rgba(0, 0, 0, 0.12);
    .action-button {
      opacity: 1;
    }
  }
`;

const QuoteContent = styled.div`
  font-size: 18px;
  line-height: 1.6;
  color: #333;
  text-align: center;
  padding-right: 40px;
`;

const ActionButton = styled.button`
  position: absolute;
  right: 12px;
  top: 50%;
  transform: translateY(-50%);
  width: 32px;
  height: 32px;
  border-radius: 50%;
  border: 2px solid #ddd;
  background: white;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  opacity: 0;
  transition: all 0.2s ease;
  font-size: 14px;
  color: #666;

  &:hover {
    border-color: #999;
    background: #f8f9fa;
  }
`;

const SummaryQuoteBox = styled.div`
  background: linear-gradient(135deg, #ffffff 0%, #f0f0f0 100%);
  border-radius: 16px;
  padding: 28px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.08);
  position: relative;
  z-index: 2;
  margin-top: 30px;

  &:hover {
    box-shadow: 0 12px 32px rgba(0, 0, 0, 0.12);
    .action-button {
      opacity: 1;
    }
  }
`;

const SummaryQuote = styled.div`
  font-size: 20px;
  font-weight: 500;
  line-height: 1.5;
  color: #333;
  text-align: center;
  padding-right: 40px;
`;

const NavigationContainer = styled.div`
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  display: flex;
  justify-content: space-between;
  pointer-events: none;
  z-index: 3;
`;

const NavigationButton = styled.button<{ $side: 'left' | 'right' }>`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background: white;
  border: none;
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 20px;
  color: #666;
  opacity: 0;
  transition: all 0.3s ease;
  pointer-events: auto;
  margin: ${props => props.$side === 'left' ? '0 20px 0 -25px' : '0 -25px 0 20px'};

  &:hover {
    opacity: 1 !important;
    background: #f8f9fa;
    transform: scale(1.1);
  }
`;

const IndicatorContainer = styled.div`
  position: absolute;
  top: 20px;
  right: 20px;
  display: flex;
  align-items: center;
  gap: 16px;
`;

const PageIndicator = styled.div`
  display: flex;
  gap: 8px;
`;

const PageDot = styled.div<{ $isActive: boolean }>`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
  cursor: pointer;
`;

const PageNumber = styled.span<{ $isActive: boolean }>`
  font-size: 14px;
  font-weight: ${props => props.$isActive ? 'bold' : 'normal'};
  color: ${props => props.$isActive ? '#333' : '#999'};
`;

const PageBar = styled.div<{ $isActive: boolean }>`
  width: 24px;
  height: 3px;
  background: ${props => props.$isActive ? '#666' : '#ddd'};
  border-radius: 2px;
  transition: all 0.3s ease;
`;

const MenuButton = styled.button`
  width: 36px;
  height: 36px;
  border: none;
  background: #f0f0f0;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: #666;
  font-size: 16px;

  &:hover {
    background: #e0e0e0;
  }
`;

const DropdownMenu = styled.div<{ $isVisible: boolean }>`
  position: absolute;
  top: 100%;
  right: 0;
  background: white;
  border-radius: 8px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.15);
  overflow: hidden;
  transform: translateY(${props => props.$isVisible ? '8px' : '0'});
  opacity: ${props => props.$isVisible ? 1 : 0};
  visibility: ${props => props.$isVisible ? 'visible' : 'hidden'};
  transition: all 0.2s ease;
  z-index: 10;
`;

const MenuOption = styled.div`
  padding: 12px 16px;
  background: #f8f9fa;
  color: #333;
  cursor: pointer;
  white-space: nowrap;
  border-bottom: 1px solid #eee;

  &:last-child {
    border-bottom: none;
  }

  &:hover {
    background: #e9ecef;
  }
`;

const LoadingState = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 400px;
  font-size: 18px;
  color: #666;
`;

const ErrorState = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 400px;
  color: #d32f2f;
  text-align: center;

  button {
    margin-top: 16px;
    padding: 8px 16px;
    background: #d32f2f;
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;
  }
`;

// Î©îÏù∏ Ïª¥Ìè¨ÎÑåÌä∏
const StimulusView: React.FC<StimulusViewProps> = ({ personas }) => {
  const [currentStimulus, setCurrentStimulus] = useState<DailyStimulusItem | null>(null);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [isMenuOpen, setIsMenuOpen] = useState<boolean>(false);

  // Ïò§ÎäòÏùò ÏûêÍ∑π Î°úÎìú
  useEffect(() => {
    loadTodayStimulus();
  }, []);

  const loadTodayStimulus = async () => {
    try {
      setIsLoading(true);
      setError(null);

      const response = await fetch('/stimulus/today');
      const data = await response.json();

      if (data.success && data.stimulus) {
        setCurrentStimulus(data.stimulus);
      } else {
        setError('ÏûêÍ∑πÏùÑ Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (err) {
      setError('ÎÑ§Ìä∏ÏõåÌÅ¨ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      console.error('ÏûêÍ∑π Î°úÎìú Ïò§Î•ò:', err);
    } finally {
      setIsLoading(false);
    }
  };

  // ÌéòÏù¥ÏßÄ Î≥ÄÍ≤Ω
  const handlePageChange = (page: number) => {
    if (page >= 1 && page <= 3) {
      setCurrentPage(page);
    }
  };

  // Ï¢åÏö∞ ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò
  const handleNavigation = (direction: 'prev' | 'next') => {
    if (direction === 'prev') {
      setCurrentPage(currentPage === 1 ? 3 : currentPage - 1);
    } else {
      setCurrentPage(currentPage === 3 ? 1 : currentPage + 1);
    }
  };

  // Í∞úÎ≥Ñ Í≤©Ïñ∏ Ï†ÄÏû•
  const handleSaveQuote = async (quoteId: string, quoteContent: string) => {
    if (!currentStimulus) return;

    try {
      console.log('=== Í∞úÎ≥Ñ Í≤©Ïñ∏ Ï†ÄÏû• ÏãúÏûë ===');
      console.log('Í≤©Ïñ∏ ID:', quoteId);
      console.log('Í≤©Ïñ∏ ÎÇ¥Ïö©:', quoteContent);

      const requestData = {
        stimulus_id: currentStimulus.id,
        save_type: 'quote',  // Í∞úÎ≥Ñ Í≤©Ïñ∏ Ï†ÄÏû•
        quote_id: quoteId    // ÌäπÏ†ï Í≤©Ïñ∏ ID
      };

      console.log('ÏöîÏ≤≠ Îç∞Ïù¥ÌÑ∞:', requestData);

      const response = await fetch('/stimulus/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestData)
      });

      console.log('ÏùëÎãµ ÏÉÅÌÉú:', response.status);
      const data = await response.json();
      console.log('ÏùëÎãµ Îç∞Ïù¥ÌÑ∞:', data);

      if (data.success) {
        alert('Í≤©Ïñ∏Ïù¥ Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§!');
      } else {
        alert('Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§: ' + (data.message || 'Ïïå Ïàò ÏóÜÎäî Ïò§Î•ò'));
      }
    } catch (err) {
      console.error('Í≤©Ïñ∏ Ï†ÄÏû• Ïò§Î•ò:', err);
      alert('Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  // Ï†ÄÏû•ÌïòÍ∏∞ - ÌòÑÏû¨ Î≥¥Í≥† ÏûàÎäî ÏÑ∏Ìä∏Îßå Ï†ÄÏû•
  const handleSave = async () => {
    if (!currentStimulus) return;

    try {
      console.log('=== ÏûêÍ∑π Ï†ÄÏû• ÏãúÏûë ===');
      console.log('ÏûêÍ∑π ID:', currentStimulus.id);
      console.log('ÌòÑÏû¨ ÌéòÏù¥ÏßÄ:', currentPage);

      const requestData = {
        stimulus_id: currentStimulus.id,
        save_type: 'current_set',  // ÌòÑÏû¨ ÏÑ∏Ìä∏Îßå Ï†ÄÏû•
        current_page: currentPage  // ÌòÑÏû¨ ÌéòÏù¥ÏßÄ Î≤àÌò∏ Ï†ÑÏÜ°
      };

      console.log('ÏöîÏ≤≠ Îç∞Ïù¥ÌÑ∞:', requestData);

      const response = await fetch('/stimulus/save', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestData)
      });

      console.log('ÏùëÎãµ ÏÉÅÌÉú:', response.status);
      const data = await response.json();
      console.log('ÏùëÎãµ Îç∞Ïù¥ÌÑ∞:', data);

      if (data.success) {
        alert(`${currentPage}Î≤àÏß∏ ÏÑ∏Ìä∏Í∞Ä Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§!`);
      } else {
        alert('Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§: ' + (data.message || 'Ïïå Ïàò ÏóÜÎäî Ïò§Î•ò'));
      }
    } catch (err) {
      console.error('Ï†ÄÏû• Ïò§Î•ò:', err);
      alert('Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }

    setIsMenuOpen(false);
  };

  // Ïª®ÌÖêÏ∏† Î†åÎçîÎßÅ
  const renderContent = () => {
    if (isLoading) {
      return <LoadingState>Ïò§ÎäòÏùò ÏûêÍ∑πÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...</LoadingState>;
    }

    if (error) {
      return (
        <ErrorState>
          <div>{error}</div>
          <button onClick={loadTodayStimulus}>Îã§Ïãú ÏãúÎèÑ</button>
        </ErrorState>
      );
    }

    if (!currentStimulus) {
      return <LoadingState>ÏûêÍ∑πÏù¥ ÏóÜÏäµÎãàÎã§.</LoadingState>;
    }

    // ÌòÑÏû¨ ÌéòÏù¥ÏßÄÏóê Ìï¥ÎãπÌïòÎäî Ïã¨Î¶¨ÌïôÏûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
    const getCurrentPersonaInfo = () => {
      if (currentStimulus.persona_info && currentStimulus.persona_info[currentPage - 1]) {
        return currentStimulus.persona_info[currentPage - 1];
      }
      return null;
    };

    const currentPersona = getCurrentPersonaInfo();

    return (
      <StimulusCard>
        <Header>
          <DateDisplay>{currentStimulus.date}</DateDisplay>
          <PersonaName>
            {currentPersona ? currentPersona.name : currentStimulus.persona_name}
          </PersonaName>
        </Header>

        <MainThemeBox>
          <MainTheme>
            {currentPersona ? currentPersona.theme : currentStimulus.main_theme}
          </MainTheme>
          <ActionButton className="action-button" onClick={() => handleInteraction('theme', currentPersona ? currentPersona.theme : currentStimulus.main_theme)}>
            ‚ãØ
          </ActionButton>
        </MainThemeBox>

        <QuotesContainer>
          <VerticalLine />

          {/* ÌòÑÏû¨ ÌéòÏù¥ÏßÄÏùò Í≤©Ïñ∏ ÏÑ∏Ìä∏ ÌëúÏãú */}
          {currentPage <= 3 && currentStimulus.quote_sets && currentStimulus.quote_sets[currentPage - 1] && (
            <QuoteSetContainer>
              {currentStimulus.quote_sets[currentPage - 1].quotes.map((quote, index) => (
                <QuoteBox 
                  key={quote.id}
                  $isVisible={true}
                  $isFirst={index === 0}
                  $isLast={index === currentStimulus.quote_sets[currentPage - 1].quotes.length - 1}
                >
                  <QuoteContent>{quote.content}</QuoteContent>
                  <ActionButton 
                    className="action-button" 
                    onClick={() => handleSaveQuote(quote.id, quote.content)}
                    title="Ïù¥ Í≤©Ïñ∏ Ï†ÄÏû•ÌïòÍ∏∞"
                  >
                    üíæ
                  </ActionButton>
                </QuoteBox>
              ))}
            </QuoteSetContainer>
          )}

          {/* Ìò∏ÌôòÏÑ±ÏùÑ ÏúÑÌïú fallback - Ïò§ÎûòÎêú Îç∞Ïù¥ÌÑ∞ Íµ¨Ï°∞ Ï≤òÎ¶¨ */}
          {(!currentStimulus.quote_sets && (currentStimulus as any).quotes) && (
            <QuoteSetContainer>
              <QuoteBox $isVisible={true} $isFirst={true} $isLast={true}>
                <QuoteContent>{(currentStimulus as any).quotes[currentPage - 1]?.content}</QuoteContent>
                <ActionButton 
                  className="action-button" 
                  onClick={() => handleInteraction('legacy', (currentStimulus as any).quotes[currentPage - 1]?.content || '')}
                >
                  ‚ãØ
                </ActionButton>
              </QuoteBox>
            </QuoteSetContainer>
          )}
        </QuotesContainer>

        <SummaryQuoteBox>
          <SummaryQuote>
            {currentPersona ? currentPersona.summary : currentStimulus.summary_quote}
          </SummaryQuote>
          <ActionButton className="action-button" onClick={() => handleInteraction('summary', currentPersona ? currentPersona.summary : currentStimulus.summary_quote)}>
            ‚ãØ
          </ActionButton>
        </SummaryQuoteBox>

        {/* ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò Î≤ÑÌäº */}
        <NavigationContainer 
          onMouseEnter={(e) => {
            const buttons = e.currentTarget.querySelectorAll('button');
            buttons.forEach(btn => btn.style.opacity = '0.7');
          }}
          onMouseLeave={(e) => {
            const buttons = e.currentTarget.querySelectorAll('button');
            buttons.forEach(btn => btn.style.opacity = '0');
          }}
        >
          <NavigationButton 
            $side="left" 
            onClick={() => handleNavigation('prev')}
          >
            ‚Üê
          </NavigationButton>
          <NavigationButton 
            $side="right" 
            onClick={() => handleNavigation('next')}
          >
            ‚Üí
          </NavigationButton>
        </NavigationContainer>

        {/* ÌéòÏù¥ÏßÄ Ïù∏ÎîîÏºÄÏù¥ÌÑ∞ Î∞è Î©îÎâ¥ */}
        <IndicatorContainer>
          <PageIndicator>
            {[1, 2, 3].map(page => (
              <PageDot 
                key={page} 
                $isActive={currentPage === page}
                onClick={() => handlePageChange(page)}
              >
                <PageNumber $isActive={currentPage === page}>{page}</PageNumber>
                <PageBar $isActive={currentPage === page} />
              </PageDot>
            ))}
          </PageIndicator>

          <div style={{ position: 'relative' }}>
            <MenuButton onClick={() => setIsMenuOpen(!isMenuOpen)}>
              üìÑ
            </MenuButton>
            <DropdownMenu $isVisible={isMenuOpen}>
              <MenuOption onClick={handleSave}>
