import React, { useState } from 'react';
import { submitQA } from '../api/qaApi';
import { QACreate } from '../types/qaTypes';
import { useNavigate } from 'react-router-dom';

const QAPage: React.FC = () => {
    const [answer1, setAnswer1] = useState('');
    const [answer2, setAnswer2] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        const qaData: QACreate = {
            question_1_answer: answer1,
            question_2_answer: answer2,
            // Add more answers as needed
        };

        try {
            await submitQA(qaData);
            alert('QA submitted successfully!');
            // TODO: Update user's QA status in global state/context
            navigate('/'); // Navigate to main app
        } catch (error) {
            console.error('Failed to submit QA:', error);
            alert('Failed to submit QA. Please try again.');
        }
    };

    return (
        <div style={{ padding: '2rem' }}>
            <h1>Initial Questions (QA)</h1>
            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: '1rem' }}>
                    <label htmlFor="question1">Question 1: What is your favorite color?</label>
                    <input
                        id="question1"
                        type="text"
                        value={answer1}
                        onChange={(e) => setAnswer1(e.target.value)}
                        required
                        style={{ width: '100%', padding: '0.5rem' }}
                    />
                </div>
                <div style={{ marginBottom: '1rem' }}>
                    <label htmlFor="question2">Question 2: What is your favorite animal?</label>
                    <input
                        id="question2"
                        type="text"
                        value={answer2}
                        onChange={(e) => setAnswer2(e.target.value)}
                        required
                        style={{ width: '100%', padding: '0.5rem' }}
                    />
                </div>
                {/* Add more questions here */}
                <button type="submit" style={{ padding: '0.75rem 1.5rem', cursor: 'pointer' }}>
                    Submit QA
                </button>
            </form>
        </div>
    );
};

export default QAPage;
