import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { TrainingThread } from '../../shared/types';
import { chatApi } from '../../shared/api/chatApi';
import MessageInput, { MessageInputRef } from '../../shared/components/MessageInput';
import Message from '../../shared/components/Message';
import LoadingMessage from '../../shared/components/LoadingMessage';

interface VerbalizationViewProps {
  selectedThread: TrainingThread | null;
  threads: TrainingThread[];
  onThreadUpdate: () => void;
  onCreateNewThread: () => Promise<void>;
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  background: white;
`;

const HeaderSection = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  border-bottom: 1px solid #e0e0e0;
  background: #f8f9fa;
`;

const HeaderTitle = styled.h2`
  font-size: 24px;
  font-weight: 700;
  color: #343a40;
  margin: 0;
`;

const NewThreadButton = styled.button`
  background: linear-gradient(135deg, #6c757d 0%, #5a6268 100%);
  color: white;
  border: none;
  border-radius: 8px;
  padding: 10px 20px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s ease;
  box-shadow: 0 2px 4px rgba(108, 117, 125, 0.2);

  &:hover {
    background: linear-gradient(135deg, #5a6268 0%, #495057 100%);
    transform: translateY(-1px);
    box-shadow: 0 4px 8px rgba(108, 117, 125, 0.3);
  }

  &:active {
    transform: translateY(0);
