  const [editingMessageIndex, setEditingMessageIndex] = useState<number | null>(null);
  
  // Î°úÏª¨ Î©îÏãúÏßÄ ÏÉÅÌÉú - ÏÇ¨Ïö©Ïûê Î©îÏãúÏßÄÎ•º Ï¶âÏãú UIÏóê Î∞òÏòÅÌïòÍ∏∞ ÏúÑÌï®
