    }
  }, [localMessages, isLoading]);

  // Î©îÏãúÏßÄ Ï†ÑÏÜ° Ï≤òÎ¶¨
  const handleSendMessage = async (message: string): Promise<boolean> => {
    if (!message.trim() || isLoading) return false;

    // 1Îã®Í≥Ñ: ÏÇ¨Ïö©Ïûê Î©îÏãúÏßÄÎ•º Ï¶âÏãú UIÏóê Ï∂îÍ∞Ä
    const userMessage = {
      role: 'user' as const,
      content: message.trim(),
      timestamp: new Date().toISOString(),
    };
    
    setLocalMessages(prev => [...prev, userMessage]);
    setIsLoading(true);

    try {
      console.log('=== Ïñ∏Ïñ¥Ìôî Î©îÏãúÏßÄ Ï†ÑÏÜ° ===');
      console.log('selectedThread:', selectedThread);
      console.log('message:', message);

      // 2Îã®Í≥Ñ: API Ìò∏Ï∂ú - Ïä§Î†àÎìú ID Ï†ÑÎã¨ (ÏóÜÏúºÎ©¥ undefined)
      const response = await chatApi.chatWithVerbalization(
        message,
        selectedThread?.id // Ïä§Î†àÎìúÍ∞Ä ÏóÜÏúºÎ©¥ undefined Ï†ÑÎã¨
      );

      console.log('API ÏùëÎãµ Ï†ÑÏ≤¥:', response);
      console.log('ÏùëÎãµ Îç∞Ïù¥ÌÑ∞:', response.data);
      console.log('ÏùëÎãµ ÏóêÎü¨:', response.error);

      if (response.data) {
        console.log('Î©îÏãúÏßÄ Ï†ÑÏÜ° ÏÑ±Í≥µ');
        console.log('ÏùëÎãµ Íµ¨Ï°∞:', {
          thread_id: response.data.thread_id,
          user_message: response.data.user_message,
          response: response.data.response,
          thread_updated_at: response.data.thread_updated_at
        });
        
        // 3Îã®Í≥Ñ: AI ÏùëÎãµÏùÑ Î°úÏª¨ Î©îÏãúÏßÄÏóê Ï∂îÍ∞Ä
        const assistantMessage = {
          role: 'assistant' as const,
          content: response.data.response.content,
          timestamp: response.data.response.timestamp,
          persona_name: 'Ïñ∏Ïñ¥Ìôî ÎèÑÏö∞ÎØ∏',
        };
        
        setLocalMessages(prev => [...prev, assistantMessage]);
        
        // 4Îã®Í≥Ñ: Î∞±ÏóîÎìúÏóêÏÑú ÏÉà Ïä§Î†àÎìúÍ∞Ä ÏÉùÏÑ±ÎêòÏóàÍ±∞ÎÇò Í∏∞Ï°¥ Ïä§Î†àÎìúÍ∞Ä ÏóÖÎç∞Ïù¥Ìä∏Îêú Í≤ΩÏö∞
        // Ïä§Î†àÎìú Î™©Î°ùÍ≥º ÏÑ†ÌÉùÎêú Ïä§Î†àÎìúÎ•º ÏÉàÎ°úÍ≥†Ïπ®
        await onThreadUpdate();
        
        // ÏÉàÎ°ú ÏÉùÏÑ±Îêú Ïä§Î†àÎìúÍ∞Ä ÏÑ†ÌÉùÎêòÎèÑÎ°ù Ï∂îÍ∞Ä Ï≤òÎ¶¨
        if (!selectedThread && response.data.thread_id) {
          console.log('ÏÉà Ïä§Î†àÎìú ÏÉùÏÑ±Îê®, ÏÑ†ÌÉù ÏóÖÎç∞Ïù¥Ìä∏ ÌïÑÏöî:', response.data.thread_id);
          // ÏïΩÍ∞ÑÏùò ÏßÄÏó∞ ÌõÑ Ïä§Î†àÎìú Î™©Î°ù Ïû¨Î°úÎìúÌïòÏó¨ ÏÉà Ïä§Î†àÎìú ÏÑ†ÌÉù
          setTimeout(async () => {
            await onThreadUpdate();
          }, 100);
        }
        
        return true;
      } else {
        // Ïã§Ìå® Ïãú ÏÇ¨Ïö©Ïûê Î©îÏãúÏßÄ Ï†úÍ±∞
        setLocalMessages(prev => prev.slice(0, -1));
        console.error('Î©îÏãúÏßÄ Ï†ÑÏÜ° Ïã§Ìå®:', response.error);
        showCopyToast(`Ïñ∏Ïñ¥Ìôî Ï±ÑÌåÖ Ïã§Ìå®: ${response.error}`);
        return false;
      }
    } catch (error) {
      // Ïò§Î•ò Ïãú ÏÇ¨Ïö©Ïûê Î©îÏãúÏßÄ Ï†úÍ±∞
      setLocalMessages(prev => prev.slice(0, -1));
      console.error('Î©îÏãúÏßÄ Ï†ÑÏÜ° Ïò§Î•ò:', error);
      showCopyToast(`ÎÑ§Ìä∏ÏõåÌÅ¨ Ïò§Î•ò: ${error}`);
      return false;
    } finally {
      setIsLoading(false);
    }
  };

  // ÏÉà Ïä§Î†àÎìú ÏÉùÏÑ± Ï≤òÎ¶¨
  const handleCreateNewThread = async () => {
    try {
      console.log('=== ÏÉà Ïä§Î†àÎìú ÏÉùÏÑ± ÏãúÏûë ===');
      
      // 1Îã®Í≥Ñ: Î°úÏª¨ Î©îÏãúÏßÄ Ï¥àÍ∏∞Ìôî (ÏÉà ÎåÄÌôî Ï§ÄÎπÑ)
      setLocalMessages([]);
      
      // 2Îã®Í≥Ñ: ÏÉà Ïä§Î†àÎìú ÏÉùÏÑ±
      console.log('ÏÉà Ïä§Î†àÎìú ÏÉùÏÑ± Ï§ë...');
      
      // 3Îã®Í≥Ñ: ÏÉÅÏúÑ Ïª¥Ìè¨ÎÑåÌä∏Ïóê ÏÉà Ïä§Î†àÎìú ÏÉùÏÑ± ÏöîÏ≤≠
      await onCreateNewThread();
      
      // 4Îã®Í≥Ñ: ÏÑ±Í≥µ Î©îÏãúÏßÄ ÌëúÏãú
      showCopyToast('ÏÉà ÎåÄÌôîÍ∞Ä ÏãúÏûëÎêòÏóàÏäµÎãàÎã§!');
      
      console.log('ÏÉà Ïä§Î†àÎìú ÏÉùÏÑ± Î∞è ÏÑ†ÌÉù ÏôÑÎ£å');
    } catch (error) {
      console.error('ÏÉà Ïä§Î†àÎìú ÏÉùÏÑ± Ïò§Î•ò:', error);
      showCopyToast('ÏÉà ÎåÄÌôî ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  // ÌÜ†Ïä§Ìä∏ Î©îÏãúÏßÄ ÌëúÏãú Ìï®Ïàò
  const showCopyToast = (message: string) => {
    setToastMessage(message);
    setShowToast(true);
    setTimeout(() => setShowToast(false), 3000);
  };

  // ÌÖçÏä§Ìä∏ Î≥µÏÇ¨ Ïú†Ìã∏Î¶¨Ìã∞ Ìï®Ïàò
  const copyToClipboard = async (text: string, successMessage: string) => {
    try {
      await navigator.clipboard.writeText(text);
      showCopyToast(successMessage);
    } catch (error) {
      console.error('Î≥µÏÇ¨ Ïã§Ìå®:', error);
      showCopyToast('Î≥µÏÇ¨ Ïã§Ìå®');
    }
  };

  // Î©îÏãúÏßÄ Î≥µÏÇ¨ Í∏∞Îä•
  const handleCopyMessage = async (messageContent: string) => {
    await copyToClipboard(messageContent, 'Î©îÏãúÏßÄÍ∞Ä Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§');
  };

  // Î©îÏãúÏßÄ ÏàòÏ†ï Í¥ÄÎ†® Ìï®ÏàòÎì§
  const handleStartEdit = (messageIndex: number) => {
    setEditingMessageIndex(messageIndex);
  };

  const handleEditMessage = async (messageIndex: number, newContent: string) => {
    // Ïñ∏Ïñ¥ÌôîÏóêÏÑúÎäî Î©îÏãúÏßÄ ÏàòÏ†ï Í∏∞Îä•ÏùÑ Ï†úÍ≥µÌïòÏßÄ ÏïäÏùå
    // ÌïÑÏöîÏãú Ï∂îÌõÑ Íµ¨ÌòÑ
    return false;
  };

  const handleCancelEdit = () => {
    setEditingMessageIndex(null);
  };

  // Ïï°ÏÖò Î≤ÑÌäº Ìï∏Îì§Îü¨Îì§ (Ïñ∏Ïñ¥ÌôîÏóêÏÑúÎäî Í∞ÑÎã®ÌïòÍ≤å Íµ¨ÌòÑ)
  const handleSunAction = () => {
    showCopyToast('Ïñ∏Ïñ¥ÌôîÏóêÏÑúÎäî Ìï¥ÏÑù Í∏∞Îä•ÏùÑ Ï†úÍ≥µÌïòÏßÄ ÏïäÏäµÎãàÎã§');
  };

  const handlePersonAction = () => {
    const proceedPrompt = "Ïù¥ Í∞êÏ†ïÏù¥ÎÇò ÏÉùÍ∞ÅÏóê ÎåÄÌï¥ Îçî ÍπäÏù¥ ÌÉêÍµ¨Ìï¥Î≥¥Í≥† Ïã∂Ïñ¥Ïöî.";
    if (messageInputRef.current) {
      messageInputRef.current.insertText(proceedPrompt);
    }
    showCopyToast('ÌÉêÍµ¨ ÌîÑÎ°¨ÌîÑÌä∏Í∞Ä ÏûÖÎ†•Ï∞ΩÏóê Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§');
  };

  const handleDocumentAction = () => {
    if (selectedThread && selectedThread.messages.length > 0) {
      const lastAssistantMessage = selectedThread.messages
        .slice()
        .reverse()
        .find(msg => msg.role === 'assistant');
      
      if (lastAssistantMessage) {
        copyToClipboard(lastAssistantMessage.content, 'ÎßàÏßÄÎßâ ÏùëÎãµÏù¥ Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§');
      } else {
        showCopyToast('Î≥µÏÇ¨Ìï† ÏùëÎãµÏù¥ ÏóÜÏäµÎãàÎã§');
      }
    }
  };

  return (
    <Container>
      {/* Ìó§Îçî */}
      <HeaderSection>
        <HeaderTitle>Ïñ∏Ïñ¥Ìôî</HeaderTitle>
        <NewThreadButton onClick={handleCreateNewThread}>
          ÏÉà ÎåÄÌôî ÏãúÏûë
        </NewThreadButton>
      </HeaderSection>

      {/* Ï±ÑÌåÖ ÏòÅÏó≠ */}
      <ChatSection>
        <ChatMessages ref={chatMessagesRef}>
          {localMessages.length === 0 ? (
            <EmptyChat>
              <EmptyIcon>üí≠</EmptyIcon>
              <div>
                <strong>Ïñ∏Ïñ¥ÌôîÎäî ÎßâÏó∞ÌïòÍ≥† Î≠îÏßÄ Î™®Î•º Í∞êÏ†ïÏù¥ÎÇò ÏÉùÍ∞ÅÏùÑ ÎßêÎ°ú ÌëúÌòÑÌï®ÏúºÎ°úÏç®<br />
                Í∑∏Í≤ÉÏùÑ Ïù∏ÏãùÌïòÍ≥† ÎßåÎì§Ïñ¥ Í∞ÄÎäî Í≤ÉÏûÖÎãàÎã§.</strong>
                <br /><br />
                ÏûêÏú†Î°≠Í≤å ÎãπÏã†Ïùò ÏÉùÍ∞ÅÏùÑ ÎßêÎ°ú ÎÇ¥Î±âÏúºÏÑ∏Ïöî!
                <br /><br />
                Ïñ¥Îñ§ Í≤ÉÏù¥Îì† Ï¢ãÏäµÎãàÎã§. ÏßÄÍ∏à ÎäêÎÅºÍ≥† ÏûàÎäî Í∞êÏ†ïÏù¥ÎÇò ÏÉùÍ∞ÅÏùÑ Ìé∏ÏïàÌïòÍ≤å ÎßêÏîÄÌï¥ Ï£ºÏÑ∏Ïöî.
              </div>
            </EmptyChat>
          ) : (
            localMessages.map((message, index) => (
              <Message
                key={index}
                message={{
                  role: message.role,
                  content: message.content,
                  timestamp: message.timestamp,
                  persona_id: message.role === 'assistant' ? 'verbalization' : undefined,
                  persona_name: message.role === 'assistant' ? 'Ïñ∏Ïñ¥Ìôî ÎèÑÏö∞ÎØ∏' : undefined,
                }}
                personas={{}} // Ïñ∏Ïñ¥ÌôîÏóêÏÑúÎäî ÌéòÎ•¥ÏÜåÎÇò Ï†ïÎ≥¥ Î∂àÌïÑÏöî
                showActionButtons={false}
                onCopy={() => handleCopyMessage(message.content)}
                onEdit={message.role === 'user' ? () => handleStartEdit(index) : undefined}
                onEditSave={message.role === 'user' ? async (newContent) => {
                  const success = await handleEditMessage(index, newContent);
                  if (success) {
                    setEditingMessageIndex(null);
                  }
                  return success;
                } : undefined}
                onEditCancel={() => handleCancelEdit()}
                isEditing={editingMessageIndex === index}
                onSunAction={handleSunAction}
                onPersonAction={handlePersonAction}
                onDocumentAction={handleDocumentAction}
              />
            ))
          )}
          
          {/* Î°úÎî© Î©îÏãúÏßÄ */}
          {isLoading && (
            <LoadingMessage 
              personaName="Ïñ∏Ïñ¥Ìôî ÎèÑÏö∞ÎØ∏"
              personaColor="#666"
            />
          )}
        </ChatMessages>

        {/* ÏûÖÎ†•Ï∞Ω */}
        <ChatInputSection>
          <MessageInput
            ref={messageInputRef}
            onSendMessage={handleSendMessage}
            disabled={isLoading}
            placeholder="Ïñ∏Ïñ¥ÌôîÎäî ÎßâÏó∞ÌïòÍ≥† Î≠îÏßÄ Î™®Î•º Í∞êÏ†ïÏù¥ÎÇò ÏÉùÍ∞ÅÏùÑ ÎßêÎ°ú ÌëúÌòÑÌï®ÏúºÎ°úÏç® Í∑∏Í≤ÉÏùÑ Ïù∏ÏãùÌïòÍ≥† ÎßåÎì§Ïñ¥ Í∞ÄÎäî Í≤ÉÏûÖÎãàÎã§. ÏûêÏú†Î°≠Í≤å ÎãπÏã†Ïùò ÏÉùÍ∞ÅÏùÑ ÎßêÎ°ú ÎÇ¥Î±âÏúºÏÑ∏Ïöî!"
          />
        </ChatInputSection>
      </ChatSection>

      {/* ÌÜ†Ïä§Ìä∏ Î©îÏãúÏßÄ */}
      <Toast show={showToast}>
        {toastMessage}
      </Toast>
    </Container>
  );
};

export default VerbalizationView;
