const MemoVaultView: React.FC<MemoVaultViewProps> = ({
  personas,
  onNavigateToThread
}) => {
  const [memos, setMemos] = useState<MemoVaultItem[]>([]);
  const [filteredMemos, setFilteredMemos] = useState<MemoVaultItem[]>([]);
  const [currentFilter, setCurrentFilter] = useState<string>('Ï†ÑÏ≤¥');
  const [loading, setLoading] = useState(true);
  const [contextMenu, setContextMenu] = useState<ContextMenu | null>(null);
  const [confirmationModal, setConfirmationModal] = useState<ConfirmationModalData | null>(null);
  const [showFilterDropdown, setShowFilterDropdown] = useState(false);

  // ÌïÑÌÑ∞ ÏòµÏÖòÎì§
  const [filterOptions, setFilterOptions] = useState<string[]>(['Ï†ÑÏ≤¥', 'Ìï¥ÏÑù', 'ÎÇòÏïÑÍ∞ÄÍ∏∞', 'Î¨∏Ïû•', 'Ïñ∏Ïñ¥Ìôî']);
  const [currentFilterIndex, setCurrentFilterIndex] = useState(0);

  const loadMemos = useCallback(async () => {
    try {
      setLoading(true);
      const data = await sentenceApi.getVaultMemos();
      setMemos(data);

      // ÌïÑÌÑ∞ ÏòµÏÖòÏóê ÌéòÎ•¥ÏÜåÎÇòÎì§ Ï∂îÍ∞Ä
      const personaNames = Object.values(personas).map(p => p.name);
      setFilterOptions(['Ï†ÑÏ≤¥', 'Ìï¥ÏÑù', 'ÎÇòÏïÑÍ∞ÄÍ∏∞', 'Î¨∏Ïû•', 'Ïñ∏Ïñ¥Ìôî', ...personaNames]);
    } catch (error) {
      console.error('Î©îÎ™® Ï†ÄÏû•Í≥† Î°úÎî© Ïã§Ìå®:', error);
    } finally {
      setLoading(false);
    }
  }, [personas]);

  const applyFilter = useCallback(() => {
    let filtered = [...memos];

    if (currentFilter !== 'Ï†ÑÏ≤¥') {
      if (currentFilter === 'Ìï¥ÏÑù') {
        filtered = filtered.filter(m => m.source_thread_type === 'interpretation');
      } else if (currentFilter === 'ÎÇòÏïÑÍ∞ÄÍ∏∞') {
        filtered = filtered.filter(m => m.source_thread_type === 'proceed');
      } else if (currentFilter === 'Î¨∏Ïû•') {
        filtered = filtered.filter(m => m.source_thread_type === 'sentence');
      } else if (currentFilter === 'Ïñ∏Ïñ¥Ìôî') {
        filtered = filtered.filter(m => m.source_thread_type === 'verbalization');
      } else {
        // ÌéòÎ•¥ÏÜåÎÇò Ïù¥Î¶ÑÏúºÎ°ú ÌïÑÌÑ∞ÎßÅ
        const personaId = Object.keys(personas).find(id => personas[id].name === currentFilter);
        if (personaId) {
          filtered = filtered.filter(m => m.tags.includes(personaId) || m.source_message_id?.includes(personaId));
        }
      }
    }

    // ÎÇ†ÏßúÏàú Ï†ïÎ†¨ (ÏµúÏã†Ïàú)
    filtered.sort((a, b) => new Date(b.created_at).getTime() - new Date(a.created_at).getTime());

    setFilteredMemos(filtered);
  }, [memos, currentFilter, personas]);

  useEffect(() => {
    loadMemos();
  }, [loadMemos]);

  useEffect(() => {
    applyFilter();
  }, [memos, currentFilter, applyFilter]);

  // ÎìúÎ°≠Îã§Ïö¥ Ïô∏Î∂Ä ÌÅ¥Î¶≠ Í∞êÏßÄ
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const target = event.target as Element;
      if (!target.closest('.filter-dropdown')) {
        setShowFilterDropdown(false);
      }
    };

    if (showFilterDropdown) {
      document.addEventListener('click', handleClickOutside);
      return () => document.removeEventListener('click', handleClickOutside);
    }
  }, [showFilterDropdown]);

  // Ïª®ÌÖçÏä§Ìä∏ Î©îÎâ¥ Ïô∏Î∂Ä ÌÅ¥Î¶≠ Í∞êÏßÄ
  useEffect(() => {
    const handleClickOutside = () => {
      setContextMenu(null);
    };

    if (contextMenu) {
      document.addEventListener('click', handleClickOutside);
      return () => document.removeEventListener('click', handleClickOutside);
    }
  }, [contextMenu]);



  const handleFilterChange = (filter: string) => {
    setCurrentFilter(filter);
    setCurrentFilterIndex(filterOptions.indexOf(filter));
    setShowFilterDropdown(false);
  };

  const handleNavigateFilter = (direction: 'prev' | 'next') => {
    const newIndex = direction === 'prev' 
      ? Math.max(0, currentFilterIndex - 1)
      : Math.min(filterOptions.length - 1, currentFilterIndex + 1);

    setCurrentFilterIndex(newIndex);
    setCurrentFilter(filterOptions[newIndex]);
  };

  const toggleFilterDropdown = () => {
    setShowFilterDropdown(!showFilterDropdown);
  };

  const handleResetData = async () => {
    if (window.confirm('Î™®Îì† Î©îÎ™®ÏôÄ Ï†ÄÏû•Í≥† Îç∞Ïù¥ÌÑ∞Î•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå? Ïù¥ ÏûëÏóÖÏùÄ ÎêòÎèåÎ¶¥ Ïàò ÏóÜÏäµÎãàÎã§.')) {
      try {
        await sentenceApi.resetDevData();
        await loadMemos(); // Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
        alert('Îç∞Ïù¥ÌÑ∞Í∞Ä Î¶¨ÏÖãÎêòÏóàÏäµÎãàÎã§.');
      } catch (error) {
        console.error('Îç∞Ïù¥ÌÑ∞ Î¶¨ÏÖã Ïã§Ìå®:', error);
        alert('Îç∞Ïù¥ÌÑ∞ Î¶¨ÏÖãÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    }
  };

  const handleMemoClick = (e: React.MouseEvent, memo: MemoVaultItem) => {
    e.preventDefault();

    if (e.type === 'contextmenu') {
      setContextMenu({
        x: e.pageX,
        y: e.pageY,
        memo
      });
      return;
    }

    // ÏùºÎ∞ò ÌÅ¥Î¶≠ Ïãú ÏÉÅÌò∏ÏûëÏö© ÌôïÏù∏ Î™®Îã¨
    setConfirmationModal({
      type: 'interact',
      memo
    });
  };

  const handleContextMenuAction = async (action: 'interact' | 'delete', memo: MemoVaultItem) => {
    setContextMenu(null);

    if (action === 'interact') {
      setConfirmationModal({
        type: 'interact',
        memo
      });
    } else if (action === 'delete') {
      setConfirmationModal({
        type: 'delete',
        memo
      });
    }
  };

  const handleConfirmAction = async () => {
    if (!confirmationModal) return;

    try {
      if (confirmationModal.type === 'interact') {
        const response = await sentenceApi.interactWithVaultMemo(confirmationModal.memo.id);

        if (response.success && response.source_thread_id && response.source_thread_type) {
          // ÏõêÎ≥∏ Ïä§Î†àÎìúÎ°ú Ïù¥ÎèôÌïòÎ©¥ÏÑú ÏÉÅÌò∏ÏûëÏö© Î©îÏãúÏßÄ Ï†ÑÎã¨
          onNavigateToThread(
            response.source_thread_id,
            response.source_thread_type,
            response.interaction_message
          );
        }
      } else if (confirmationModal.type === 'delete') {
        await sentenceApi.deleteVaultMemo(confirmationModal.memo.id);
        await loadMemos(); // Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
      }
    } catch (error) {
      console.error('Î©îÎ™® Ïï°ÏÖò Ïã§Ìå®:', error);
    } finally {
      setConfirmationModal(null);
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('ko-KR', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getThreadTypeLabel = (threadType?: string) => {
    switch (threadType) {
      case 'interpretation': return 'Ìï¥ÏÑù';
      case 'proceed': return 'ÎÇòÏïÑÍ∞ÄÍ∏∞';
      case 'sentence': return 'Î¨∏Ïû•';
      case 'verbalization': return 'Ïñ∏Ïñ¥Ìôî';
      default: return threadType || 'ÏïåÏàòÏóÜÏùå';
    }
  };

  const getPersonaName = (memo: MemoVaultItem) => {
    // source_message_idÏóêÏÑú ÌéòÎ•¥ÏÜåÎÇò ID Ï∂îÏ∂ú ÏãúÎèÑ
    if (memo.source_message_id) {
      const parts = memo.source_message_id.split('_');
      if (parts.length > 1) {
        const personaId = parts[1];
        const persona = personas[personaId];
        if (persona) {
          return persona.name;
        }
      }
    }

    // tagsÏóêÏÑú ÌéòÎ•¥ÏÜåÎÇò Ï∞æÍ∏∞ (thread_typeÏù¥ ÏïÑÎãå Í≤ÉÎì§Îßå)
    const excludeTypes = ['interpretation', 'proceed', 'sentence', 'verbalization'];
    for (const tag of memo.tags) {
      if (!excludeTypes.includes(tag)) {
        const persona = personas[tag];
        if (persona) {
          return persona.name;
        }
      }
    }

    // Ïñ∏Ïñ¥Ìôî ÌäπÎ≥Ñ Ï≤òÎ¶¨
    if (memo.source_message_id === 'verbalization_chat' || 
        memo.source_thread_type === 'verbalization' ||
        memo.tags.includes('verbalization')) {
      return 'Ïñ∏Ïñ¥Ìôî';
    }

    return 'ÏïåÏàòÏóÜÏùå';
  };

  if (loading) {
    return (
      <Container>
        <div style={{ textAlign: 'center', padding: '60px 20px', color: '#6c757d' }}>
          Î°úÎî© Ï§ë...
        </div>
      </Container>
    );
  }

  return (
    <Container>
      <Header>
        <NavigationButton
          onClick={() => handleNavigateFilter('prev')}
          disabled={currentFilterIndex === 0}
        >
          ‚Äπ
        </NavigationButton>

        <FilterContainer className="filter-dropdown">
          <FilterDropdownButton
            $isOpen={showFilterDropdown}
            onClick={toggleFilterDropdown}
          >
            {currentFilter}
            <span className="arrow">‚ñº</span>
          </FilterDropdownButton>
          <FilterDropdownMenu $isOpen={showFilterDropdown}>
            {filterOptions.map(option => (
              <FilterDropdownItem
                key={option}
                $isSelected={option === currentFilter}
                onClick={() => handleFilterChange(option)}
              >
                {option}
              </FilterDropdownItem>
            ))}
          </FilterDropdownMenu>
        </FilterContainer>

        <NavigationButton
          onClick={() => handleNavigateFilter('next')}
          disabled={currentFilterIndex === filterOptions.length - 1}
        >
          ‚Ä∫
        </NavigationButton>

        <ResetButton onClick={handleResetData}>
          üîÑ Îç∞Ïù¥ÌÑ∞ Î¶¨ÏÖã
        </ResetButton>
      </Header>

      <ContentArea>
        {filteredMemos.length === 0 ? (
          <EmptyState>
            <div className="icon">üìù</div>
            <div className="title">Ï†ÄÏû•Îêú Î©îÎ™®Í∞Ä ÏóÜÏäµÎãàÎã§</div>
            <div className="description">
              ÌõàÏäµÏù¥ÎÇò Ïñ∏Ïñ¥Ìôî ÌéòÏù¥ÏßÄÏóêÏÑú Î©îÎ™®Î•º ÏÉùÏÑ±ÌïòÎ©¥ ÏûêÎèôÏúºÎ°ú Ï†ÄÏû•Îê©ÎãàÎã§
            </div>
          </EmptyState>
        ) : (
          <MemoGrid>
            {filteredMemos.map(memo => (
              <MemoContainer key={memo.id}>
                <MemoRow
                  $isHighlighted={true}
                  onClick={(e) => handleMemoClick(e, memo)}
                  onContextMenu={(e) => handleMemoClick(e, memo)}
                >
                  <MemoContent>
                    <UserMemoContent>{memo.memo_content}</UserMemoContent>

                    <SentenceContent>
                      {memo.sentence_content}
                    </SentenceContent>
                  </MemoContent>

                  <StatusIndicators>
                    <StatusIndicator 
                      $type="memo" 
                      title="Î©îÎ™®"
                    />
                  </StatusIndicators>

                  <MemoMeta>
                    <ThreadTypeLabel>
                      {getPersonaName(memo)} - {getThreadTypeLabel(memo.source_thread_type)}
                    </ThreadTypeLabel>
                    <DateLabel>
                      {formatDate(memo.created_at)}
                    </DateLabel>
                  </MemoMeta>
                </MemoRow>
              </MemoContainer>
            ))}
          </MemoGrid>
        )}
      </ContentArea>

      {/* Ïª®ÌÖçÏä§Ìä∏ Î©îÎâ¥ */}
      {contextMenu && (
        <ContextMenuContainer $x={contextMenu.x} $y={contextMenu.y}>
          <ContextMenuItem
