import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { confusionApi, ConfusionItemDetail, ChatMessage as ApiChatMessage } from '../api/confusionApi';
import Message from '../../shared/components/Message';
import LoadingMessage from '../../shared/components/LoadingMessage';

interface DetailChatViewProps {
  personaId: string;
  itemId: string;
  itemTitle: string;
  itemType: 'theoretical' | 'insightful';
  onBack?: () => void;
}

interface ChatMessage {
  role: 'user' | 'assistant';
  content: string;
  timestamp: string;
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background: white;
`;

const Header = styled.div`
  padding: 16px 20px;
  background: #ffffff;
  color: #333;
  display: flex;
  align-items: center;
  gap: 12px;
  border-bottom: 1px solid #e9ecef;
`;

const BackButton = styled.button`
  background: #f8f9fa;
  border: 1px solid #ddd;
  color: #333;
  padding: 8px 12px;
  border-radius: 6px;
  cursor: pointer;
  font-size: 14px;
  transition: all 0.2s ease;

  &:hover {
    background: #e9ecef;
    border-color: #ccc;
  }
`;

const HeaderTitle = styled.h2`
  margin: 0;
  font-size: 18px;
  font-weight: 600;
  color: #333;
`;

const ChatContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
`;

const ChatMessages = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 16px;
  background: #ffffff;
`;

const InputArea = styled.div`
  padding: 16px 20px;
  background: white;
  border-top: 1px solid #e9ecef;
  display: flex;
  gap: 12px;
  align-items: flex-end;
`;

const MessageInput = styled.textarea`
  flex: 1;
  border: 1px solid #ddd;
  border-radius: 6px;
  padding: 12px 16px;
  font-size: 14px;
  line-height: 1.4;
  resize: none;
  max-height: 100px;
  min-height: 44px;
  
  &:focus {
    outline: none;
    border-color: #6c757d;
    box-shadow: 0 0 0 2px rgba(108, 117, 125, 0.1);
  }
`;

const SendButton = styled.button`
  background: #6c757d;
  color: white;
  border: 1px solid #5a6268;
  border-radius: 6px;
  width: 80px;
  height: 44px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 14px;
  font-weight: 500;
  transition: all 0.2s ease;

  &:hover {
    background: #5a6268;
    border-color: #545b62;
  }

  &:disabled {
    background: #ccc;
    border-color: #bbb;
    cursor: not-allowed;
  }
`;

const CategoryLabel = {
  interpretation: 'üìñ Ìï¥ÏÑù',
  key_sentence: 'üí° ÌïµÏã¨ Î¨∏Ïû•',
  proceed: 'üìù ÎÇòÏïÑÍ∞ÄÍ∏∞',
  proceed_key_sentence: '‚≠ê ÎÇòÏïÑÍ∞ÄÍ∏∞ ÌïµÏã¨ Î¨∏Ïû•'
};

const DetailChatView: React.FC<DetailChatViewProps> = ({
  personaId,
  itemId,
  itemTitle,
  itemType,
  onBack
}) => {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    // Ï¥àÍ∏∞ Î©îÏãúÏßÄ Î°úÎìú (Î∞±ÏóîÎìúÏóêÏÑú Î∞õÏïÑÏò®Îã§Í≥† Í∞ÄÏ†ï)
    loadInitialMessages();
  }, [itemId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const loadInitialMessages = async () => {
    setIsLoading(true);
    
    try {
      // Î∞±ÏóîÎìúÏóêÏÑú Ïã§Ï†ú Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå
      const detail = await confusionApi.getItemDetail(personaId, itemId);
      
      const initialMessages: ChatMessage[] = [
        {
          role: 'assistant',
          content: `üìñ Ìï¥ÏÑù

${itemTitle}Ïóê ÎåÄÌïú Ïã¨Ï∏µ Î∂ÑÏÑùÏùÑ ÏãúÏûëÌïòÍ≤†ÏäµÎãàÎã§.

${detail.interpretation}`,
          timestamp: new Date().toISOString()
        },
        {
          role: 'assistant',
          content: `üí° ÌïµÏã¨ Î¨∏Ïû•

"${detail.key_sentence}"`,
          timestamp: new Date().toISOString()
        },
        {
          role: 'assistant',
          content: `üìù ÎÇòÏïÑÍ∞ÄÍ∏∞

${detail.proceed}`,
          timestamp: new Date().toISOString()
        },
        {
          role: 'assistant',
          content: `‚≠ê ÎÇòÏïÑÍ∞ÄÍ∏∞ ÌïµÏã¨ Î¨∏Ïû•

"${detail.proceed_key_sentence}"`,
          timestamp: new Date().toISOString()
        }
      ];
      
      setMessages(initialMessages);
    } catch (error) {
      console.error('Ï¥àÍ∏∞ Î©îÏãúÏßÄ Î°úÎìú Ïã§Ìå®:', error);
      // ÏóêÎü¨ Î∞úÏÉù Ïãú Í∏∞Î≥∏ Î©îÏãúÏßÄ ÌëúÏãú
      const errorMessage: ChatMessage = {
        role: 'assistant',
        content: 'Ï£ÑÏÜ°Ìï©ÎãàÎã§. Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.',
        timestamp: new Date().toISOString()
      };
      setMessages([errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage: ChatMessage = {
      role: 'user',
      content: inputValue.trim(),
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);

    // Î∞±ÏóîÎìúÎ°ú Î©îÏãúÏßÄ Ï†ÑÏÜ° Î∞è ÏùëÎãµ Î∞õÍ∏∞
    try {
      // ÎåÄÌôî ÌûàÏä§ÌÜ†Î¶¨Î•º API ÌòïÏãùÏúºÎ°ú Î≥ÄÌôò
      const conversationHistory: ApiChatMessage[] = messages.map(msg => ({
        role: msg.role,
        content: msg.content
      }));

      const response = await confusionApi.chatWithItem(personaId, itemId, {
        user_message: userMessage.content,
        conversation_history: conversationHistory
      });

      const systemResponse: ChatMessage = {
        role: 'assistant',
        content: response.response,
        timestamp: new Date().toISOString()
      };
      
      setMessages(prev => [...prev, systemResponse]);
    } catch (error) {
      console.error('Î©îÏãúÏßÄ Ï†ÑÏÜ° Ïã§Ìå®:', error);
      const errorResponse: ChatMessage = {
        role: 'assistant',
        content: 'Ï£ÑÏÜ°Ìï©ÎãàÎã§. ÏùëÎãµÏùÑ ÏÉùÏÑ±ÌïòÎäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.',
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, errorResponse]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <Container>
      <Header>
        <BackButton onClick={onBack}>‚Üê Îí§Î°ú</BackButton>
        <HeaderTitle>{itemTitle}</HeaderTitle>
      </Header>
      
      <ChatContainer>
        <ChatMessages ref={messagesEndRef}>
          {messages.map((message, index) => 
            message.role === 'user' ? (
              <Message
                key={`detail_${index}`}
                message={{
                  ...message,
                  persona_id: undefined,
                  persona_name: undefined,
                }}
                personas={{}}
                showActionButtons={false}
              />
            ) : (
              <Message
                key={`detail_${index}`}
                message={{
                  ...message,
                  persona_id: 'current_persona',
                  persona_name: itemTitle,
                }}
                personas={{
                  current_persona: {
                    name: itemTitle,
                    description: '',
                    color: '#6c757d',
                    prompt: '',
                    category: '',
                    subcategory: ''
                  }
                }}
                showActionButtons={false}
              />
            )
          )}
          
          {isLoading && (
            <LoadingMessage 
              personaName={itemTitle}
              personaColor="#6c757d"
            />
          )}
        </ChatMessages>
        
        <InputArea>
          <MessageInput
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Í∂ÅÍ∏àÌïú Ï†êÏù¥ÎÇò Îçî ÏûêÏÑ∏Ìûà ÏïåÍ≥† Ïã∂ÏùÄ ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî..."
            disabled={isLoading}
          />
          <SendButton onClick={handleSendMessage} disabled={isLoading || !inputValue.trim()}>
            Ï†ÑÏÜ°
          </SendButton>
        </InputArea>
      </ChatContainer>
    </Container>
  );
};

export default DetailChatView;
