import React, { useState } from 'react';
import styled from 'styled-components';
import SentenceVaultView from './SentenceVaultView';

interface VaultViewProps {
  onNavigateToPersona?: (personaId: string, mode: 'sentence') => void;
  onNavigateToThread?: (threadId: string, threadType: string) => void;
}

const Container = styled.div`
  display: flex;
  height: 100vh;
`;

const SecondSidebar = styled.div`
  width: 200px;
  background: #f8f9fa;
  border-right: 1px solid #e9ecef;
  padding: 20px 0;
`;

const SidebarItem = styled.div<{ $isActive: boolean }>`
  padding: 12px 20px;
  cursor: pointer;
  color: ${props => props.$isActive ? '#007bff' : '#333'};
  background: ${props => props.$isActive ? '#e3f2fd' : 'transparent'};
  border-left: ${props => props.$isActive ? '3px solid #007bff' : '3px solid transparent'};

  &:hover {
    background: #f0f0f0;
  }
`;

const ContentArea = styled.div`
  flex: 1;
  background: #fff;
`;

const VaultView: React.FC<VaultViewProps> = ({
  onNavigateToPersona,
  onNavigateToThread
}) => {
  const [activeTab, setActiveTab] = useState<'sentences' | 'memos' | 'letters' | 'qa'>('sentences');

  const sidebarItems = [
    { key: 'sentences' as const, label: '문장' },
    { key: 'memos' as const, label: '메모' },
    { key: 'letters' as const, label: '편지' },
    { key: 'qa' as const, label: 'QA 업데이트' },
  ];

  const renderContent = () => {
    switch (activeTab) {
      case 'sentences':
        return (
          <SentenceVaultView
            onNavigateToPersona={onNavigateToPersona}
            onNavigateToThread={onNavigateToThread}
          />
        );
      case 'memos':
        return <div style={{ padding: '20px' }}>메모 기능은 준비 중입니다.</div>;
      case 'letters':
        return <div style={{ padding: '20px' }}>편지 기능은 준비 중입니다.</div>;
      case 'qa':
        return <div style={{ padding: '20px' }}>QA 업데이트 기능은 준비 중입니다.</div>;
      default:
        return null;
    }
  };

  return (
    <Container>
      <SecondSidebar>
        {sidebarItems.map(item => (
          <SidebarItem
            key={item.key}
            $isActive={activeTab === item.key}
            onClick={() => setActiveTab(item.key)}
          >
            {item.label}
          </SidebarItem>
        ))}
      </SecondSidebar>

      <ContentArea>
        {renderContent()}
      </ContentArea>
    </Container>
  );
};

export default VaultView;
