        } else {
          // ëª¨ë‘ í•˜ì´ë¼ì´íŠ¸ë˜ì§€ ì•Šì•˜ìœ¼ë©´ ëª¨ë‘ ì¶”ê°€
          setHighlightedSentences(prev => {
