import React from 'react';
import styled from 'styled-components';
import { TrainingThread, Persona } from '../types';

interface RecentInteractionsViewProps {
  personas: Record<string, Persona>;
  onSelectPersona: (personaId: string) => void;
  onThreadSelect: (thread: TrainingThread, personaId: string) => void;
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  background: white;
`;

const EmptyInteractionView = styled.div`
  flex: 1;
  background: white;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: #999;
  font-size: 16px;
`;

const RecentInteractionsView: React.FC<RecentInteractionsViewProps> = ({
  personas,
  onSelectPersona,
  onThreadSelect,
}) => {
  // 상호작용 기록이 선택되었을 때는 빈 화면만 표시
  return (
    <Container>
      <EmptyInteractionView>
        <div style={{ fontSize: '24px', marginBottom: '16px' }}>📊</div>
        <div>상호작용 기록</div>
        <div style={{ fontSize: '14px', marginTop: '8px', color: '#666' }}>
          좌측 사이드바에서 상호작용 기록을 탐색할 수 있습니다
        </div>
      </EmptyInteractionView>
    </Container>
  );
};

export default RecentInteractionsView;
