import React from 'react';
import styled from 'styled-components';
// import { FaAngleLeft } from 'react-icons/fa'; // 삭제

const SidebarContainer = styled.div<{ width: string; $isCollapsed: boolean; }>`
  width: ${props => props.$isCollapsed ? '0' : props.width};
  /* 기타 스타일 */
  background-color: #f8f9fa;
  border-right: 1px solid #dee2e6;
  transition: width 0.3s ease;
  position: relative;
  overflow: hidden;
`;

const ToggleButton = styled.button`
  /* 스타일 */
  position: absolute;
  right: 10px;
  top: 10px;
`;

const MenuContent = styled.div<{ $isCollapsed: boolean }>`
  opacity: ${props => props.$isCollapsed ? '0' : '1'};
  transition: opacity 0.3s ease;
`;

const VaultMenuItem = styled.div<{ $isSelected: boolean }>`
  padding: 15px 20px;
  cursor: pointer;
  font-weight: 500;
  border-left: 3px solid transparent;
  background-color: ${props => props.$isSelected ? '#e9ecef' : 'transparent'};
  border-left-color: ${props => props.$isSelected ? '#343a40' : 'transparent'};

  &:hover {
    background-color: #f1f3f5;
  }
`;

interface VaultSidebarProps {
  selectedItem: string;
  onSelectItem: (itemId: string) => void;
  isCollapsed: boolean;
  onToggle: () => void;
}

const AngleLeftIcon = () => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M15.41 7.41L14 6l-6 6 6 6 1.41-1.41L10.83 12z" fill="currentColor"/>
  </svg>
);

const VaultSidebar: React.FC<VaultSidebarProps> = ({ selectedItem, onSelectItem, isCollapsed, onToggle }) => {
  const menuItems = [
    { id: 'sentence', name: '문장' },
    { id: 'memo', name: '메모' },
    { id: 'letter', name: '편지' },
  ];

  return (
    <SidebarContainer width="240px" $isCollapsed={isCollapsed}>
      <ToggleButton onClick={onToggle}>
        <AngleLeftIcon />
      </ToggleButton>
      <MenuContent $isCollapsed={isCollapsed}>
        {menuItems.map(item => (
          <VaultMenuItem
            key={item.id}
            $isSelected={selectedItem === item.id}
            onClick={() => onSelectItem(item.id)}
          >
            {item.name}
          </VaultMenuItem>
        ))}
      </MenuContent>
    </SidebarContainer>
  );
};

export default VaultSidebar;
