import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

// 타입 정의
interface StimulusQuote {
  id: string;
  content: string;
  order: number;
}

interface StimulusQuoteSet {
  quotes: StimulusQuote[];
}

interface PersonaInfo {
  name: string;
  theme: string;
  summary: string;
}

interface DailyStimulusItem {
  id: string;
  date: string;
  persona_name: string;
  main_theme: string;
  quote_sets: StimulusQuoteSet[];
  summary_quote: string;
  persona_info?: PersonaInfo[];
  created_at: string;
}

interface StimulusVaultItem {
  id: string;
  original_stimulus_id: string;
  saved_content: string;
  content_type: 'quote' | 'summary' | 'theme';
  source_date: string;
  persona_name: string;
  saved_at: string;
  tags: string[];
  original_stimulus?: DailyStimulusItem;
}

interface StimulusVaultViewProps {
  personas?: Record<string, any>;
}

// 스타일 컴포넌트들
const Container = styled.div`
  padding: 24px;
  height: 100%;
  overflow-y: auto;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 24px;
  padding-bottom: 16px;
  border-bottom: 1px solid #e0e0e0;
`;

const Title = styled.h2`
  font-size: 24px;
  font-weight: 600;
  color: #333;
  margin: 0;
`;

const Stats = styled.div`
  font-size: 14px;
  color: #666;
`;

const ItemsList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const StimulusItem = styled.div`
  background: white;
  border: 1px solid #e0e0e0;
  border-radius: 12px;
  padding: 20px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
  transition: all 0.2s ease;
  
  &:hover {
    box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
    transform: translateY(-1px);
  }
`;

const ItemHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 12px;
`;

const ItemMeta = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4px;
`;

const PersonaName = styled.span`
  font-weight: 600;
  color: #333;
  font-size: 16px;
`;

const SourceDate = styled.span`
  font-size: 14px;
  color: #666;
`;

const ContentType = styled.span<{ $type: string }>`
  font-size: 12px;
  padding: 4px 8px;
  border-radius: 12px;
  font-weight: 500;
  background: ${props => {
    switch (props.$type) {
      case 'quote': return '#e3f2fd';
      case 'summary': return '#f3e5f5';
      case 'theme': return '#e8f5e8';
      default: return '#f5f5f5';
    }
  }};
  color: ${props => {
    switch (props.$type) {
      case 'quote': return '#1976d2';
      case 'summary': return '#7b1fa2';
      case 'theme': return '#388e3c';
      default: return '#666';
    }
  }};
`;

const Content = styled.div`
  font-size: 16px;
  line-height: 1.6;
  color: #333;
  white-space: pre-wrap;
`;

const Actions = styled.div`
  display: flex;
  gap: 8px;
  margin-top: 16px;
`;

const ActionButton = styled.button<{ $variant?: 'primary' | 'danger' }>`
  padding: 6px 12px;
  border: none;
  border-radius: 6px;
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s ease;
  
  ${props => props.$variant === 'danger' ? `
    background: #ffebee;
    color: #d32f2f;
    
    &:hover {
      background: #ffcdd2;
    }
  ` : `
    background: #f5f5f5;
    color: #666;
    
    &:hover {
      background: #e0e0e0;
    }
  `}
`;

const EmptyState = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 300px;
  color: #666;
  text-align: center;
`;

const EmptyIcon = styled.div`
  font-size: 48px;
  margin-bottom: 16px;
`;

const LoadingState = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 300px;
  font-size: 16px;
  color: #666;
`;

// 메인 컴포넌트
const StimulusVaultView: React.FC<StimulusVaultViewProps> = ({ personas }) => {
  const [items, setItems] = useState<StimulusVaultItem[]>([]);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // 데이터 로드
  useEffect(() => {
    loadStimulusVault();
  }, []);

  const loadStimulusVault = async () => {
    try {
      setIsLoading(true);
      setError(null);
      
      const response = await fetch('/vault/stimulus');
      const data = await response.json();
      
      if (data.success) {
        setItems(data.items || []);
      } else {
        setError('데이터를 불러오는데 실패했습니다.');
      }
    } catch (err) {
      setError('네트워크 오류가 발생했습니다.');
      console.error('자극 저장고 로드 오류:', err);
    } finally {
      setIsLoading(false);
    }
  };

  // 아이템 삭제
  const handleDelete = async (itemId: string) => {
    if (!window.confirm('정말 삭제하시겠습니까?')) return;
    
    try {
      const response = await fetch(`/vault/stimulus/${itemId}`, {
        method: 'DELETE'
      });
      
      const data = await response.json();
      if (data.success) {
        setItems(items.filter(item => item.id !== itemId));
      } else {
        alert('삭제에 실패했습니다.');
      }
    } catch (err) {
      console.error('삭제 오류:', err);
      alert('삭제에 실패했습니다.');
    }
  };

  // 타입 라벨 변환
  const getTypeLabel = (type: string) => {
    switch (type) {
      case 'quote': return '격언';
      case 'summary': return '요약';
      case 'theme': return '주제';
      default: return type;
    }
  };

  // 렌더링
  const renderContent = () => {
    if (isLoading) {
      return <LoadingState>자극 저장고를 불러오는 중...</LoadingState>;
    }
    
    if (error) {
      return (
        <EmptyState>
          <EmptyIcon>⚠️</EmptyIcon>
          <div>{error}</div>
          <ActionButton onClick={loadStimulusVault} style={{ marginTop: '16px' }}>
            다시 시도
          </ActionButton>
        </EmptyState>
      );
    }
    
    if (items.length === 0) {
      return (
        <EmptyState>
          <EmptyIcon>⚡</EmptyIcon>
          <div>저장된 자극이 없습니다</div>
          <div style={{ fontSize: '14px', marginTop: '8px', opacity: 0.7 }}>
            자극 페이지에서 격언을 저장해보세요
          </div>
        </EmptyState>
      );
    }

    return (
      <ItemsList>
        {items.map((item) => (
          <StimulusItem key={item.id}>
            <ItemHeader>
              <ItemMeta>
                <PersonaName>{item.persona_name}</PersonaName>
                <SourceDate>{item.source_date} 저장</SourceDate>
              </ItemMeta>
              <ContentType $type={item.content_type}>
                {getTypeLabel(item.content_type)}
              </ContentType>
            </ItemHeader>
            
            <Content>{item.saved_content}</Content>
            
            <Actions>
              <ActionButton onClick={() => navigator.clipboard.writeText(item.saved_content)}>
                복사
              </ActionButton>
              <ActionButton $variant="danger" onClick={() => handleDelete(item.id)}>
                삭제
              </ActionButton>
            </Actions>
          </StimulusItem>
        ))}
      </ItemsList>
    );
  };

  return (
    <Container>
      <Header>
        <Title>자극 저장고</Title>
        <Stats>총 {items.length}개 항목</Stats>
      </Header>
      
      {renderContent()}
    </Container>
  );
};

export default StimulusVaultView;
