import React from 'react';
import styled from 'styled-components';

interface SelectableSentenceProps {
  sentence: string;
  sentenceId: string;
  isSelected: boolean;
  isHighlighted: boolean;
  hasMemo: boolean;
  memo?: string;
  showSelector?: boolean;
  onToggleSelect: (sentenceId: string) => void;
  onMemoChange?: (sentenceId: string, memo: string) => void;
  onDeleteMemo?: (sentenceId: string) => void;
}

const SentenceContainer = styled.div<{ hasSelector: boolean }>`
  position: relative;
  display: flex;
  align-items: flex-start;
  gap: ${props => props.hasSelector ? '8px' : '0'};
  margin: 4px 0;
`;

const SelectButton = styled.button<{ isSelected: boolean }>`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  border: 2px solid #ccc;
  background: ${props => props.isSelected ? '#333' : 'white'};
  cursor: pointer;
  transition: all 0.2s ease;
  flex-shrink: 0;
  margin-top: 2px;

  &:hover {
    border-color: #666;
  }

  &:before {
    content: '';
    display: block;
    width: 100%;
    height: 100%;
    border-radius: 50%;
  }
`;

const SentenceText = styled.span<{ isHighlighted: boolean }>`
  background: ${props => props.isHighlighted ? '#ffff00' : 'transparent'};
  padding: ${props => props.isHighlighted ? '2px 4px' : '0'};
  border-radius: 4px;
  transition: background-color 0.2s ease;
  line-height: 1.6;
`;

const MemoBox = styled.div`
  position: absolute;
  left: calc(100% + 20px);
  top: 0;
  width: 180px;
  background: white;
  border: 2px solid #ddd;
  border-radius: 8px;
  padding: 8px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  z-index: 100;

  /* ÌôîÎ©¥ Ïò§Î•∏Ï™ΩÏùÑ Î≤óÏñ¥ÎÇòÏßÄ ÏïäÎèÑÎ°ù Ï°∞Ï†ï */
  @media (max-width: 768px) {
    left: auto;
    right: -10px;
    width: 150px;
  }
`;

const MemoTextarea = styled.textarea`
  width: 100%;
  height: 60px;
  border: none;
  outline: none;
  resize: none;
  font-size: 12px;
  line-height: 1.4;
  padding: 4px;
`;

const DeleteMemoButton = styled.button`
  position: absolute;
  top: 4px;
  right: 4px;
  width: 20px;
  height: 20px;
  border: none;
  background: none;
  cursor: pointer;
  font-size: 14px;
  color: #999;

  &:hover {
    color: #f00;
  }
`;

const SelectableSentence: React.FC<SelectableSentenceProps> = ({
  sentence,
  sentenceId,
  isSelected,
  isHighlighted,
  hasMemo,
  memo = '',
  showSelector = true,
  onToggleSelect,
  onMemoChange,
  onDeleteMemo
}) => {
  const handleMemoChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    if (onMemoChange) {
      onMemoChange(sentenceId, event.target.value);
    }
  };

  const handleDeleteMemo = () => {
    if (onDeleteMemo) {
      onDeleteMemo(sentenceId);
    }
  };

  return (
    <SentenceContainer hasSelector={showSelector}>
      {showSelector && (
        <SelectButton 
          isSelected={isSelected}
          onClick={() => onToggleSelect(sentenceId)}
        />
      )}
      <SentenceText isHighlighted={isHighlighted}>
        {sentence}
      </SentenceText>
      {hasMemo && (
        <MemoBox>
          <DeleteMemoButton onClick={handleDeleteMemo}>
            üóë
          </DeleteMemoButton>
          <MemoTextarea
            value={memo || ''}
            onChange={handleMemoChange}
            placeholder="Î©îÎ™®Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
            autoFocus
          />
        </MemoBox>
      )}
    </SentenceContainer>
  );
};

export default SelectableSentence;
