import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import SentenceFilterDropdown from './SentenceFilterDropdown';
import SentenceCard from './SentenceCard';
import ConfirmationModal from './ConfirmationModal';
import { sentenceApi, SentenceVaultItem } from '../../training/api/sentenceApi';

interface SentenceVaultViewProps {
  onNavigateToPersona: (personaId: string, mode: 'sentence') => void;
  onNavigateToThread: (threadId: string, threadType: string) => void;
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
`;

const FilterContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
  margin-bottom: 24px;
`;

const ArrowButton = styled.button`
  background: #e9ecef;
  border: 1px solid #dee2e6;
  border-radius: 6px;
  width: 32px;
  height: 32px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 14px;
  
  &:hover {
    background: #dee2e6;
  }
  
  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const SentenceGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 16px;
  flex: 1;
`;

const EmptyState = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 300px;
  color: #6c757d;
  font-size: 16px;
`;

const LoadingState = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 300px;
  color: #6c757d;
  font-size: 16px;
`;

interface SentenceWithMeta extends SentenceVaultItem {
  persona_name?: string;
  thread_type?: string;
  is_pinned?: boolean;
}

const SentenceVaultView: React.FC<SentenceVaultViewProps> = ({
  onNavigateToPersona,
  onNavigateToThread
}) => {
  const [sentences, setSentences] = useState<SentenceWithMeta[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentFilter, setCurrentFilter] = useState('Ï†ÑÏ≤¥');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [confirmModal, setConfirmModal] = useState<{
    isOpen: boolean;
    type: 'interact' | 'navigate' | 'pin' | 'delete';
    sentence: SentenceWithMeta | null;
  }>({
    isOpen: false,
    type: 'interact',
    sentence: null
  });

  // ÎçîÎØ∏ ÌéòÎ•¥ÏÜåÎÇò Îç∞Ïù¥ÌÑ∞ (Ïã§Ï†úÎ°úÎäî Î∞±ÏóîÎìúÏóêÏÑú Í∞ÄÏ†∏ÏôÄÏïº Ìï®)
  const personas = {
    'persona1': 'ÍπÄÏ≤†Ïàò',
    'persona2': 'Ïù¥ÏòÅÌù¨',
    'persona3': 'Î∞ïÎØºÏàò',
    'verbalization': 'Ïñ∏Ïñ¥Ìôî'
  };

  // ÌïÑÌÑ∞ ÏòµÏÖò ÏÉùÏÑ±
  const getFilterOptions = () => {
    const baseOptions = ['Ï†ÑÏ≤¥', 'Ïñ∏Ïñ¥Ìôî', 'Ìé∏ÏßÄ'];
    const personaOptions = Object.values(personas).filter(name => name !== 'Ïñ∏Ïñ¥Ìôî');
    return [...baseOptions, ...personaOptions];
  };

  const filterOptions = getFilterOptions();
  const currentFilterIndex = filterOptions.indexOf(currentFilter);

  // Îç∞Ïù¥ÌÑ∞ Î°úÎî©
  useEffect(() => {
    loadSentences();
  }, []);

  const loadSentences = async () => {
    try {
      setLoading(true);
      const data = await sentenceApi.getVaultSentences();
      
      // Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ Ï∂îÍ∞Ä (Ïã§Ï†úÎ°úÎäî Î∞±ÏóîÎìúÏóêÏÑú Ï†úÍ≥µÎêòÏñ¥Ïïº Ìï®)
      const sentencesWithMeta: SentenceWithMeta[] = data.map(sentence => ({
        ...sentence,
        persona_name: getPersonaNameFromMetadata(sentence),
        thread_type: getThreadTypeFromMetadata(sentence),
        is_pinned: sentence.metadata?.is_pinned || false
      }));
      
      // Í≥†Ï†ïÎêú Ìï≠Î™©ÏùÑ Îß® ÏúÑÎ°ú, Í∑∏ Îã§ÏùåÏùÄ ÏµúÏã†ÏàúÏúºÎ°ú Ï†ïÎ†¨
      const sortedSentences = sentencesWithMeta.sort((a, b) => {
        if (a.is_pinned && !b.is_pinned) return -1;
        if (!a.is_pinned && b.is_pinned) return 1;
        return new Date(b.created_at).getTime() - new Date(a.created_at).getTime();
      });
      
      setSentences(sortedSentences);
    } catch (error) {
      console.error('Î¨∏Ïû• Î°úÎî© Ïò§Î•ò:', error);
    } finally {
      setLoading(false);
    }
  };

  // Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ÏóêÏÑú ÌéòÎ•¥ÏÜåÎÇòÎ™Ö Ï∂îÏ∂ú
  const getPersonaNameFromMetadata = (sentence: SentenceVaultItem): string => {
    // metadataÏóêÏÑú persona_id ÎòêÎäî thread_typeÏùÑ ÌôïÏù∏
    const personaId = sentence.metadata?.persona_id;
    const threadType = sentence.metadata?.thread_type;
    
    if (threadType === 'verbalization') {
      return 'Ïñ∏Ïñ¥Ìôî';
    }
    
    if (personaId && personas[personaId as keyof typeof personas]) {
      return personas[personaId as keyof typeof personas];
    }
    
    // Í∏∞Î≥∏Í∞í
    return 'Ïïå Ïàò ÏóÜÏùå';
  };

  // Î©îÌÉÄÎç∞Ïù¥ÌÑ∞ÏóêÏÑú Ïä§Î†àÎìú ÌÉÄÏûÖ Ï∂îÏ∂ú
  const getThreadTypeFromMetadata = (sentence: SentenceVaultItem): string => {
    return sentence.metadata?.thread_type || 'interpretation';
  };

  // ÌïÑÌÑ∞ÎßÅÎêú Î¨∏Ïû•Îì§
  const getFilteredSentences = () => {
    if (currentFilter === 'Ï†ÑÏ≤¥') {
      return sentences;
    }
    
    if (currentFilter === 'Ïñ∏Ïñ¥Ìôî') {
      return sentences.filter(s => s.persona_name === 'Ïñ∏Ïñ¥Ìôî');
    }
    
    if (currentFilter === 'Ìé∏ÏßÄ') {
      return sentences.filter(s => s.thread_type === 'letter');
    }
    
    // ÌäπÏ†ï ÌéòÎ•¥ÏÜåÎÇò ÌïÑÌÑ∞
    return sentences.filter(s => s.persona_name === currentFilter);
  };

  const filteredSentences = getFilteredSentences();

  // ÌïÑÌÑ∞ Ïù¥Îèô
  const moveFilter = (direction: 'left' | 'right') => {
    const newIndex = direction === 'left' 
      ? Math.max(0, currentFilterIndex - 1)
      : Math.min(filterOptions.length - 1, currentFilterIndex + 1);
    setCurrentFilter(filterOptions[newIndex]);
  };

  // Ïπ¥Îìú Ïï°ÏÖò Ï≤òÎ¶¨
  const handleCardAction = (sentence: SentenceWithMeta, action: 'interact' | 'navigate' | 'pin' | 'delete') => {
    setConfirmModal({
      isOpen: true,
      type: action,
      sentence
    });
  };

  // Î™®Îã¨ ÌôïÏù∏ Ï≤òÎ¶¨
  const handleConfirmAction = async () => {
    const { type, sentence } = confirmModal;
    if (!sentence) return;

    try {
      switch (type) {
        case 'interact':
          // ÏÉÅÌò∏ÏûëÏö© ÌïòÍ∏∞ - Ìï¥Îãπ ÌéòÎ•¥ÏÜåÎÇòÏùò Î¨∏Ïû• Î™®ÎìúÎ°ú Ïù¥Îèô
          if (sentence.metadata?.persona_id) {
            onNavigateToPersona(sentence.metadata.persona_id, 'sentence');
          }
          break;
          
        case 'navigate':
          // Ìï¥Îãπ Í∏∞Î°ùÏúºÎ°ú Ïù¥Îèô - Ìï¥Îãπ Ïä§Î†àÎìúÎ°ú Ïù¥Îèô
          if (sentence.metadata?.thread_id && sentence.thread_type) {
            onNavigateToThread(sentence.metadata.thread_id, sentence.thread_type);
          }
          break;
          
        case 'pin':
          // Í≥†Ï†ï/Ìï¥Ï†ú
          const updatedSentences = sentences.map(s => 
            s.id === sentence.id 
              ? { ...s, is_pinned: !s.is_pinned }
              : s
          );
          setSentences(updatedSentences);
          // TODO: Î∞±ÏóîÎìúÏóê Í≥†Ï†ï ÏÉÅÌÉú Ï†ÄÏû•
          break;
          
        case 'delete':
          // ÏÇ≠Ï†ú
          await sentenceApi.deleteVaultSentence(sentence.id);
          setSentences(sentences.filter(s => s.id !== sentence.id));
          break;
      }
    } catch (error) {
      console.error('Ïï°ÏÖò Ï≤òÎ¶¨ Ïò§Î•ò:', error);
    }

    setConfirmModal({ isOpen: false, type: 'interact', sentence: null });
  };

  const getConfirmMessage = () => {
    const { type, sentence } = confirmModal;
    if (!sentence) return '';

    switch (type) {
      case 'interact':
        return `"${sentence.persona_name}"ÏôÄ ÏÉÅÌò∏ÏûëÏö©ÏùÑ ÏãúÏûëÌïòÏãúÍ≤†ÏäµÎãàÍπå?`;
      case 'navigate':
        return `Ìï¥Îãπ Í∏∞Î°ùÏúºÎ°ú Ïù¥ÎèôÌïòÏãúÍ≤†ÏäµÎãàÍπå?`;
      case 'pin':
        return sentence.is_pinned 
          ? `Ïù¥ Î¨∏Ïû•Ïùò Í≥†Ï†ïÏùÑ Ìï¥Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?`
          : `Ïù¥ Î¨∏Ïû•ÏùÑ Îß® ÏúÑÏóê Í≥†Ï†ïÌïòÏãúÍ≤†ÏäµÎãàÍπå?`;
      case 'delete':
        return `Ïù¥ Î¨∏Ïû•ÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå? Ïù¥ ÏûëÏóÖÏùÄ ÎêòÎèåÎ¶¥ Ïàò ÏóÜÏäµÎãàÎã§.`;
      default:
        return '';
    }
  };

  if (loading) {
    return (
      <Container>
        <LoadingState>
          Î¨∏Ïû•ÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...
        </LoadingState>
      </Container>
    );
  }

  return (
    <Container>
      <FilterContainer>
        <ArrowButton
          onClick={() => moveFilter('left')}
          disabled={currentFilterIndex === 0}
        >
          ‚Üê
        </ArrowButton>
        
        <SentenceFilterDropdown
          currentFilter={currentFilter}
          options={filterOptions}
          isOpen={isDropdownOpen}
          onToggle={() => setIsDropdownOpen(!isDropdownOpen)}
                     onSelect={(option: string) => {
             setCurrentFilter(option);
             setIsDropdownOpen(false);
           }}
        />
        
        <ArrowButton
          onClick={() => moveFilter('right')}
          disabled={currentFilterIndex === filterOptions.length - 1}
        >
          ‚Üí
        </ArrowButton>
      </FilterContainer>

      {filteredSentences.length === 0 ? (
        <EmptyState>
          <div>üìù</div>
          <div>Ï†ÄÏû•Îêú Î¨∏Ïû•Ïù¥ ÏóÜÏäµÎãàÎã§</div>
          <div style={{ fontSize: '14px', marginTop: '8px' }}>
            ÎåÄÌôî Ï§ëÏóê Î¨∏Ïû•ÏùÑ ÏÑ†ÌÉùÌïòÍ≥† "Ï†ÄÏû•Í≥†Ïóê Ï†ÄÏû•" Î≤ÑÌäºÏùÑ ÎàåÎü¨Î≥¥ÏÑ∏Ïöî
          </div>
        </EmptyState>
      ) : (
        <SentenceGrid>
          {filteredSentences.map(sentence => (
            <SentenceCard
              key={sentence.id}
              sentence={sentence}
              personaName={sentence.persona_name || 'Ïïå Ïàò ÏóÜÏùå'}
              isPinned={sentence.is_pinned || false}
              onAction={(action) => handleCardAction(sentence, action)}
            />
          ))}
        </SentenceGrid>
      )}

      <ConfirmationModal
        isOpen={confirmModal.isOpen}
        title={confirmModal.type === 'delete' ? 'Î¨∏Ïû• ÏÇ≠Ï†ú' : 'ÌôïÏù∏'}
        message={getConfirmMessage()}
        onConfirm={handleConfirmAction}
        onCancel={() => setConfirmModal({ isOpen: false, type: 'interact', sentence: null })}
        confirmText={confirmModal.type === 'delete' ? 'ÏÇ≠Ï†ú' : 'ÌôïÏù∏'}
        cancelText="Ï∑®ÏÜå"
        isDangerous={confirmModal.type === 'delete'}
      />
    </Container>
  );
};

export default SentenceVaultView;
