import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import styled, { keyframes, css } from 'styled-components';
import { qaApi, QAQuestion, QASubmission } from '../api/qaApi';
import { useWhisperStream, RecordingStatus } from '../hooks/useWhisperStream';
import { FaMicrophone, FaStopCircle } from 'react-icons/fa';

// Animations
const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const pulsate = keyframes`
  0% { transform: scale(1); opacity: 0.7; }
  50% { transform: scale(1.2); opacity: 1; }
  100% { transform: scale(1); opacity: 0.7; }
`;

// Styled Components
const PageContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  width: 100vw;
  background-color: #f0f2f5;
  padding: 20px;
  animation: ${fadeIn} 0.8s ease-out;
`;

const QAContainer = styled.div`
  width: 100%;
  max-width: 700px;
  height: 100%;
  max-height: 800px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const QuestionBox = styled.div`
  padding: 40px;
  border-radius: 20px;
  background: linear-gradient(to bottom, #ffffff, #e9ecef);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  text-align: center;
  margin-bottom: 20px;
  animation: ${fadeIn} 0.5s ease-out forwards;

  h2 {
    font-size: 1.5rem;
    font-weight: 600;
    color: #343a40;
    line-height: 1.6;
  }
`;

const InputSection = styled.div`
  width: 100%;
`;

const InputBox = styled.div`
  position: relative;
  padding: 20px;
  border-radius: 20px;
  background: linear-gradient(to bottom, #ffffff, #e9ecef);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
`;

const StyledTextarea = styled.textarea`
  width: 100%;
  height: 150px;
  border: none;
  background-color: transparent;
  resize: none;
  font-size: 1rem;
  font-family: inherit;
  color: #495057;
  padding-right: 50px; // for microphone button

  &:focus {
    outline: none;
  }
`;

const MicButton = styled.button<{ $status: RecordingStatus }>`
  position: absolute;
  right: 20px;
  bottom: 65px;
  background: none;
  border: none;
  cursor: pointer;
  font-size: 1.5rem;
  color: #868e96;
  transition: color 0.2s;

  &:hover {
    color: #495057;
  }

  ${({ $status }) => $status === 'recording' && css`
    background-color: #e03131;
    color: white;
    box-shadow: 0 0 12px 2px rgba(224, 49, 49, 0.5);
    &::after {
      content: '';
      position: absolute;
      top: -4px; left: -4px; right: -4px; bottom: -4px;
      border-radius: 50%;
      border: 2px solid #e03131;
      animation: ${pulsate} 1.5s infinite;
    }
  `}
`;

const BottomBar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 15px;
  padding: 0 10px;
`;

const CharCounter = styled.span<{ hasError: boolean }>`
  font-size: 0.9rem;
  color: ${props => props.hasError ? '#e03131' : '#868e96'};
`;

const SubmitButton = styled.button`
  padding: 10px 20px;
  border: 1px solid #343a40;
  border-radius: 50px;
  background-color: #fff;
  color: #343a40;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;

  &:hover:not(:disabled) {
    background-color: #343a40;
    color: #fff;
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const ProgressTracker = styled.div`
  margin-top: 20px;
  font-size: 1rem;
  font-weight: 500;
  color: #495057;
`;

const CompletionOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  z-index: 1000;

  h2 {
    font-size: 2rem;
    font-weight: 600;
    color: #fff;
    margin-bottom: 10px;
  }

  p {
    font-size: 1.2rem;
    font-weight: 500;
    color: #fff;
  }
`;

const MicIcon = () => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
    <path d="M12 1a3 3 0 0 0-3 3v8a3 3 0 0 0 6 0V4a3 3 0 0 0-3-3z"/>
    <path d="M19 12v1a7 7 0 0 1-14 0v-1h2v1a5 5 0 0 0 10 0v-1h2z"/>
    <path d="M5 12H3a7 7 0 0 0 6 6.92V22h2v-3.08A7 7 0 0 0 21 12h-2a5 5 0 0 1-10 0H5z"/>
  </svg>
);

const StopIcon = () => (
    <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
        <rect x="6" y="6" width="12" height="12" rx="2"/>
    </svg>
);

const QAPage: React.FC = () => {
  const [questions, setQuestions] = useState<QAQuestion[]>([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [currentAnswer, setCurrentAnswer] = useState('');
  const [allAnswers, setAllAnswers] = useState<QASubmission[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isCompleting, setIsCompleting] = useState(false);
  const navigate = useNavigate();

  const handleTranscriptUpdate = useCallback((text: string) => {
    setCurrentAnswer(text);
  }, []);

  const { status, toggleRecording } = useWhisperStream(handleTranscriptUpdate);

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const data = await qaApi.getQuestions();
        setQuestions(data);
        } catch (error) {
        console.error("질문을 가져오는 데 실패했습니다.", error);
      } finally {
        setIsLoading(false);
      }
    };
    fetchQuestions();
  }, []);

  const handleNextQuestion = async () => {
    const newAnswer: QASubmission = {
      question_id: questions[currentQuestionIndex].question_id,
      answer: currentAnswer
    };
    const updatedAnswers = [...allAnswers, newAnswer];
    setAllAnswers(updatedAnswers);
    setCurrentAnswer('');

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setIsCompleting(true);
      try {
        await qaApi.submitAnswers(updatedAnswers);
        setTimeout(() => navigate('/'), 2000);
      } catch (error) {
        console.error("답변 제출에 실패했습니다.", error);
        setIsCompleting(false);
      }
    }
  };

  const getMicIcon = () => {
    switch (status) {
      case 'recording':
        return <StopIcon />;
      case 'connecting':
      case 'stopping':
        return <div className="spinner" />;
      default:
        return <MicIcon />;
    }
  };

  const MIN_CHARS = 100;
  const isSubmitDisabled = currentAnswer.length < MIN_CHARS;

  if (isLoading) {
    return <PageContainer>질문을 불러오는 중입니다...</PageContainer>;
  }

  if (questions.length === 0) {
    return <PageContainer>표시할 질문이 없습니다.</PageContainer>;
  }

  if (isCompleting) {
    return (
      <CompletionOverlay>
        <h2>모든 답변이 완료되었습니다.</h2>
        <p>잠시 후 메인 페이지로 이동합니다.</p>
      </CompletionOverlay>
    );
  }

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <PageContainer>
      <QAContainer>
        <QuestionBox key={currentQuestion.question_id}>
          <h2>{currentQuestion.question_text}</h2>
        </QuestionBox>

        <InputSection>
          <InputBox>
            <StyledTextarea
              placeholder="답변을 입력해주세요..."
              value={currentAnswer}
              onChange={(e) => setCurrentAnswer(e.target.value)}
              maxLength={500}
            />
            <MicButton onClick={toggleRecording} $status={status}>
              {getMicIcon()}
            </MicButton>
          </InputBox>
          <BottomBar>
            <CharCounter hasError={currentAnswer.length < MIN_CHARS}>
              {currentAnswer.length} / {MIN_CHARS}자 이상
            </CharCounter>
            <SubmitButton onClick={handleNextQuestion} disabled={isSubmitDisabled}>
              {currentQuestionIndex === questions.length - 1 ? '모든 답변 완료' : '답변 완료'}
            </SubmitButton>
          </BottomBar>
        </InputSection>
      </QAContainer>
      <ProgressTracker>
        {currentQuestionIndex + 1} / {questions.length}
      </ProgressTracker>
    </PageContainer>
  );
};

export default QAPage;
