import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled, { keyframes, css } from 'styled-components';
import { qaApi, QAQuestion, QASubmission } from '../api/qaApi';
import { useVoiceToText, RecordingStatus } from '../hooks/useVoiceToText';

// Animations
const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.2); }
  100% { transform: scale(1); }
`;

// Styled Components
const PageContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  width: 100vw;
  background-color: #f0f2f5;
  padding: 20px;
  animation: ${fadeIn} 0.8s ease-out;
`;

const QAContainer = styled.div`
  width: 100%;
  max-width: 700px;
  height: 100%;
  max-height: 800px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const QuestionBox = styled.div`
  padding: 40px;
  border-radius: 20px;
  background: linear-gradient(to bottom, #ffffff, #e9ecef);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
  text-align: center;
  margin-bottom: 20px;
  animation: ${fadeIn} 0.5s ease-out forwards;

  h2 {
    font-size: 1.5rem;
    font-weight: 600;
    color: #343a40;
    line-height: 1.6;
  }
`;

const InputSection = styled.div`
  width: 100%;
`;

const InputBox = styled.div`
  position: relative;
  padding: 20px;
  border-radius: 20px;
  background: linear-gradient(to bottom, #ffffff, #e9ecef);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
`;

const StyledTextarea = styled.textarea`
  width: 100%;
  height: 150px;
  border: none;
  background-color: transparent;
  resize: none;
  font-size: 1rem;
  font-family: inherit;
  color: #495057;
  padding-right: 50px; // for microphone button

  &:focus {
    outline: none;
  }
`;

const MicButton = styled.button<{ status: RecordingStatus }>`
  position: absolute;
  right: 20px;
  bottom: 65px;
  background: none;
  border: none;
  cursor: pointer;
  font-size: 1.5rem;
  color: #868e96;
  transition: color 0.2s;

  &:hover {
    color: #495057;
  }

  // 녹음 상태에 따른 동적 스타일
  ${({ status }) => status === "recording" && css`
    color: #fff;
    background-color: #e03131; // 빨간색 배경
    animation: ${pulse} 1.5s infinite;
  `}
`;

const BottomBar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 15px;
  padding: 0 10px;
`;

const CharCounter = styled.span<{ hasError: boolean }>`
  font-size: 0.9rem;
  color: ${props => props.hasError ? '#e03131' : '#868e96'};
`;

const SubmitButton = styled.button`
  padding: 10px 20px;
  border: 1px solid #343a40;
  border-radius: 50px;
  background-color: #fff;
  color: #343a40;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;

  &:hover:not(:disabled) {
    background-color: #343a40;
    color: #fff;
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const ProgressTracker = styled.div`
  margin-top: 20px;
  font-size: 1rem;
  font-weight: 500;
  color: #495057;
`;

const CompletionOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  z-index: 1000;

  h1 {
    font-size: 2rem;
    font-weight: 600;
    color: #fff;
    margin-bottom: 10px;
  }

  p {
    font-size: 1rem;
    font-weight: 500;
    color: #fff;
  }
`;

const MicIcon = () => (
  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M12 1a3 3 0 0 0-3 3v8a3 3 0 0 0 6 0V4a3 3 0 0 0-3-3z" fill="currentColor"/>
    <path d="M19 12v1a7 7 0 0 1-14 0v-1h2v1a5 5 0 0 0 10 0v-1h2z" fill="currentColor"/>
    <path d="M5 12H3a7 7 0 0 0 6 6.92V22h2v-3.08A7 7 0 0 0 21 12h-2a5 5 0 0 1-10 0H5z" fill="currentColor"/>
  </svg>
);

const QAPage: React.FC = () => {
  const [questions, setQuestions] = useState<QAQuestion[]>([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [currentAnswer, setCurrentAnswer] = useState('');
  const [interimTranscript, setInterimTranscript] = useState('');
  const [allAnswers, setAllAnswers] = useState<QASubmission[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isComplete, setIsComplete] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const data = await qaApi.getQuestions();
        setQuestions(data);
      } catch (error) {
        console.error("질문을 가져오는 데 실패했습니다.", error);
      } finally {
        setIsLoading(false);
      }
    };
    fetchQuestions();
  }, []);

  const handleTranscript = (transcript: string, isFinal: boolean) => {
    if (isFinal) {
      setCurrentAnswer(prev => prev + transcript + ' ');
      setInterimTranscript('');
    } else {
      setInterimTranscript(transcript);
    }
  };

  const { status, toggleRecording } = useVoiceToText(handleTranscript);

  const handleNextQuestion = async () => {
    const newAnswer: QASubmission = {
      question_id: questions[currentQuestionIndex].question_id,
      answer: currentAnswer
    };
    const updatedAnswers = [...allAnswers, newAnswer];
    setAllAnswers(updatedAnswers);
    setCurrentAnswer('');

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      // 마지막 질문, 답변 제출
      try {
        await qaApi.submitAnswers(updatedAnswers);
        setIsComplete(true); // API 호출이 성공한 후에 완료 상태로 변경
        setTimeout(() => navigate('/'), 2000); // 2초 후 메인으로 이동
      } catch (error) {
        console.error("답변 제출에 실패했습니다.", error);
        // 사용자에게 에러 알림 (예: alert 또는 토스트 메시지)
        alert("답변 제출 중 오류가 발생했습니다. 다시 시도해주세요.");
        setIsComplete(false); // 오류 발생 시 완료 상태로 넘어가지 않음
      }
    }
  };

  const MIN_CHARS = 100;
  const isSubmitDisabled = currentAnswer.length < MIN_CHARS;

  if (isLoading) {
    return <PageContainer>질문을 불러오는 중입니다...</PageContainer>;
  }

  if (questions.length === 0) {
    return <PageContainer>표시할 질문이 없습니다.</PageContainer>;
  }

  const currentQuestion = questions[currentQuestionIndex];

  if (isComplete) {
    return (
      <CompletionOverlay>
        <h1>수고하셨습니다!</h1>
        <p>잠시 후 메인 페이지로 이동합니다.</p>
      </CompletionOverlay>
    );
  }

  return (
    <PageContainer>
      <QAContainer>
        <QuestionBox key={currentQuestion.question_id}>
          <h2>{currentQuestion.question_text}</h2>
        </QuestionBox>

        <InputSection>
          <InputBox>
            <StyledTextarea
              placeholder="답변을 입력해주세요..."
              value={currentAnswer + interimTranscript}
              onChange={(e) => setCurrentAnswer(e.target.value)}
              maxLength={500}
            />
            <MicButton status={status} onClick={toggleRecording} title="음성으로 입력하기">
              <MicIcon />
            </MicButton>
          </InputBox>
          <BottomBar>
            <CharCounter hasError={currentAnswer.length < MIN_CHARS}>
              {currentAnswer.length} / {MIN_CHARS}자 이상
            </CharCounter>
            <SubmitButton onClick={handleNextQuestion} disabled={isSubmitDisabled}>
              {currentQuestionIndex === questions.length - 1 ? '모든 답변 완료' : '답변 완료'}
            </SubmitButton>
          </BottomBar>
        </InputSection>
      </QAContainer>
      <ProgressTracker>
        {currentQuestionIndex + 1} / {questions.length}
      </ProgressTracker>
    </PageContainer>
  );
};

export default QAPage;
