import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { confusionApi, ConfusionItemDetail, ChatMessage as ApiChatMessage } from '../api/confusionApi';

interface DetailChatViewProps {
  personaId: string;
  itemId: string;
  itemTitle: string;
  itemType: 'theoretical' | 'insightful';
  onBack?: () => void;
}

interface ChatMessage {
  id: string;
  type: 'system' | 'user';
  content: string;
  timestamp: Date;
  category?: 'interpretation' | 'key_sentence' | 'proceed' | 'proceed_key_sentence';
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background: white;
`;

const Header = styled.div`
  padding: 16px 20px;
  background: #ffffff;
  color: #333;
  display: flex;
  align-items: center;
  gap: 12px;
  border-bottom: 1px solid #e9ecef;
`;

const BackButton = styled.button`
  background: #f8f9fa;
  border: 1px solid #ddd;
  color: #333;
  padding: 8px 12px;
  border-radius: 6px;
  cursor: pointer;
  font-size: 14px;
  transition: all 0.2s ease;

  &:hover {
    background: #e9ecef;
    border-color: #ccc;
  }
`;

const HeaderTitle = styled.h2`
  margin: 0;
  font-size: 18px;
  font-weight: 600;
  color: #333;
`;

const ChatContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
`;

const MessagesArea = styled.div`
  flex: 1;
  padding: 20px;
  overflow-y: auto;
  background: #ffffff;
`;

const MessageSection = styled.div`
  margin-bottom: 24px;
  padding: 16px;
  background: #f8f9fa;
  border-radius: 8px;
  border: 1px solid #e9ecef;
`;

const MessageBubble = styled.div<{ $isUser: boolean; $category?: string }>`
  margin-bottom: 16px;
  display: block;
  width: 100%;
`;

const MessageHeader = styled.div<{ $category?: string }>`
  font-size: 14px;
  color: #333;
  margin-bottom: 8px;
  font-weight: 600;
  border-bottom: 1px solid #ddd;
  padding-bottom: 8px;
`;

const MessageContent = styled.div<{ $isUser: boolean; $category?: string }>`
  background: ${props => props.$isUser ? '#f0f0f0' : 'transparent'};
  color: #333;
  padding: ${props => props.$isUser ? '12px 16px' : '0'};
  border-radius: ${props => props.$isUser ? '8px' : '0'};
  border: ${props => props.$isUser ? '1px solid #ddd' : 'none'};
  margin: ${props => props.$isUser ? '8px 0 8px auto' : '0'};
  max-width: ${props => props.$isUser ? '70%' : '100%'};
  line-height: 1.6;
  font-size: 14px;
  word-wrap: break-word;
  white-space: pre-wrap;
`;

const InputArea = styled.div`
  padding: 16px 20px;
  background: white;
  border-top: 1px solid #e9ecef;
  display: flex;
  gap: 12px;
  align-items: flex-end;
`;

const MessageInput = styled.textarea`
  flex: 1;
  border: 1px solid #ddd;
  border-radius: 6px;
  padding: 12px 16px;
  font-size: 14px;
  line-height: 1.4;
  resize: none;
  max-height: 100px;
  min-height: 44px;
  
  &:focus {
    outline: none;
    border-color: #6c757d;
    box-shadow: 0 0 0 2px rgba(108, 117, 125, 0.1);
  }
`;

const SendButton = styled.button`
  background: #6c757d;
  color: white;
  border: 1px solid #5a6268;
  border-radius: 6px;
  width: 80px;
  height: 44px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 14px;
  font-weight: 500;
  transition: all 0.2s ease;

  &:hover {
    background: #5a6268;
    border-color: #545b62;
  }

  &:disabled {
    background: #ccc;
    border-color: #bbb;
    cursor: not-allowed;
  }
`;

const CategoryLabel = {
  interpretation: '💭 해석',
  key_sentence: '⭐ 핵심 문장',
  proceed: '🚀 나아가기',
  proceed_key_sentence: '✨ 나아가기 핵심 문장'
};

const DetailChatView: React.FC<DetailChatViewProps> = ({
  personaId,
  itemId,
  itemTitle,
  itemType,
  onBack
}) => {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    // 초기 메시지 로드 (백엔드에서 받아온다고 가정)
    loadInitialMessages();
  }, [itemId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const loadInitialMessages = async () => {
    setIsLoading(true);
    
    try {
      // 백엔드에서 실제 데이터 조회
      const detail = await confusionApi.getItemDetail(personaId, itemId);
      
      const initialMessages: ChatMessage[] = [
        {
          id: '1',
          type: 'system',
          category: 'interpretation',
          content: `${itemTitle}에 대한 심층 분석을 시작하겠습니다.

${detail.interpretation}`,
          timestamp: new Date()
        },
        {
          id: '2',
          type: 'system',
          category: 'key_sentence',
          content: `💡 "${detail.key_sentence}"`,
          timestamp: new Date()
        },
        {
          id: '3',
          type: 'system',
          category: 'proceed',
          content: detail.proceed,
          timestamp: new Date()
        },
        {
          id: '4',
          type: 'system',
          category: 'proceed_key_sentence',
          content: `🌟 "${detail.proceed_key_sentence}"`,
          timestamp: new Date()
        }
      ];
      
      setMessages(initialMessages);
    } catch (error) {
      console.error('초기 메시지 로드 실패:', error);
      // 에러 발생 시 기본 메시지 표시
      const errorMessage: ChatMessage = {
        id: 'error',
        type: 'system',
        content: '죄송합니다. 데이터를 불러오는 중 오류가 발생했습니다. 잠시 후 다시 시도해주세요.',
        timestamp: new Date()
      };
      setMessages([errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage: ChatMessage = {
      id: `user_${Date.now()}`,
      type: 'user',
      content: inputValue.trim(),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);

    // 백엔드로 메시지 전송 및 응답 받기
    try {
      // 대화 히스토리를 API 형식으로 변환
      const conversationHistory: ApiChatMessage[] = messages.map(msg => ({
        role: msg.type === 'user' ? 'user' : 'assistant',
        content: msg.content
      }));

      const response = await confusionApi.chatWithItem(personaId, itemId, {
        user_message: userMessage.content,
        conversation_history: conversationHistory
      });

      const systemResponse: ChatMessage = {
        id: `system_${Date.now()}`,
        type: 'system',
        content: response.response,
        timestamp: new Date()
      };
      
      setMessages(prev => [...prev, systemResponse]);
    } catch (error) {
      console.error('메시지 전송 실패:', error);
      const errorResponse: ChatMessage = {
        id: `error_${Date.now()}`,
        type: 'system',
        content: '죄송합니다. 응답을 생성하는 중 오류가 발생했습니다. 잠시 후 다시 시도해주세요.',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorResponse]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <Container>
      <Header>
        <BackButton onClick={onBack}>← 뒤로</BackButton>
        <HeaderTitle>{itemTitle}</HeaderTitle>
      </Header>
      
      <ChatContainer>
        <MessagesArea>
          {messages.map((message) => (
            <MessageBubble key={message.id} $isUser={message.type === 'user'}>
              {message.category && (
                <MessageHeader $category={message.category}>
                  {CategoryLabel[message.category]}
                </MessageHeader>
              )}
              <MessageContent 
                $isUser={message.type === 'user'} 
                $category={message.category}
              >
                {message.content}
              </MessageContent>
            </MessageBubble>
          ))}
          {isLoading && (
            <MessageBubble $isUser={false}>
              <MessageContent $isUser={false}>
                <div style={{ display: 'flex', gap: '4px', alignItems: 'center' }}>
                  <span>분석 중</span>
                  <span style={{ animation: 'pulse 1.5s infinite' }}>...</span>
                </div>
              </MessageContent>
            </MessageBubble>
          )}
          <div ref={messagesEndRef} />
        </MessagesArea>
        
        <InputArea>
          <MessageInput
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="궁금한 점이나 더 자세히 알고 싶은 내용을 입력해주세요..."
            disabled={isLoading}
          />
          <SendButton onClick={handleSendMessage} disabled={isLoading || !inputValue.trim()}>
            ➤
          </SendButton>
        </InputArea>
      </ChatContainer>
    </Container>
  );
};

export default DetailChatView;
