import React, { useState, useEffect } from 'react';
import {
  Sidebar,
  SidebarHeader,
  SidebarTitle,
  SidebarContent,
  SidebarMenuContent,
  SidebarToggleButton,
  ConversationMenuItem,
} from '../styles/GlobalStyle';
import { chatApi } from '../api/chatApi';
import { TrainingThread } from '../types';
import styled from 'styled-components';

interface ConversationSidebarProps {
  selectedItem: string | null;
  onSelectItem: (itemId: string | null) => void;
  isCollapsed?: boolean;
  onToggle?: () => void;
  selectedPersonaName?: string;
  selectedPersonaId?: string;
}

// ÏÉàÎ°úÏö¥ Ïä§ÌÉÄÏùº Ïª¥Ìè¨ÎÑåÌä∏Îì§
const AccordionContainer = styled.div`
  width: 100%;
`;

const AccordionItem = styled.div`
  border-bottom: 1px solid #f0f0f0;
`;

const AccordionHeader = styled.div<{ isSelected: boolean; isExpanded: boolean }>`
  padding: 12px 16px;
  cursor: pointer;
  background: ${props => props.isSelected ? 'linear-gradient(135deg, #6c757d 0%, #5a6268 100%)' : 'transparent'};
  color: ${props => props.isSelected ? 'white' : '#333'};
  font-weight: ${props => props.isSelected ? '600' : '500'};
  border-radius: ${props => props.isSelected ? '8px' : '0'};
  margin: ${props => props.isSelected ? '4px 8px' : '0'};
  transition: all 0.2s ease;
  display: flex;
  justify-content: space-between;
  align-items: center;

  &:hover {
    background: ${props => props.isSelected ? 'linear-gradient(135deg, #6c757d 0%, #5a6268 100%)' : '#f8f9fa'};
  }
`;

const AccordionIcon = styled.span<{ isExpanded: boolean }>`
  transform: rotate(${props => props.isExpanded ? '90deg' : '0deg'});
  transition: transform 0.2s ease;
  font-size: 12px;
`;

const ThreadsList = styled.div<{ isExpanded: boolean }>`
  max-height: ${props => props.isExpanded ? '300px' : '0'};
  overflow: hidden;
  transition: max-height 0.3s ease;
  background: #f8f9fa;
`;

const ThreadItem = styled.div<{ isSelected: boolean }>`
  padding: 8px 32px 8px 32px;
  cursor: pointer;
  color: ${props => props.isSelected ? '#6c757d' : '#666'};
  font-size: 14px;
  border-left: 3px solid ${props => props.isSelected ? '#6c757d' : 'transparent'};
  background: ${props => props.isSelected ? '#f8f9fa' : 'transparent'};
  transition: all 0.2s ease;
  display: flex;
  justify-content: space-between;
  align-items: center;

  &:hover {
    background: #f8f9fa;
    color: #6c757d;
  }
`;

const ThreadItemContent = styled.div`
  flex: 1;
`;

const DeleteThreadButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  color: #999;
  font-size: 14px;
  padding: 4px;
  border-radius: 4px;
  opacity: 0;
  transition: all 0.2s ease;

  &:hover {
    background: #e9ecef;
    color: #dc3545;
  }

  ${ThreadItem}:hover & {
    opacity: 1;
  }
`;

const ThreadTitle = styled.div`
  font-weight: 500;
  margin-bottom: 2px;
`;

const ThreadTime = styled.div`
  font-size: 12px;
  opacity: 0.7;
`;

const ConversationSidebar: React.FC<ConversationSidebarProps> = ({
  selectedItem,
  onSelectItem,
  isCollapsed = false,
  onToggle,
  selectedPersonaName,
  selectedPersonaId,
}) => {
  const [expandedItems, setExpandedItems] = useState<Set<string>>(new Set());
  const [threads, setThreads] = useState<Record<string, TrainingThread[]>>({
    interpretation: [],
    proceed: [],
    sentence: []
  });
  const [selectedThreadId, setSelectedThreadId] = useState<string | null>(null);

  const menuItems = [
    { id: 'interpretation', name: 'Ìï¥ÏÑù' },
    { id: 'proceed', name: 'ÎÇòÏïÑÍ∞ÄÍ∏∞' },
    { id: 'sentence', name: 'Î¨∏Ïû•' },
    { id: 'interaction_history', name: 'ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ù' },
  ];

  // Ïä§Î†àÎìú Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  useEffect(() => {
    const loadThreads = async () => {
      if (!selectedPersonaId) {
        setThreads({
          interpretation: [],
          proceed: [],
          sentence: []
        });
        return;
      }

      try {
        const response = await chatApi.getPersonaThreads(selectedPersonaId);
        if (response.data) {
          const threadsByType = {
            interpretation: response.data.filter(t => t.thread_type === 'interpretation'),
            proceed: response.data.filter(t => t.thread_type === 'proceed'),
            sentence: response.data.filter(t => t.thread_type === 'sentence')
          };
          setThreads(threadsByType);

          // Ïä§Î†àÎìúÍ∞Ä ÏûàÏúºÎ©¥ ÏûêÎèôÏúºÎ°ú Ï≤´ Î≤àÏß∏ Ïä§Î†àÎìú ÌôïÏû•
          ['interpretation', 'proceed', 'sentence'].forEach(type => {
            if (threadsByType[type as keyof typeof threadsByType].length > 0) {
              setExpandedItems(prev => new Set(prev).add(type));
            }
          });
        }
      } catch (error) {
        console.error('Ïä§Î†àÎìú Î°úÎìú Ïã§Ìå®:', error);
        // ÏóêÎü¨ Ïãú Îπà ÏÉÅÌÉúÎ°ú ÏÑ§Ï†ï
        setThreads({
          interpretation: [],
          proceed: [],
          sentence: []
        });
      }
    };

    loadThreads();
  }, [selectedPersonaId]);

  const handleAccordionToggle = (itemId: string) => {
    const newExpanded = new Set(expandedItems);
    if (newExpanded.has(itemId)) {
      newExpanded.delete(itemId);
    } else {
      newExpanded.add(itemId);
    }
    setExpandedItems(newExpanded);

    // ÏïÑÏΩîÎîîÏñ∏ ÌÜ†Í∏Ä ÏãúÏóêÎßå Î©îÏù∏ ÏïÑÏù¥ÌÖú ÏÑ†ÌÉù (Ïä§Î†àÎìúÍ∞Ä ÏóÜÎäî Í≤ΩÏö∞ÏóêÎßå)
    const itemThreads = threads[itemId as keyof typeof threads] || [];
    if (itemThreads.length === 0) {
      onSelectItem(itemId);
    }
  };

  const handleThreadSelect = (threadId: string, itemType: string) => {
    setSelectedThreadId(threadId);
    onSelectItem(`${itemType}-thread-${threadId}`);
  };

  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString('ko-KR', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const handleDeleteThread = async (threadId: string, e: React.MouseEvent) => {
    e.stopPropagation(); // Ïù¥Î≤§Ìä∏ Î≤ÑÎ∏îÎßÅ Î∞©ÏßÄ
    if (window.confirm('Ïù¥ Ïä§Î†àÎìúÎ•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      // TODO: Î∞±ÏóîÎìú API Ìò∏Ï∂úÎ°ú Ïä§Î†àÎìú ÏÇ≠Ï†ú
      console.log('Ïä§Î†àÎìú ÏÇ≠Ï†ú:', threadId);
      // ÏûÑÏãúÎ°ú Î°úÏª¨ ÏÉÅÌÉúÏóêÏÑú Ï†úÍ±∞
      const updatedThreads = { ...threads };
      Object.keys(updatedThreads).forEach(key => {
        updatedThreads[key as keyof typeof threads] = updatedThreads[key as keyof typeof threads].filter(t => t.id !== threadId);
      });
      setThreads(updatedThreads);

      // ÏÑ†ÌÉùÎêú Ïä§Î†àÎìúÍ∞Ä ÏÇ≠Ï†úÎêú Í≤ΩÏö∞ ÏÑ†ÌÉù Ìï¥Ï†ú
      if (selectedThreadId === threadId) {
        setSelectedThreadId(null);
      }
    }
  };

  const renderThreads = (itemId: string) => {
    const itemThreads = threads[itemId as keyof typeof threads] || [];
    const isExpanded = expandedItems.has(itemId);

    return (
      <ThreadsList isExpanded={isExpanded}>
        {itemThreads.map((thread, index) => (
          <ThreadItem
            key={thread.id}
            isSelected={selectedThreadId === thread.id}
            onClick={() => handleThreadSelect(thread.id, itemId)}
          >
            <ThreadItemContent>
              <ThreadTitle>
                {itemId === 'interpretation' && `Ìï¥ÏÑù #${index + 1}`}
                {itemId === 'proceed' && `ÎÇòÏïÑÍ∞ÄÍ∏∞ #${index + 1}`}
                {itemId === 'sentence' && `Î¨∏Ïû• #${index + 1}`}
              </ThreadTitle>
              <ThreadTime>
                {formatTime(thread.created_at)} ‚Ä¢ {thread.messages.length}Í∞ú Î©îÏãúÏßÄ
              </ThreadTime>
            </ThreadItemContent>
            <DeleteThreadButton
              onClick={(e) => handleDeleteThread(thread.id, e)}
              title="Ïä§Î†àÎìú ÏÇ≠Ï†ú"
            >
              üóëÔ∏è
            </DeleteThreadButton>
          </ThreadItem>
        ))}
        {itemThreads.length === 0 && (
          <div style={{ padding: '16px 32px', fontSize: '14px', color: '#999' }}>
            Ïä§Î†àÎìúÍ∞Ä ÏóÜÏäµÎãàÎã§
          </div>
        )}
      </ThreadsList>
    );
  };

  return (
    <Sidebar width="250px" isCollapsed={isCollapsed} variant="conversation">
      {onToggle && (
        <SidebarToggleButton onClick={onToggle}>
          {isCollapsed ? '‚ñ∑' : '‚óÅ'}
        </SidebarToggleButton>
      )}

      <SidebarHeader isCollapsed={isCollapsed}>
        {selectedPersonaName && (
          <SidebarTitle isCollapsed={isCollapsed}>
            {selectedPersonaName}
          </SidebarTitle>
        )}
      </SidebarHeader>

      <SidebarContent isCollapsed={isCollapsed}>
        <SidebarMenuContent variant="conversation">
          <AccordionContainer>
            {menuItems.map((item) => (
              <AccordionItem key={item.id}>
                <AccordionHeader
                  isSelected={selectedItem === item.id}
                  isExpanded={expandedItems.has(item.id)}
                  onClick={() => handleAccordionToggle(item.id)}
                >
                  <span>
                    {item.name}
                  </span>
                </AccordionHeader>

                {item.id !== 'interaction_history' && renderThreads(item.id)}
              </AccordionItem>
            ))}
          </AccordionContainer>
        </SidebarMenuContent>
      </SidebarContent>
    </Sidebar>
  );
};

export default ConversationSidebar;
