import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import MessageInput, { MessageInputRef } from './MessageInput';
import Message from './Message';
import SelectableMessage from './SelectableMessage';
import FloatingActionButton from './FloatingActionButton';
import LoadingMessage from './LoadingMessage';
import { sentenceApi } from '../api/sentenceApi';
import { Message as MessageType } from '../types';

interface InterpretationViewProps {
  interpretationData: {
    personaId: string;
    personaName: string;
    content: string;
    messages: MessageType[];
  };
  onSendMessage: (message: string) => Promise<boolean>;
  isLoading: boolean;
  onMenuAction: (action: string) => void;
  onCreateNewInterpretation?: () => void;
}

const InterpretationContainer = styled.div`
  height: 100%;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const InterpretationHeaderContainer = styled.div`
  padding: 20px 20px 10px 20px;
  border-bottom: 1px solid #e5e5e5;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const InterpretationHeader = styled.h2`
  margin: 0;
  color: #333;
  font-size: 24px;
  font-weight: 600;
`;

const NewInterpretationButton = styled.button`
  background: #007bff;
  color: white;
  border: none;
  border-radius: 50%;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 18px;
  transition: all 0.2s ease;

  &:hover {
    background: #0056b3;
    transform: scale(1.05);
  }
`;

const InterpretationContent = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
`;

const MessagesArea = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 0 20px;
  background: #fff;
`;

const ActionButtonsArea = styled.div`
  position: fixed;
  bottom: 90px;
  right: 30px;
  z-index: 1000;
`;

const InterpretationView: React.FC<InterpretationViewProps> = ({
  interpretationData,
  onSendMessage,
  isLoading,
  onMenuAction,
  onCreateNewInterpretation
}) => {
  const chatMessagesRef = useRef<HTMLDivElement>(null);
  const messageInputRef = useRef<MessageInputRef>(null);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [editingMessageIndex, setEditingMessageIndex] = useState<number | null>(null);

  // Î¨∏Ïû• ÏÑ†ÌÉù Í¥ÄÎ†® ÏÉÅÌÉú
  const [selectedSentences, setSelectedSentences] = useState<Set<string>>(new Set());
  const [highlightedSentences, setHighlightedSentences] = useState<Set<string>>(new Set());
  const [memos, setMemos] = useState<Record<string, string>>({});
  const [isSentenceModeActive, setIsSentenceModeActive] = useState(false);

  const [menuOptions] = useState([
    { id: 'copy', label: 'Î≥µÏÇ¨', emoji: 'üìã' },
    { id: 'interpretation', label: 'Ìï¥', emoji: 'üîç' },
    { id: 'proceed', label: 'ÎÇò', emoji: 'üéØ' },
    { id: 'sentence', label: 'Î¨∏', emoji: 'üìù' }
  ]);

  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [interpretationData.messages]);

  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString('ko-KR', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  // ÌÜ†Ïä§Ìä∏ Î©îÏãúÏßÄ ÌëúÏãú Ìï®Ïàò
  const showCopyToast = (message: string) => {
    setToastMessage(message);
    setShowToast(true);
    setTimeout(() => setShowToast(false), 3000);
  };

  // ÌÖçÏä§Ìä∏ Î≥µÏÇ¨ Ïú†Ìã∏Î¶¨Ìã∞ Ìï®Ïàò
  const copyToClipboard = async (text: string, successMessage: string) => {
    try {
      await navigator.clipboard.writeText(text);
      showCopyToast(successMessage);
    } catch (error) {
      console.error('Î≥µÏÇ¨ Ïã§Ìå®:', error);
      // ÎåÄÏ≤¥ Î≥µÏÇ¨ Î∞©Î≤ï
      try {
        const textArea = document.createElement('textarea');
        textArea.value = text;
        textArea.style.position = 'fixed';
        textArea.style.left = '-999999px';
        textArea.style.top = '-999999px';
        document.body.appendChild(textArea);
        textArea.focus();
        textArea.select();
        document.execCommand('copy');
        document.body.removeChild(textArea);
        showCopyToast(successMessage);
      } catch (fallbackError) {
        console.error('ÎåÄÏ≤¥ Î≥µÏÇ¨ Î∞©Î≤ïÎèÑ Ïã§Ìå®:', fallbackError);
        showCopyToast('Î≥µÏÇ¨ Ïã§Ìå®');
      }
    }
  };

  // Ìï¥ÏÑù ÎÇ¥Ïö© Î≥µÏÇ¨ Í∏∞Îä•
  const handleCopyInterpretation = async () => {
    await copyToClipboard(interpretationData.content, 'Ìï¥ÏÑù ÎÇ¥Ïö©Ïù¥ Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§');
  };

  // Î©îÏãúÏßÄ Î≥µÏÇ¨ Í∏∞Îä•
  const handleCopyMessage = async (messageContent: string) => {
    await copyToClipboard(messageContent, 'Î©îÏãúÏßÄÍ∞Ä Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§');
  };

  // Î©îÏãúÏßÄ ÏàòÏ†ï ÏãúÏûë
  const handleStartEdit = (messageIndex: number) => {
    setEditingMessageIndex(messageIndex);
  };

  // Î©îÏãúÏßÄ ÏàòÏ†ï ÏôÑÎ£å
  const handleEditMessage = async (messageIndex: number, newContent: string) => {
    // Implementation needed
  };

  // Î©îÏãúÏßÄ ÏàòÏ†ï Ï∑®ÏÜå
  const handleCancelEdit = () => {
    setEditingMessageIndex(null);
  };

  // Î¨∏Ïû• ÏÑ†ÌÉù Ìï∏Îì§Îü¨Îì§
  const handleToggleSelect = (sentenceId: string) => {
    setSelectedSentences(prev => {
      const newSet = new Set(prev);
      if (newSet.has(sentenceId)) {
        newSet.delete(sentenceId);
      } else {
        newSet.add(sentenceId);
      }
      return newSet;
    });
  };

  const handleMemoChange = async (sentenceId: string, memo: string) => {
    try {
      // Î°úÏª¨ ÏÉÅÌÉúÏóêÏÑú Î©îÎ™® ÏóÖÎç∞Ïù¥Ìä∏ (Ï¶âÏãú Î∞òÏòÅ)
      setMemos(prev => ({
        ...prev,
        [sentenceId]: memo
      }));

      // Î∞±ÏóîÎìú API Ìò∏Ï∂ú (ÎπÑÎèôÍ∏∞, Ïã§Ìå®Ìï¥ÎèÑ Î°úÏª¨ ÏóÖÎç∞Ïù¥Ìä∏Îäî Ïú†ÏßÄ)
      sentenceApi.createOrUpdateMemo({
        sentence_id: sentenceId,
        content: memo
      }).catch(error => {
        console.warn('Î∞±ÏóîÎìú Î©îÎ™® Ï†ÄÏû• Ïã§Ìå® (Î°úÏª¨ Ï†ÄÏû•ÏùÄ ÏÑ±Í≥µ):', error);
      });
    } catch (error) {
      console.error('Î©îÎ™® Ï†ÄÏû• Ïã§Ìå®:', error);
      showCopyToast('Î©îÎ™® Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
    }
  };

  const handleDeleteMemo = async (sentenceId: string) => {
    try {
      // Î°úÏª¨ ÏÉÅÌÉúÏóêÏÑú Î©îÎ™® ÏÇ≠Ï†ú
      setMemos(prev => {
        const newMemos = { ...prev };
        delete newMemos[sentenceId];
        return newMemos;
      });
      setHighlightedSentences(prev => {
        const newSet = new Set(prev);
        newSet.delete(sentenceId);
        return newSet;
      });

      // Î∞±ÏóîÎìú API Ìò∏Ï∂ú (ÎπÑÎèôÍ∏∞, Ïã§Ìå®Ìï¥ÎèÑ Î°úÏª¨ ÏÇ≠Ï†úÎäî Ïú†ÏßÄ)
      sentenceApi.deleteMemo(sentenceId).catch(error => {
        console.warn('Î∞±ÏóîÎìú Î©îÎ™® ÏÇ≠Ï†ú Ïã§Ìå® (Î°úÏª¨ ÏÇ≠Ï†úÎäî ÏÑ±Í≥µ):', error);
      });

      showCopyToast('Î©îÎ™®Í∞Ä ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§');
    } catch (error) {
      console.error('Î©îÎ™® ÏÇ≠Ï†ú Ïã§Ìå®:', error);
      showCopyToast('Î©îÎ™® ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
    }
  };

  // ÌîåÎ°úÌåÖ Ïï°ÏÖò Î≤ÑÌäº Î©îÎâ¥ Ïï°ÏÖòÎì§
  const handleMenuAction = (action: string) => {
    if (action === 'copy') {
      handleCopyInterpretation();
    } else if (action === 'interpretation') {
      onMenuAction('generateInterpretation');
    } else if (action === 'proceed') {
      onMenuAction('generateProceed');
    } else if (action === 'sentence') {
      onMenuAction('generateSentence');
    }
  };

  // Î¨∏Ïû•ÏÑ†ÌÉù Î™®Îìú ÌÜ†Í∏Ä
  const handleToggleSentenceMode = () => {
    setIsSentenceModeActive(prev => !prev);
  };

  return (
    <InterpretationContainer>
      <Toast show={showToast}>
        {toastMessage}
      </Toast>
      <InterpretationHeaderContainer>
        <InterpretationHeader>
          {interpretationData.personaName} Ìï¥ÏÑù
        </InterpretationHeader>
        <NewInterpretationButton 
          onClick={onCreateNewInterpretation}
          title="ÏÉàÎ°úÏö¥ Ìï¥ÏÑù ÎßåÎì§Í∏∞"
        >
          ‚ú®
        </NewInterpretationButton>
      </InterpretationHeaderContainer>

      <InterpretationContent>
        <MessagesArea>
          {interpretationData.messages.map((msg, index) => (
            <Message
              key={index}
              message={{
                ...msg,
                id: `msg-${index}`,
                isUser: msg.role === 'user'
              }}
            />
          ))}
          {isLoading && <LoadingMessage />}
        </MessagesArea>

        <MessageInput
          ref={messageInputRef}
          onSendMessage={onSendMessage}
          disabled={isLoading}
          placeholder={`${interpretationData.personaName}ÏóêÍ≤å Î©îÏãúÏßÄÎ•º Î≥¥ÎÇ¥ÏÑ∏Ïöî...`}
          onToggleSentenceMode={handleToggleSentenceMode}
          isSentenceModeActive={isSentenceModeActive}
          hasSelectedSentences={selectedSentences.size > 0}
        />
      </InterpretationContent>

      <ActionButtonsArea>
        <FloatingActionButton
          options={menuOptions}
          onMenuAction={handleMenuAction}
        />
      </ActionButtonsArea>
    </InterpretationContainer>
  );
};

