    console.log('로컬 메시지 수:', localMessages.length);

    // assistant 메시지에서만 선택된 문장들 필터링
    const assistantSelectedTexts: { text: string; id: string }[] = [];
    const assistantSelectedIds: string[] = [];

    selectedIds.forEach(id => {
      console.log('처리 중인 ID:', id);
      // sentenceId에서 실제 문장 텍스트를 찾아내는 로직
      const [timestamp, , sentenceIndex] = id.split('_');
      console.log('파싱된 정보:', { timestamp, sentenceIndex });

      // 더 안전한 메시지 찾기 로직
      let message = null;

      // 1. timestamp로 정확히 찾기
      message = localMessages.find(m => m.timestamp === timestamp);
      console.log('timestamp 정확 매칭:', message ? '성공' : '실패');

      // 2. timestamp가 없으면 index로 찾기 (분해할당에서 가져온 것 사용)
      if (!message) {
        console.log('timestamp로 메시지를 찾을 수 없음, index로 시도');
        // sentenceId에서 messageIndex를 다시 추출
        const messageIndex = id.split('_')[1];
        const index = parseInt(messageIndex);
        if (!isNaN(index) && index >= 0 && index < localMessages.length) {
          message = localMessages[index];
          console.log('messageIndex로 찾은 메시지:', message ? `${message.role}: ${message.content.substring(0, 50)}...` : '없음');
        }
      }

      // 3. 여전히 없으면 timestamp 부분 매칭 시도
      if (!message) {
        console.log('부분 timestamp 매칭 시도');
        message = localMessages.find(m => {
          const messageTime = new Date(m.timestamp).getTime();
          const searchTime = new Date(timestamp).getTime();
          const timeDiff = Math.abs(messageTime - searchTime);
          return timeDiff < 1000; // 1초 이내 차이면 같은 메시지로 간주
        });
        console.log('부분 매칭 결과:', message ? '성공' : '실패');
