          console.log('선택된 문장들:', selectedTexts);
          console.log('필터링된 ID들:', filteredSelectedIds);
          console.log('선택된 스레드:', selectedThread?.id);
          console.log('로컬 메시지들:', localMessages.map(m => ({ role: m.role, timestamp: m.timestamp, content: m.content.substring(0, 50) + '...' })));

          if (selectedTexts.length === 0) {
