            console.warn('- 필터링된 ID 개수:', filteredSelectedIds.length); 
            console.warn('- 최종 텍스트 개수:', selectedTexts.length);

            if (selectedIds.length > 0 && filteredSelectedIds.length === 0) {
              console.warn('모든 선택된 문장이 user 메시지여서 필터링되었습니다');
              showCopyToast('assistant 메시지만 저장할 수 있습니다');
