          // 선택된 문장들의 하이라이트/메모 상태 수집 (assistant 메시지만)
          const highlightStates: boolean[] = [];
          const highlightColors: (string | null)[] = [];
          const memoContents: (string | null)[] = [];

          for (const sentenceId of filteredSelectedIds) {
            const isHighlighted = highlightedSentences.has(sentenceId);
            const memoContent = memos[sentenceId] || null;

            highlightStates.push(isHighlighted);
            highlightColors.push(isHighlighted ? 'yellow' : null);
            memoContents.push(memoContent);
          }

          const saveRequest = {
            sentences: selectedTexts,
            source_message_id: 'verbalization_chat',
            source_conversation_id: selectedThread.id,
            source_thread_id: selectedThread.id,
            source_thread_type: 'verbalization',
            source_sentence_ids: filteredSelectedIds,
            tags: ['verbalization'],
            highlight_states: highlightStates,
            highlight_colors: highlightColors,
            memo_contents: memoContents
          };

          console.log('저장 요청 데이터:', saveRequest);

          const saveResponse = await sentenceApi.saveSentencesToVault(saveRequest);
          console.log('저장 응답:', saveResponse);

          // 백엔드에 하이라이트도 저장 (기존 로직 유지)
          if (selectedThread?.id) {
            console.log('하이라이트 저장 시작');
            for (const sentenceId of filteredSelectedIds) {
              try {
              await sentenceApi.createHighlight({
                sentence_id: sentenceId,
                thread_id: selectedThread.id,
                thread_type: 'verbalization'
              });
                console.log('하이라이트 저장 완료:', sentenceId);
              } catch (highlightError) {
                console.warn('하이라이트 저장 실패:', sentenceId, highlightError);
              }
            }
          }

          // 성공 시 로컬 상태 업데이트
          setHighlightedSentences(prev => {
            const newSet = new Set(prev);
            filteredSelectedIds.forEach(id => newSet.add(id));
            return newSet;
          });

          console.log('=== 언어화 저장고 저장 완료 ===');
          showCopyToast('저장고에 저장되었습니다 (하이라이트/메모 정보 포함)');
        } catch (error) {
          console.error('=== 언어화 저장고 저장 실패 ===', error);
          console.error('오류 세부사항:', {
            message: error instanceof Error ? error.message : String(error),
            stack: error instanceof Error ? error.stack : undefined
          });
          showCopyToast(`저장고 저장에 실패했습니다: ${error instanceof Error ? error.message : String(error)}`);
        }
        break;

      case 'addMemo':
        if (filteredSelectedIds.length > 0) {
          // 가장 아래에 있는 문장 ID 찾기 (assistant 메시지만)
          const sortedIds = filteredSelectedIds.sort((a, b) => {
            const [timestampA, , sentenceIndexA] = a.split('_');
            const [timestampB, , sentenceIndexB] = b.split('_');

            if (timestampA !== timestampB) {
              return new Date(timestampA).getTime() - new Date(timestampB).getTime();
            }
            return parseInt(sentenceIndexA) - parseInt(sentenceIndexB);
          });

          const bottomSentenceId = sortedIds[sortedIds.length - 1];

          try {
            // 백엔드에 메모 생성 - 메모 저장고 정보 포함
            const bottomSentenceText = selectedTexts[selectedTexts.length - 1];
            await sentenceApi.createOrUpdateMemo({
              sentence_id: bottomSentenceId,
              thread_id: selectedThread?.id,
              thread_type: 'verbalization',
              content: '',
              sentence_content: bottomSentenceText,
              source_message_id: 'verbalization_chat'
            });

            // 백엔드에 하이라이트 저장
            if (selectedThread?.id) {
              for (const sentenceId of filteredSelectedIds) {
                await sentenceApi.createHighlight({
                  sentence_id: sentenceId,
                  thread_id: selectedThread.id,
                  thread_type: 'verbalization'
                });
              }
            }

            // 성공 시 로컬 상태 업데이트
            setMemos(prev => ({ ...prev, [bottomSentenceId]: '' }));
            setHighlightedSentences(prev => {
              const newSet = new Set(prev);
              filteredSelectedIds.forEach(id => newSet.add(id));
              return newSet;
            });

            showCopyToast('메모와 하이라이트가 추가되었습니다');
          } catch (error) {
            console.error('메모/하이라이트 추가 실패:', error);
            showCopyToast('메모/하이라이트 추가에 실패했습니다');
          }
        }
        break;

      case 'highlight':
        if (filteredSelectedIds.length > 0 && selectedThread?.id) {
          try {
            console.log('하이라이트 토글 시작:', filteredSelectedIds);

            // 현재 하이라이트된 문장들과 선택된 문장들을 비교 (assistant 메시지만)
            const currentlyHighlighted = filteredSelectedIds.filter(id => highlightedSentences.has(id));
            const notHighlighted = filteredSelectedIds.filter(id => !highlightedSentences.has(id));

            console.log('현재 하이라이트된 문장들:', currentlyHighlighted);
            console.log('아직 하이라이트되지 않은 문장들:', notHighlighted);

            if (currentlyHighlighted.length > 0) {
              // 일부가 하이라이트되어 있으면 모두 제거
              console.log('기존 하이라이트 제거 중...');

              // 로컬 상태에서 하이라이트 제거
              setHighlightedSentences(prev => {
                const newSet = new Set(prev);
                filteredSelectedIds.forEach(id => newSet.delete(id));
                return newSet;
              });

              // 백엔드에서 하이라이트 삭제
              for (const sentenceId of filteredSelectedIds) {
                try {
                  await sentenceApi.deleteHighlight(sentenceId);
                } catch (error) {
                  console.warn('백엔드 하이라이트 삭제 실패:', error);
                }
              }

              showCopyToast('하이라이트가 제거되었습니다');
            } else {
              // 모두 하이라이트되지 않았으면 모두 추가
              console.log('새 하이라이트 추가 중...');

              // 로컬 상태에 하이라이트 추가
              setHighlightedSentences(prev => {
                const newSet = new Set(prev);
                filteredSelectedIds.forEach(id => newSet.add(id));
                return newSet;
              });

              // 백엔드에 하이라이트 저장
              for (const sentenceId of filteredSelectedIds) {
                try {
                  await sentenceApi.createHighlight({
                    sentence_id: sentenceId,
                    thread_id: selectedThread.id,
                    thread_type: 'verbalization'
                  });
                } catch (error) {
                  console.warn('백엔드 하이라이트 저장 실패:', error);
                }
              }

              showCopyToast('하이라이트가 추가되었습니다');
            }
          } catch (error) {
            console.error('하이라이트 토글 실패:', error);
            showCopyToast('하이라이트 처리에 실패했습니다');
          }
        }
        break;

      case 'copy':
        if (selectedTexts.length > 0) {
          await copyToClipboard(selectedTexts.join(' '), '선택된 문장이 복사되었습니다');
        }
        break;
    }

    // 모든 선택 해제
    setSelectedSentences(new Set());
  };

  // 문장선택 모드 토글
  const handleToggleSentenceMode = () => {
    setIsSentenceModeActive(prev => !prev);
  };

  return (
    <Container>
      {/* 헤더 */}
      <HeaderSection>
        <HeaderTitle>언어화</HeaderTitle>
        <NewThreadButton onClick={handleCreateNewThread}>
          새 대화 시작
        </NewThreadButton>
      </HeaderSection>

      {/* 채팅 영역 */}
      <ChatSection>
        <ChatMessages ref={chatMessagesRef}>
          {localMessages.length === 0 ? (
            <EmptyChat>
              <EmptyIcon>💭</EmptyIcon>
              <div>
                <strong>언어화는 막연하고 뭔지 모를 감정이나 생각을 말로 표현함으로써<br />
                그것을 인식하고 만들어 가는 것입니다.</strong>
                <br /><br />
                자유롭게 당신의 생각을 말로 내뱉으세요!
                <br /><br />
                어떤 것이든 좋습니다. 지금 느끼고 있는 감정이나 생각을 편안하게 말씀해 주세요.
              </div>
            </EmptyChat>
          ) : (
            localMessages.map((message, index) => {
              const isAssistant = message.role === 'assistant';
              const showSentenceSelector = isAssistant;

              console.log(`메시지 ${index} 렌더링:`, {
                role: message.role,
                isAssistant,
                showSentenceSelector,
                isSentenceModeActive,
                content: message.content?.substring(0, 50) + '...',
                timestamp: message.timestamp,
                messageType: typeof message,
                messageKeys: Object.keys(message)
              });

              return isSentenceModeActive ? (
                <SelectableMessage
                  key={`${message.role}-${index}`}
                  message={message}
                  messageIndex={index}
                  personas={{}}
                  selectedSentences={selectedSentences}
                  highlightedSentences={highlightedSentences}
                  memos={memos}
                  onToggleSelect={(sentenceId) => {
                    console.log('=== 언어화 문장 선택 토글 ===');
                    console.log('sentenceId:', sentenceId);
                    console.log('파싱된 sentenceId:', sentenceId.split('_'));
                    console.log('메시지 정보:', { 
                      index, 
                      role: message.role, 
                      timestamp: message.timestamp,
                      content: message.content?.substring(0, 50) + '...'
                    });
                    console.log('현재 selectedSentences:', Array.from(selectedSentences));
                    handleToggleSelect(sentenceId);
                    console.log('토글 후 selectedSentences:', Array.from(selectedSentences));
                  }}
                  onMemoChange={handleMemoChange}
                  onDeleteMemo={handleDeleteMemo}
                  showSentenceSelector={showSentenceSelector}
                />
              ) : (
                <Message
                  key={`${message.role}-${index}`}
                  message={message}
                  personas={{}} // 언어화에서는 페르소나 정보 불필요
                  showActionButtons={true}
                  showThreeActionButtons={false}
                  onCopy={() => handleCopyMessage(message.content)}
                  onEdit={message.role === 'user' ? () => handleStartEdit(index) : undefined}
                  onEditSave={(newContent) => handleEditMessage(index, newContent)}
                  onEditCancel={handleCancelEdit}
                  isEditing={editingMessageIndex === index}
                />
              );
            })
          )}

          {/* 로딩 메시지 */}
          {isLoading && (
            <LoadingMessage 
              personaName="언어화 도우미"
              personaColor="#666"
            />
          )}
        </ChatMessages>

        {/* 입력창 */}
        <ChatInputSection>
          <MessageInput
            ref={messageInputRef}
            onSendMessage={handleSendMessage}
            disabled={isLoading}
            placeholder="언어화는 막연하고 뭔지 모를 감정이나 생각을 말로 표현함으로써 그것을 인식하고 만들어 가는 것입니다. 자유롭게 당신의 생각을 말로 내뱉으세요!"
            isSentenceModeActive={isSentenceModeActive}
            hasSelectedSentences={selectedSentences.size > 0}
            onToggleSentenceMode={handleToggleSentenceMode}
          />
        </ChatInputSection>
      </ChatSection>

      {/* 토스트 메시지 */}
      <Toast show={showToast}>
        {toastMessage}
      </Toast>

      {/* FloatingActionButton */}
      <FloatingActionButton
        show={isSentenceModeActive ? selectedSentences.size > 0 : showFAB}
        onMenuAction={isSentenceModeActive ? handleMenuAction : handleFABMenuAction}
        personaId="verbalization"
        currentInterpretation=""
      />
    </Container>
  );
};

export default VerbalizationView;
