import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const Container = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
  padding: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const FormFrame = styled.div`
  background: white;
  padding: 3rem 2.5rem;
  border-radius: 12px;
  border: 1px solid #e2e8f0;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
  width: 100%;
  max-width: 600px;
`;

const Title = styled.h1`
  font-size: 2rem;
  font-weight: 600;
  color: #2d3748;
  margin-bottom: 2rem;
  text-align: center;
`;

const Question = styled.div`
  margin-bottom: 2rem;
`;

const QuestionText = styled.h3`
  font-size: 1.2rem;
  color: #2d3748;
  margin-bottom: 1rem;
`;

const TextArea = styled.textarea`
  width: 100%;
  min-height: 120px;
  padding: 1rem;
  border: 2px solid #e2e8f0;
  border-radius: 8px;
  font-size: 1rem;
  font-family: inherit;
  resize: vertical;
  box-sizing: border-box;
  
  &:focus {
    outline: none;
    border-color: #3182ce;
    box-shadow: 0 0 0 3px rgba(49, 130, 206, 0.1);
  }
`;

const SubmitButton = styled.button`
  width: 100%;
  background: #2d3748;
  color: white;
  border: none;
  padding: 1rem;
  border-radius: 8px;
  font-size: 1.1rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  
  &:hover:not(:disabled) {
    background: #1a202c;
    transform: translateY(-1px);
  }
  
  &:disabled {
    background: #a0aec0;
    cursor: not-allowed;
    transform: none;
  }
`;

const LogoutButton = styled.button`
  position: absolute;
  top: 20px;
  right: 20px;
  background: #e53e3e;
  color: white;
  border: none;
  padding: 0.5rem 1rem;
  border-radius: 6px;
  cursor: pointer;
  font-size: 0.9rem;
  font-weight: 500;
  
  &:hover {
    background: #c53030;
  }
`;

interface QAData {
  question1: string;
  question2: string;
  question3: string;
}

const QAPage: React.FC = () => {
  const [answers, setAnswers] = useState<QAData>({
    question1: '',
    question2: '',
    question3: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // 로그인 상태 확인
    const token = localStorage.getItem('access_token');
    if (!token) {
      navigate('/');
      return;
    }
  }, [navigate]);

  const handleAnswerChange = (questionKey: keyof QAData, value: string) => {
    setAnswers(prev => ({
      ...prev,
      [questionKey]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!answers.question1.trim() || !answers.question2.trim() || !answers.question3.trim()) {
      alert('모든 질문에 답변해주세요.');
      return;
    }

    setIsSubmitting(true);

    try {
      // QA 답변을 localStorage에 저장
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      const qaData = {
        userId: user.id,
        username: user.username,
        answers: answers,
        completedAt: new Date().toISOString()
      };

      // 기존 QA 데이터 불러오기
      const existingQAData = JSON.parse(localStorage.getItem('qa_data') || '[]');
      
      // 현재 사용자의 QA 데이터가 이미 있는지 확인
      const existingIndex = existingQAData.findIndex((qa: any) => qa.userId === user.id);
      
      if (existingIndex >= 0) {
        // 기존 데이터 업데이트
        existingQAData[existingIndex] = qaData;
      } else {
        // 새 데이터 추가
        existingQAData.push(qaData);
      }

      localStorage.setItem('qa_data', JSON.stringify(existingQAData));

      alert('QA 완료! Training 페이지로 이동합니다.');
      navigate('/training');
    } catch (error) {
      console.error('QA 저장 중 오류:', error);
      alert('저장 중 오류가 발생했습니다.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('token_type');
    localStorage.removeItem('user');
    navigate('/');
  };

  return (
    <Container>
      <LogoutButton onClick={handleLogout}>
        로그아웃
      </LogoutButton>
      
      <FormFrame>
        <Title>사전 질문답변</Title>
        
        <form onSubmit={handleSubmit}>
          <Question>
            <QuestionText>1. 당신의 현재 상황과 고민을 자세히 설명해주세요.</QuestionText>
            <TextArea
              value={answers.question1}
              onChange={(e) => handleAnswerChange('question1', e.target.value)}
              placeholder="현재 상황, 느끼는 감정, 구체적인 고민 등을 자유롭게 작성해주세요..."
            />
          </Question>

          <Question>
            <QuestionText>2. 이 문제가 언제부터 시작되었고, 어떤 변화를 원하시나요?</QuestionText>
            <TextArea
              value={answers.question2}
              onChange={(e) => handleAnswerChange('question2', e.target.value)}
              placeholder="문제의 시작 시점, 원하는 변화, 목표 등을 구체적으로 작성해주세요..."
            />
          </Question>

          <Question>
            <QuestionText>3. 이전에 시도해본 해결방법이 있다면 어떤 것들이었나요?</QuestionText>
            <TextArea
              value={answers.question3}
              onChange={(e) => handleAnswerChange('question3', e.target.value)}
              placeholder="시도했던 방법들, 그 결과, 도움이 되었던 것들을 작성해주세요..."
            />
          </Question>

          <SubmitButton type="submit" disabled={isSubmitting}>
            {isSubmitting ? '저장 중...' : 'QA 완료'}
          </SubmitButton>
        </form>
      </FormFrame>
    </Container>
  );
};

export default QAPage;
