import React, { useState } from 'react';
import { Message as MessageType, Persona } from '../types';
import styled from 'styled-components';
import {
  MessageContainer,
  MessageBubble,
  MessageInfo,
} from '../styles/GlobalStyle';

interface MessageProps {
  message: MessageType;
  personas: Record<string, Persona>;
  showActionButtons?: boolean;
  onCopy?: () => void;
  onSunAction?: () => void;
  onPersonAction?: () => void;
  onDocumentAction?: () => void;
  onEdit?: () => void;
  isEditing?: boolean;
  onEditSave?: (newContent: string) => void;
  onEditCancel?: () => void;
}

const ActionButtonsContainer = styled.div`
  display: flex;
  gap: 8px;
  margin-top: 8px;
  margin-left: 4px;
`;

const ActionButton = styled.button<{ variant?: 'copy' | 'sun' | 'person' | 'document' | 'edit' | 'save' | 'cancel' }>`
  width: 32px;
  height: 32px;
  border-radius: 50%;
  border: none;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 14px;
  box-shadow: 0 1px 4px rgba(0, 0, 0, 0.1);
  
  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none !important;
    box-shadow: 0 1px 4px rgba(0, 0, 0, 0.1) !important;
  }
  
  ${props => {
    switch (props.variant) {
      case 'copy':
        return `
          background: linear-gradient(135deg, #6c757d 0%, #495057 100%);
          color: white;
          &:hover:not(:disabled) { 
            transform: translateY(-1px); 
            box-shadow: 0 2px 8px rgba(108, 117, 125, 0.3); 
          }
        `;
      case 'edit':
        return `
          background: linear-gradient(135deg, #17a2b8 0%, #138496 100%);
          color: white;
          &:hover:not(:disabled) { 
            transform: translateY(-1px); 
            box-shadow: 0 2px 8px rgba(23, 162, 184, 0.3); 
          }
        `;
      case 'save':
        return `
          background: linear-gradient(135deg, #28a745 0%, #1e7e34 100%);
          color: white;
          &:hover:not(:disabled) { 
            transform: translateY(-1px); 
            box-shadow: 0 2px 8px rgba(40, 167, 69, 0.3); 
          }
        `;
      case 'cancel':
        return `
          background: linear-gradient(135deg, #dc3545 0%, #bd2130 100%);
          color: white;
          &:hover:not(:disabled) { 
            transform: translateY(-1px); 
            box-shadow: 0 2px 8px rgba(220, 53, 69, 0.3); 
          }
        `;
      case 'sun':
        return `
          background: linear-gradient(135deg, #ffc107 0%, #fd7e14 100%);
          color: white;
          &:hover:not(:disabled) { 
            transform: translateY(-1px); 
            box-shadow: 0 2px 8px rgba(255, 193, 7, 0.3); 
          }
        `;
      case 'person':
        return `
          background: linear-gradient(135deg, #20c997 0%, #198754 100%);
          color: white;
          &:hover:not(:disabled) { 
            transform: translateY(-1px); 
            box-shadow: 0 2px 8px rgba(32, 201, 151, 0.3); 
          }
        `;
      case 'document':
        return `
          background: linear-gradient(135deg, #0d6efd 0%, #0b5ed7 100%);
          color: white;
          &:hover:not(:disabled) { 
            transform: translateY(-1px); 
            box-shadow: 0 2px 8px rgba(13, 110, 253, 0.3); 
          }
        `;
      default:
        return `
          background: #f8f9fa;
          color: #6c757d;
          &:hover:not(:disabled) { background: #e9ecef; }
        `;
    }
  }}
  
  &:active:not(:disabled) {
    transform: translateY(0);
  }
`;

const EditTextarea = styled.textarea`
  width: 100%;
  min-height: 80px;
  padding: 12px;
  border: 2px solid #007bff;
  border-radius: 8px;
  font-size: 14px;
  font-family: inherit;
  line-height: 1.4;
  resize: vertical;
  outline: none;
  
  &:focus {
    border-color: #0056b3;
    box-shadow: 0 0 0 3px rgba(0, 123, 255, 0.1);
  }
`;

const EditButtonsContainer = styled.div`
  display: flex;
  gap: 8px;
  margin-top: 8px;
  justify-content: flex-end;
`;

const Message: React.FC<MessageProps> = ({ 
  message, 
  personas, 
  showActionButtons = false,
  onCopy,
  onSunAction,
  onPersonAction,
  onDocumentAction,
  onEdit,
  isEditing = false,
  onEditSave,
  onEditCancel
}) => {
  const [editText, setEditText] = useState(message.content);
  const [isSaving, setIsSaving] = useState(false);
  const isUser = message.role === 'user';
  const persona = message.persona_id ? personas[message.persona_id] : null;
  
  const formatTime = (timestamp: string) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('ko-KR', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const handleSaveEdit = async () => {
    if (onEditSave && editText.trim()) {
      setIsSaving(true);
      try {
        await onEditSave(editText.trim());
      } finally {
        setIsSaving(false);
      }
    }
  };

  const handleCancelEdit = () => {
    setEditText(message.content); // ÏõêÎûò ÎÇ¥Ïö©ÏúºÎ°ú Î≥µÏõê
    if (onEditCancel) {
      onEditCancel();
    }
  };

  // Ìé∏Ïßë Î™®ÎìúÏùº Îïå ÌÖçÏä§Ìä∏ Î≥ÄÍ≤Ω Ïãú ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
  React.useEffect(() => {
    if (isEditing) {
      setEditText(message.content);
    }
  }, [isEditing, message.content]);

  return (
    <MessageContainer isUser={isUser}>
      {isEditing ? (
        <div style={{ width: '100%' }}>
          <EditTextarea
            value={editText}
            onChange={(e) => setEditText(e.target.value)}
            placeholder="Î©îÏãúÏßÄÎ•º ÏàòÏ†ïÌïòÏÑ∏Ïöî..."
            autoFocus
            disabled={isSaving}
          />
          <EditButtonsContainer>
            <ActionButton 
              variant="save" 
              onClick={handleSaveEdit} 
              title={isSaving ? "Ï†ÄÏû• Ï§ë..." : "Ï†ÄÏû•"}
              disabled={isSaving}
            >
              {isSaving ? "‚è≥" : "‚úì"}
            </ActionButton>
            <ActionButton 
              variant="cancel" 
              onClick={handleCancelEdit} 
              title="Ï∑®ÏÜå"
              disabled={isSaving}
            >
              ‚úï
            </ActionButton>
          </EditButtonsContainer>
          {isSaving && (
            <div style={{ 
              fontSize: '12px', 
              color: '#666', 
              marginTop: '8px',
              textAlign: 'center'
            }}>
              Î©îÏãúÏßÄÎ•º ÏàòÏ†ïÌïòÍ≥† ÏÉàÎ°úÏö¥ ÏùëÎãµÏùÑ ÏÉùÏÑ±ÌïòÎäî Ï§ë...
            </div>
          )}
        </div>
      ) : (
        <>
          <MessageBubble
            isUser={isUser}
            personaColor={persona?.color}
          >
            {message.content}
          </MessageBubble>
          <MessageInfo>
            {!isUser && persona && (
              <span style={{ color: persona.color, fontWeight: 'bold' }}>
                {persona.name}
              </span>
            )}
            <span>{formatTime(message.timestamp)}</span>
          </MessageInfo>
          
          {/* Ïï°ÏÖò Î≤ÑÌäº ÌëúÏãú */}
          {showActionButtons && (
            <ActionButtonsContainer>
              {isUser ? (
                // ÏÇ¨Ïö©Ïûê Î©îÏãúÏßÄ: Î≥µÏÇ¨ + ÏàòÏ†ï Î≤ÑÌäºÎßå
                <>
                  <ActionButton variant="copy" onClick={onCopy} title="Î≥µÏÇ¨">
                    üìã
                  </ActionButton>
                  {onEdit && (
                    <ActionButton variant="edit" onClick={onEdit} title="ÏàòÏ†ï">
                      ‚úèÔ∏è
                    </ActionButton>
                  )}
                </>
              ) : (
                // LLM Î©îÏãúÏßÄ: Î≥µÏÇ¨ + Ìï¥/ÎÇò/Î¨∏ Î≤ÑÌäº
                <>
                  <ActionButton variant="copy" onClick={onCopy} title="Î≥µÏÇ¨">
                    üìã
                  </ActionButton>
                  <ActionButton variant="sun" onClick={onSunAction} title="Ìï¥">
                    ‚òÄÔ∏è
                  </ActionButton>
                  <ActionButton variant="person" onClick={onPersonAction} title="ÎÇò">
                    üë§
                  </ActionButton>
                  <ActionButton variant="document" onClick={onDocumentAction} title="Î¨∏">
                    üìÑ
                  </ActionButton>
                </>
              )}
            </ActionButtonsContainer>
          )}
        </>
      )}
    </MessageContainer>
  );
};

export default Message;
