import React, { useState } from 'react';
import styled from 'styled-components';
import SentenceVaultView from './SentenceVaultView';

interface VaultViewProps {
  onNavigateToPersona: (personaId: string, mode: 'sentence') => void;
  onNavigateToThread: (threadId: string, threadType: string) => void;
}

const VaultContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background: #f8f9fa;
`;

const VaultHeader = styled.div`
  background: #fff;
  border-bottom: 1px solid #dee2e6;
  padding: 20px 24px;
`;

const VaultTitle = styled.h1`
  font-size: 24px;
  font-weight: 600;
  color: #333;
  margin: 0;
`;

const TabContainer = styled.div`
  display: flex;
  background: #fff;
  border-bottom: 1px solid #dee2e6;
  padding: 0 24px;
`;

const Tab = styled.button<{ $isActive: boolean }>`
  background: none;
  border: none;
  padding: 16px 24px;
  font-size: 16px;
  font-weight: 500;
  color: ${props => props.$isActive ? '#007bff' : '#6c757d'};
  cursor: pointer;
  border-bottom: 2px solid ${props => props.$isActive ? '#007bff' : 'transparent'};
  transition: all 0.2s ease;
  
  &:hover {
    color: #007bff;
  }
`;

const ContentArea = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 24px;
`;

const ComingSoon = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 300px;
  color: #6c757d;
  font-size: 18px;
`;

const VaultView: React.FC<VaultViewProps> = ({
  onNavigateToPersona,
  onNavigateToThread
}) => {
  const [activeTab, setActiveTab] = useState<'sentences' | 'memos' | 'letters' | 'qa'>('sentences');

  const tabs = [
    { id: 'sentences' as const, label: 'Î¨∏Ïû•' },
    { id: 'memos' as const, label: 'Î©îÎ™®' },
    { id: 'letters' as const, label: 'Ìé∏ÏßÄ' },
    { id: 'qa' as const, label: 'QA ÏóÖÎç∞Ïù¥Ìä∏' }
  ];

  const renderContent = () => {
    switch (activeTab) {
      case 'sentences':
        return (
          <SentenceVaultView
            onNavigateToPersona={onNavigateToPersona}
            onNavigateToThread={onNavigateToThread}
          />
        );
      case 'memos':
      case 'letters':
      case 'qa':
        return (
          <ComingSoon>
            <div>üöß</div>
            <div>Í≥ß Ï†úÍ≥µÎê† ÏòàÏ†ïÏûÖÎãàÎã§</div>
          </ComingSoon>
        );
      default:
        return null;
    }
  };

  return (
    <VaultContainer>
      <VaultHeader>
        <VaultTitle>Ï†ÄÏû•Í≥†</VaultTitle>
      </VaultHeader>
      
      <TabContainer>
        {tabs.map(tab => (
          <Tab
            key={tab.id}
            $isActive={activeTab === tab.id}
            onClick={() => setActiveTab(tab.id)}
          >
            {tab.label}
          </Tab>
        ))}
      </TabContainer>
      
      <ContentArea>
        {renderContent()}
      </ContentArea>
    </VaultContainer>
  );
};

export default VaultView;
