import React, { useState } from 'react';
import styled from 'styled-components';
import { FaRegFileAlt, FaRegStickyNote, FaRegEnvelope, FaChevronLeft } from 'react-icons/fa';
import MemoVaultView from './MemoVaultView';
import SentenceVaultView from './SentenceVaultView';
import { Persona } from '../../shared/types';

interface VaultViewProps {
  personas: Record<string, Persona>;
  onNavigateToPersona: (personaId: string, mode: 'sentence') => void;
  onNavigateToThread: (threadId: string, threadType: string, interactionMessage?: string) => void;
  onNavigateToPersonaWithSentence: (personaId: string, mode: 'sentence', selectedSentence: string) => void;
}

const Container = styled.div`
  display: flex;
  height: 100vh;
  width: 100%;
  background-color: #f8f9fa;
`;

const Sidebar = styled.div<{ $isCollapsed: boolean }>`
  position: relative;
  width: ${props => props.$isCollapsed ? '80px' : '240px'};
  background-color: #ffffff;
  border-right: 1px solid #dee2e6;
  padding: 20px 0;
  display: flex;
  flex-direction: column;
  transition: width 0.3s ease;
`;

const SidebarHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 25px;
  margin-bottom: 30px;
  h2 {
    font-size: 1.5rem;
    font-weight: 700;
    color: #343a40;
    white-space: nowrap;
    overflow: hidden;
  }
`;

const ToggleButton = styled.button<{ $isCollapsed: boolean }>`
  background: #f1f3f5;
  border: 1px solid #dee2e6;
  border-radius: 50%;
  width: 28px;
  height: 28px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: #495057;
  transition: transform 0.3s ease, background-color 0.2s;

  &:hover {
    background: #e9ecef;
  }

  svg {
    transform: rotate(${props => props.$isCollapsed ? '180deg' : '0deg'});
    transition: transform 0.3s ease;
  }
`;

const TabButton = styled.button<{ $isActive: boolean; $isCollapsed: boolean }>`
  width: calc(100% - 40px);
  margin: 0 auto 10px auto;
  padding: 15px;
  border: none;
  border-radius: 8px;
  background-color: ${props => props.$isActive ? '#e9ecef' : 'transparent'};
  color: ${props => props.$isActive ? '#212529' : '#495057'};
  font-size: 1rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  justify-content: ${props => props.$isCollapsed ? 'center' : 'flex-start'};
  gap: 15px;

  span {
    opacity: ${props => props.$isCollapsed ? 0 : 1};
    white-space: nowrap;
    transition: opacity 0.2s ease;
  }

  svg {
    font-size: 1.2rem;
    min-width: 20px;
  }

  &:hover {
    background-color: #f1f3f5;
    color: #000;
  }
`;

const Content = styled.div`
  flex-grow: 1;
  overflow-y: auto;
`;

const VaultView: React.FC<VaultViewProps> = (props) => {
  const [activeTab, setActiveTab] = useState<'sentence' | 'memo' | 'letter'>('sentence');
  const [isCollapsed, setIsCollapsed] = useState(false);

  return (
    <Container>
      <Sidebar $isCollapsed={isCollapsed}>
        <SidebarHeader>
          {!isCollapsed && <h2>저장고</h2>}
          <ToggleButton onClick={() => setIsCollapsed(!isCollapsed)} $isCollapsed={isCollapsed}>
            <FaChevronLeft />
          </ToggleButton>
        </SidebarHeader>

        <TabButton $isActive={activeTab === 'sentence'} $isCollapsed={isCollapsed} onClick={() => setActiveTab('sentence')}>
          <FaRegFileAlt />
          <span>문장</span>
        </TabButton>
        <TabButton $isActive={activeTab === 'memo'} $isCollapsed={isCollapsed} onClick={() => setActiveTab('memo')}>
          <FaRegStickyNote />
          <span>메모</span>
        </TabButton>
        <TabButton $isActive={activeTab === 'letter'} $isCollapsed={isCollapsed} onClick={() => setActiveTab('letter')}>
          <FaRegEnvelope />
          <span>편지</span>
        </TabButton>
      </Sidebar>
      <Content>
        {activeTab === 'sentence' && <SentenceVaultView {...props} />}
        {activeTab === 'memo' && <MemoVaultView {...props} />}
        {activeTab === 'letter' && <div>편지 저장고는 준비 중입니다.</div>}
      </Content>
    </Container>
  );
};

export default VaultView;
