import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

// 타입 정의
interface StimulusQuote {
  id: string;
  content: string;
  order: number;
}

interface StimulusQuoteSet {
  quotes: StimulusQuote[];
}

interface PersonaInfo {
  name: string;
  theme: string;
  summary: string;
}

interface DailyStimulusItem {
  id: string;
  date: string;
  persona_name: string;
  main_theme: string;
  quote_sets: StimulusQuoteSet[];
  summary_quote: string;
  persona_info?: PersonaInfo[];
  created_at: string;
}

interface StimulusVaultItem {
  id: string;
  original_stimulus_id: string;
  saved_content: string;
  content_type: 'quote' | 'summary' | 'theme';
  source_date: string;
  persona_name: string;
  saved_at: string;
  tags: string[];
  original_stimulus?: DailyStimulusItem;
}

interface StimulusVaultViewProps {
  personas?: Record<string, any>;
}

// StimulusView와 동일한 스타일 컴포넌트들
const VaultContainer = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  position: relative;
  overflow: hidden;
`;

const VaultHeader = styled.div`
  padding: 20px 40px;
  background: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const VaultTitle = styled.h1`
  font-size: 28px;
  font-weight: 700;
  color: #333;
  margin: 0;
`;

const VaultStats = styled.div`
  font-size: 16px;
  color: #666;
`;

const ContentArea = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 40px;
  position: relative;
`;

const StimulusCard = styled.div`
  background: linear-gradient(180deg, #ffffff 0%, #f8f9fa 100%);
  border-radius: 24px;
  padding: 80px 70px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.1);
  max-width: 900px;
  width: 100%;
  position: relative;
  backdrop-filter: blur(10px);
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 50px;
  gap: 16px;
`;

const DateDisplay = styled.div`
  font-size: 18px;
  color: #666;
  font-weight: 500;
`;

const PersonaName = styled.div`
  font-size: 18px;
  color: #333;
  font-weight: 600;
`;

const MainThemeBox = styled.div`
  background: linear-gradient(135deg, #ffffff 0%, #f0f0f0 100%);
  border-radius: 16px;
  padding: 28px;
  margin-bottom: 30px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.08);
  position: relative;
  z-index: 2;
`;

const MainTheme = styled.h1`
  font-size: 28px;
  font-weight: bold;
  color: #222;
  text-align: center;
  margin: 0;
  line-height: 1.4;
`;

const QuotesContainer = styled.div`
  position: relative;
  margin-bottom: 30px;
`;

const VerticalLine = styled.div`
  position: absolute;
  left: 50%;
  top: 0;
  bottom: 0;
  width: 2px;
  background: #ddd;
  transform: translateX(-50%);
  z-index: 1;
`;

const QuoteSetContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0;
`;

const QuoteBox = styled.div<{ $isVisible: boolean; $isFirst?: boolean; $isLast?: boolean }>`
  background: linear-gradient(135deg, #ffffff 0%, #f0f0f0 100%);
  border-radius: ${props => {
    if (props.$isFirst) return '16px 16px 0 0';
    if (props.$isLast) return '0 0 16px 16px';
    return '0';
  }};
  padding: 24px 28px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.08);
  position: relative;
  z-index: 2;
  transition: all 0.3s ease;
  opacity: ${props => props.$isVisible ? 1 : 0};
  transform: translateY(${props => props.$isVisible ? '0' : '20px'});
  border: 1px solid #e8e8e8;
  border-top: ${props => !props.$isFirst ? 'none' : '1px solid #e8e8e8'};
  
  &:hover {
    box-shadow: 0 12px 32px rgba(0, 0, 0, 0.12);
    .action-button {
      opacity: 1;
    }
  }
`;

const QuoteContent = styled.div`
  font-size: 18px;
  line-height: 1.6;
  color: #333;
  text-align: center;
  padding-right: 40px;
`;

const ActionButton = styled.button`
  position: absolute;
  right: 12px;
  top: 50%;
  transform: translateY(-50%);
  width: 32px;
  height: 32px;
  border-radius: 50%;
  border: 2px solid #ddd;
  background: white;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  opacity: 0;
  transition: all 0.2s ease;
  font-size: 14px;
  color: #666;
  
  &:hover {
    border-color: #999;
    background: #f8f9fa;
  }
`;

const SummaryQuoteBox = styled.div`
  background: linear-gradient(135deg, #ffffff 0%, #f0f0f0 100%);
