if(onRefreshThreads){onRefreshThreads();}showCopyToast('Î©îÏãúÏßÄÍ∞Ä Î¨∏Ïû• Ïä§Î†àÎìúÏóê Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§');}else{showCopyToast('Î¨∏Ïû• Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');}}catch(error){console.error('Î¨∏Ïû• Ï†ÄÏû• Ï§ë Ïò§Î•ò:',error);showCopyToast('Î¨∏Ïû• Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');}};return/*#__PURE__*/_jsxs(Container,{children:[/*#__PURE__*/_jsx(Toast,{show:showToast,children:toastMessage}),/*#__PURE__*/_jsxs(ChatSection,{children:[/*#__PURE__*/_jsxs(ChatMessages,{ref:chatMessagesRef,children:[messages.length===0?/*#__PURE__*/_jsxs(EmptyChat,{children:[/*#__PURE__*/_jsx("div",{style:{fontSize:'24px',marginBottom:'8px'},children:"💬"}),/*#__PURE__*/_jsx("div",{children:"해석에 대해 더 자세히 질문해보세요"}),/*#__PURE__*/_jsxs("div",{style:{fontSize:'12px',marginTop:'4px'},children:[personaName,"와 대화를 나눌 수 있습니다"]})]}):messages.map((message,index)=>message.role==='user'?/*#__PURE__*/_jsx(Message,{message:_objectSpread(_objectSpread({},message),{},{persona_id:undefined,persona_name:undefined}),personas:{},showActionButtons:true,onCopy:()=>handleCopyMessage(message.content),onEdit:()=>handleStartEdit(index),isEditing:editingMessageIndex===index,onEditSave:newContent=>handleEditMessage(index,newContent),onEditCancel:handleCancelEdit},"interpretation_".concat(index)):/*#__PURE__*/_jsx(SelectableMessage,{message:_objectSpread(_objectSpread({},message),{},{persona_id:'current_persona',persona_name:personaName}),personas:{current_persona:{name:personaName,description:'',color:'#6c757d',prompt:'',category:'',subcategory:''}},selectedSentences:isSentenceModeActive?selectedSentences:new Set(),highlightedSentences:highlightedSentences,memos:memos,onToggleSelect:isSentenceModeActive?handleToggleSelect:()=>{},onMemoChange:handleMemoChange,onDeleteMemo:handleDeleteMemo,showSentenceSelector:isSentenceModeActive,showActionButtons:true,onCopy:()=>handleCopyMessage(message.content),onSunAction:handleSunAction,onPersonAction:handlePersonAction,onDocumentAction:handleDocumentAction},"interpretati