      if (response.data && response.data.persona_id && response.data.interpretation) {
        console.log('Ìï¥ÏÑù Ï†ÄÏû• ÏÑ±Í≥µ:', response.data);
        // Ïä§Î†àÎìú ÏÉàÎ°úÍ≥†Ïπ®
