    try {
      // chatApiÎ•º ÌÜµÌï¥ Î©îÏãúÏßÄ ÎÇ¥Ïö©ÏùÑ ÎÇòÏïÑÍ∞ÄÍ∏∞ Ïä§Î†àÎìúÏóê Ï†ÄÏû•
      const { chatApi } = await import('../../shared/api/chatApi');
      const response = await chatApi.saveCurrentAsProceed(personaId, messageContent);

