import React from 'react';
import TrainingChatView from './TrainingChatView';
import { TrainingThread } from '../../shared/types';

interface SentenceViewProps {
  personaId: string;
  personaName: string;
  sentenceContent: string;
  threads: TrainingThread[];
  onThreadUpdate: (threads: TrainingThread[]) => void;
  selectedThread?: TrainingThread | null;
  onRefreshThreads?: () => void;
}

const SentenceView: React.FC<SentenceViewProps> = (props) => {
  return (
    <TrainingChatView
      viewType="sentence"
      {...props} // 나머지 props를 그대로 전달
      initialContent={props.sentenceContent}
      selectedThread={props.selectedThread || null}
    />
  );
};

export default SentenceView;
