import { API_BASE_URL } from '../../shared/api/chatApi';

export interface SentenceVaultItem {
  id: string;
  sentence: string;
  source_message_id: string;
  source_conversation_id?: string;
  created_at: string;
  tags: string[];
  metadata?: Record<string, any>;
}

export interface SentenceVaultRequest {
  sentences: string[];
  source_message_id: string;
  source_conversation_id?: string;
  tags?: string[];
}

export interface SentenceVaultResponse {
  success: boolean;
  message: string;
  saved_items: SentenceVaultItem[];
}

export interface SentenceMemo {
  id: string;
  sentence_id: string;
  content: string;
  created_at: string;
  updated_at: string;
}

export interface SentenceMemoRequest {
  sentence_id: string;
  content: string;
}

export interface SentenceMemoResponse {
  success: boolean;
  message: string;
  memo?: SentenceMemo;
}

class SentenceApi {
  // 저장고 관련 API
  async saveSentencesToVault(request: SentenceVaultRequest): Promise<SentenceVaultResponse> {
    try {
      const response = await fetch(`${API_BASE_URL}/vault/sentences`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(request),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || '저장고 저장 실패');
      }

      return await response.json();
    } catch (error) {
      console.error('저장고 저장 오류:', error);
      throw error;
    }
  }

  async getVaultSentences(): Promise<SentenceVaultItem[]> {
    try {
      const response = await fetch(`${API_BASE_URL}/vault/sentences`);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || '저장고 조회 실패');
      }

      return await response.json();
    } catch (error) {
      console.error('저장고 조회 오류:', error);
      throw error;
    }
  }

  async deleteVaultSentence(sentenceId: string): Promise<{ success: boolean; message: string }> {
    try {
      const response = await fetch(`${API_BASE_URL}/vault/sentences/${sentenceId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || '문장 삭제 실패');
      }

      return await response.json();
    } catch (error) {
      console.error('문장 삭제 오류:', error);
      throw error;
    }
  }

  // 메모 관련 API
  async createOrUpdateMemo(request: SentenceMemoRequest): Promise<SentenceMemoResponse> {
    try {
      const response = await fetch(`${API_BASE_URL}/memos`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(request),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || '메모 저장 실패');
      }

      return await response.json();
    } catch (error) {
      console.error('메모 저장 오류:', error);
      throw error;
    }
  }

  async getMemo(sentenceId: string): Promise<SentenceMemo | null> {
    try {
      const response = await fetch(`${API_BASE_URL}/memos/${sentenceId}`);

      if (response.status === 404) {
        return null;
      }

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || '메모 조회 실패');
      }

      const result = await response.json();
      return result || null;
    } catch (error) {
      console.error('메모 조회 오류:', error);
      throw error;
    }
  }

  async deleteMemo(sentenceId: string): Promise<{ success: boolean; message: string }> {
    try {
      const response = await fetch(`${API_BASE_URL}/memos/${sentenceId}`, {
        method: 'DELETE',
      });

      // 404는 이미 삭제된 것으로 간주하여 성공으로 처리
      if (response.status === 404) {
        return { success: true, message: '메모가 이미 삭제되었습니다' };
      }

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || '메모 삭제 실패');
      }

      return await response.json();
    } catch (error) {
      console.error('메모 삭제 오류:', error);
      throw error;
    }
  }
}

export const sentenceApi = new SentenceApi();
