const InterpretationView: React.FC<InterpretationViewProps> = ({
  interpretation,
  personaName,
  onSendMessage,
  onEditMessage,
  isLoading,
  messages,
  onGenerateNewInterpretation,
  onSwitchToMode,
  personaId,
  onRefreshThreads,
}) => {
  const chatMessagesRef = useRef<HTMLDivElement>(null);
  const messageInputRef = useRef<MessageInputRef>(null);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [editingMessageIndex, setEditingMessageIndex] = useState<number | null>(null);

  // 문장 선택 관련 상태
  const [selectedSentences, setSelectedSentences] = useState<Set<string>>(new Set());
  const [highlightedSentences, setHighlightedSentences] = useState<Set<string>>(new Set());
  const [memos, setMemos] = useState<Record<string, string>>({});
  const [isSentenceModeActive, setIsSentenceModeActive] = useState(false);

  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [messages]);

  // 토스트 메시지 표시 함수
  const showCopyToast = (message: string) => {
    setToastMessage(message);
    setShowToast(true);
    setTimeout(() => setShowToast(false), 3000);
  };

  // 텍스트 복사 유틸리티 함수
  const copyToClipboard = async (text: string, successMessage: string) => {
    try {
      await navigator.clipboard.writeText(text);
      showCopyToast(successMessage);
    } catch (error) {
      console.error('복사 실패:', error);
      // 대체 복사 방법
      try {
        const textArea = document.createElement('textarea');
        textArea.value = text;
        textArea.style.position = 'fixed';
        textArea.style.left = '-999999px';
        textArea.style.top = '-999999px';
        document.body.appendChild(textArea);
        textArea.focus();
        textArea.select();
        document.execCommand('copy');
        document.body.removeChild(textArea);
        showCopyToast(successMessage);
      } catch (fallbackError) {
        console.error('대체 복사 방법도 실패:', fallbackError);
        showCopyToast('복사 실패');
      }
    }
  };

  // 메시지 복사 기능
  const handleCopyMessage = async (messageContent: string) => {
    await copyToClipboard(messageContent, '메시지가 복사되었습니다');
  };

  // 메시지 수정 시작
  const handleStartEdit = (messageIndex: number) => {
    setEditingMessageIndex(messageIndex);
  };

  // 메시지 수정 완료
  const handleEditMessage = async (messageIndex: number, newContent: string) => {
    if (onEditMessage) {
      const success = await onEditMessage(messageIndex, newContent);
      if (success) {
        setEditingMessageIndex(null);
        showCopyToast('메시지가 수정되었습니다');
      } else {
        showCopyToast('메시지 수정에 실패했습니다');
      }
    }
  };

  // 메시지 수정 취소
  const handleCancelEdit = () => {
    setEditingMessageIndex(null);
  };

  // 문장 선택 핸들러들
  const handleToggleSelect = (sentenceId: string) => {
    setSelectedSentences(prev => {
      const newSet = new Set(prev);
      if (newSet.has(sentenceId)) {
        newSet.delete(sentenceId);
      } else {
        newSet.add(sentenceId);
      }
      return newSet;
    });
  };

  const handleMemoChange = async (sentenceId: string, memo: string) => {
    try {
      // 로컬 상태에서 메모 업데이트 (즉시 반영)
      setMemos(prev => ({
        ...prev,
        [sentenceId]: memo
      }));

      // 백엔드 API 호출 (비동기, 실패해도 로컬 업데이트는 유지)
      sentenceApi.createOrUpdateMemo({
        sentence_id: sentenceId,
        content: memo
      }).catch(error => {
        console.warn('백엔드 메모 저장 실패 (로컬 저장은 성공):', error);
      });
    } catch (error) {
      console.error('메모 저장 실패:', error);
      showCopyToast('메모 저장에 실패했습니다');
    }
  };

  const handleDeleteMemo = async (sentenceId: string) => {
    try {
      // 로컬 상태에서 메모 삭제
      setMemos(prev => {
        const newMemos = { ...prev };
        delete newMemos[sentenceId];
        return newMemos;
      });
      setHighlightedSentences(prev => {
        const newSet = new Set(prev);
        newSet.delete(sentenceId);
        return newSet;
      });

      // 백엔드 API 호출 (비동기, 실패해도 로컬 삭제는 유지)
      sentenceApi.deleteMemo(sentenceId).catch(error => {
        console.warn('백엔드 메모 삭제 실패 (로컬 삭제는 성공):', error);
      });

      showCopyToast('메모가 삭제되었습니다');
    } catch (error) {
      console.error('메모 삭제 실패:', error);
      showCopyToast('메모 삭제에 실패했습니다');
    }
  };

  // 플로팅 액션 버튼 메뉴 액션들
  const handleMenuAction = async (action: 'sendToInput' | 'saveToVault' | 'addMemo' | 'highlight' | 'copy' | 'generateProceed' | 'generateSentence') => {
    const selectedIds = Array.from(selectedSentences);
    const selectedTexts = selectedIds.map(id => {
      // sentenceId에서 실제 문장 텍스트를 찾아내는 로직
      const [timestamp, messageIndex, sentenceIndex] = id.split('_');
      const message = messages.find(m => m.timestamp === timestamp);
      if (message) {
        const sentences = message.content.split(/[\n\.]+/).map(s => s.trim()).filter(s => s.length > 0);
        return sentences[parseInt(sentenceIndex)] || '';
      }
      return '';
    }).filter(text => text.length > 0);

    switch (action) {
      case 'sendToInput':
        if (messageInputRef.current && selectedTexts.length > 0) {
          const formattedText = selectedTexts.map(text => `"${text}"`).join(', ');
          messageInputRef.current.insertText(formattedText);
        }
        break;

      case 'saveToVault':
        try {
          await sentenceApi.saveSentencesToVault({
            sentences: selectedTexts,
            source_message_id: 'interpretation_chat', // 임시 ID
            source_conversation_id: undefined,
            tags: ['interpretation']
          });
          selectedIds.forEach(id => {
            setHighlightedSentences(prev => new Set(prev).add(id));
          });
          showCopyToast('저장고에 저장되었습니다');
        } catch (error) {
          console.error('저장고 저장 실패:', error);
          showCopyToast('저장고 저장에 실패했습니다');
        }
        break;

      case 'addMemo':
        selectedIds.forEach(id => {
          // 로컬 상태에서 메모 생성 (즉시 반영)
          setMemos(prev => ({ ...prev, [id]: '' }));
          setHighlightedSentences(prev => new Set(prev).add(id));

          // 백엔드 API 호출 (비동기, 실패해도 로컬 생성은 유지)
          sentenceApi.createOrUpdateMemo({
            sentence_id: id,
            content: ''
          }).catch(error => {
            console.warn('백엔드 메모 생성 실패 (로컬 생성은 성공):', error);
          });
        });
        break;

      case 'highlight':
        selectedIds.forEach(id => {
          setHighlightedSentences(prev => {
            const newSet = new Set(prev);
            if (newSet.has(id)) {
              newSet.delete(id);
            } else {
              newSet.add(id);
            }
            return newSet;
          });
        });
        break;

      case 'copy':
        if (selectedTexts.length > 0) {
          copyToClipboard(selectedTexts.join(' '), '선택된 문장이 복사되었습니다');
        }
        break;

      case 'generateProceed':
        showCopyToast('나아가기 생성 기능을 사용하려면 하단 메뉴를 이용해주세요');
        break;

      case 'generateSentence':
        showCopyToast('문장 생성 기능을 사용하려면 하단 메뉴를 이용해주세요');
        break;
    }

    // 모든 선택 해제
    setSelectedSentences(new Set());
  };

  // 문장선택 모드 토글
  const handleToggleSentenceMode = () => {
    setIsSentenceModeActive(prev => !prev);
  };

  // 향후 확장을 위한 버튼 핸들러들
  const handleSunAction = async () => {
    if (onGenerateNewInterpretation) {
      onGenerateNewInterpretation();
    }
  };

  const handlePersonAction = async () => {
    // 나아가기 프롬프트를 입력창에 삽입하고 현재 Assistant의 마지막 채팅 내용을 나아가기 사이드바에 저장
    if (!personaId) return;

    const proceedPrompt = "좋아. 너의 답변에 근거하여, 내가 나아가야 할 방향은?";

    // 입력창에 프롬프트 삽입
    if (messageInputRef.current) {
      messageInputRef.current.insertText(proceedPrompt);
    }

    showCopyToast('나아가기 프롬프트가 입력창에 추가되었습니다');

    try {
      // 현재 Assistant의 마지막 채팅 내용만 사용
      let contentToSave = interpretation; // 기본값: 해석 내용

      if (messages && messages.length > 0) {
        // Assistant의 마지막 메시지 찾기
        const lastAssistantMessage = messages
          .slice()
          .reverse()
          .find(msg => msg.role === 'assistant');

        if (lastAssistantMessage) {
          contentToSave = lastAssistantMessage.content;
        }
      }

      // chatApi를 통해 Assistant의 마지막 채팅 내용을 나아가기 사이드바에 저장
      const { chatApi } = await import('../../shared/api/chatApi');
      const response = await chatApi.saveCurrentAsProceed(personaId, contentToSave);

      if (response.data) {
        // 스레드 새로고침
        if (onRefreshThreads) {
          onRefreshThreads();
        }
        showCopyToast('현재 Assistant 응답이 나아가기에 저장되었습니다');
      }
    } catch (error) {
      console.error('나아가기 저장 중 오류:', error);
      showCopyToast('나아가기 저장에 실패했습니다');
    }
  };

  const handleDocumentAction = async () => {
    // 문장 프롬프트를 입력창에 삽입하고 현재 Assistant의 마지막 채팅 내용을 문장 사이드바에 저장
    if (!personaId) return;

    const sentencePrompt = "네 답변을 세 문장으로 통찰적, 명제형, 핵심적으로 짧게 요약 (in bullet style)";

    // 입력창에 프롬프트 삽입
    if (messageInputRef.current) {
      messageInputRef.current.insertText(sentencePrompt);
    }

    showCopyToast('문장 프롬프트가 입력창에 추가되었습니다');

    try {
      // 현재 Assistant의 마지막 채팅 내용만 사용
      let contentToSave = interpretation; // 기본값: 해석 내용

      if (messages && messages.length > 0) {
        // Assistant의 마지막 메시지 찾기
        const lastAssistantMessage = messages
          .slice()
          .reverse()
          .find(msg => msg.role === 'assistant');

        if (lastAssistantMessage) {
          contentToSave = lastAssistantMessage.content;
        }
      }

      // chatApi를 통해 Assistant의 마지막 채팅 내용을 문장 사이드바에 저장
      const { chatApi } = await import('../../shared/api/chatApi');
      const response = await chatApi.saveCurrentAsSentence(personaId, contentToSave);

      if (response.data) {
        // 스레드 새로고침
        if (onRefreshThreads) {
          onRefreshThreads();
        }
        showCopyToast('현재 Assistant 응답이 문장에 저장되었습니다');
      }
    } catch (error) {
      console.error('문장 저장 중 오류:', error);
      showCopyToast('문장 저장에 실패했습니다');
    }
  };

  return (
    <Container>
      <Toast show={showToast}>
        {toastMessage}
      </Toast>

      <ChatSection>
        <ChatMessages ref={chatMessagesRef}>
          {messages.length === 0 ? (
            <EmptyChat>
              <div style={{ fontSize: '24px', marginBottom: '8px' }}>💬</div>
              <div>해석에 대해 더 자세히 질문해보세요</div>
              <div style={{ fontSize: '12px', marginTop: '4px' }}>
                {personaName}와 대화를 나눌 수 있습니다
              </div>
            </EmptyChat>
          ) : (
            messages.map((message, index) => 
              message.role === 'user' ? (
                <Message
                  key={`interpretation_${index}`}
                  message={{
                    ...message,
                    persona_id: undefined,
                    persona_name: undefined,
                  }}
                  personas={{}}
                  showActionButtons={true}
                  onCopy={() => handleCopyMessage(message.content)}
                  onEdit={() => handleStartEdit(index)}
                  isEditing={editingMessageIndex === index}
                  onEditSave={(newContent) => handleEditMessage(index, newContent)}
                  onEditCancel={handleCancelEdit}
                />
              ) : (
                <SelectableMessage
                  key={`interpretation_${index}`}
                  message={{
                    ...message,
                    persona_id: 'current_persona',
                    persona_name: personaName,
                  }}
                  personas={{
                    current_persona: {
                      name: personaName,
                      description: '',
                      color: '#6c757d',
                      prompt: '',
                      category: '',
                      subcategory: ''
                    }
                  }}
                  selectedSentences={isSentenceModeActive ? selectedSentences : new Set()}
                  highlightedSentences={highlightedSentences}
                  memos={memos}
                  onToggleSelect={isSentenceModeActive ? handleToggleSelect : () => {}}
                  onMemoChange={handleMemoChange}
                  onDeleteMemo={handleDeleteMemo}
                  showSentenceSelector={isSentenceModeActive}
                  showActionButtons={true}
                  onCopy={() => handleCopyMessage(message.content)}
                  onSunAction={handleSunAction}
                  onPersonAction={handlePersonAction}
                  onDocumentAction={handleDocumentAction}
                />
              )
            )
          )}

          {isLoading && (
            <LoadingMessage 
              personaName={personaName}
              personaColor="#6c757d"
            />
          )}
        </ChatMessages>

        <ChatInputSection>
          <MessageInput
            ref={messageInputRef}
            onSendMessage={onSendMessage}
            disabled={isLoading}
            placeholder={`${personaName}에게 해석에 대해 질문해보세요...`}
            onToggleSentenceMode={handleToggleSentenceMode}
            isSentenceModeActive={isSentenceModeActive}
            hasSelectedSentences={selectedSentences.size > 0}
            currentInterpretation={interpretation}
            personaId={personaId}
            onGenerateProceed={handlePersonAction}
            onGenerateSentence={handleDocumentAction}
            currentChatMessages={messages}
            onRefreshThreads={onRefreshThreads}
          />
        </ChatInputSection>
      </ChatSection>

      <FloatingActionButton
        show={isSentenceModeActive && selectedSentences.size > 0}
        onMenuAction={handleMenuAction}
        personaId={personaId}
        currentInterpretation={interpretation}
      />
    </Container>
  );
};

export default InterpretationView;
