const VerbalizationView: React.FC<VerbalizationViewProps> = ({
  selectedThread,
  threads,
  onThreadUpdate,
  onCreateNewThread
}) => {
  const [isLoading, setIsLoading] = useState(false);
  const chatMessagesRef = useRef<HTMLDivElement>(null);
  const messageInputRef = useRef<MessageInputRef>(null);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [editingMessageIndex, setEditingMessageIndex] = useState<number | null>(null);

  // 텍스트 선택 관련 상태
  const [selectedText, setSelectedText] = useState('');
  const [showFAB, setShowFAB] = useState(false);

  // 로컬 메시지 상태 - 사용자 메시지를 즉시 UI에 반영하기 위함
  const [localMessages, setLocalMessages] = useState<Array<{
    role: 'user' | 'assistant';
    content: string;
    timestamp: string;
    persona_name?: string;
  }>>([]);

  // selectedThread가 변경될 때 localMessages 동기화
  useEffect(() => {
    if (selectedThread) {
      setLocalMessages(selectedThread.messages || []);
    } else {
      setLocalMessages([]);
    }
  }, [selectedThread]);

  // 스크롤을 맨 아래로 이동 - localMessages 기준으로 변경
  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [localMessages, isLoading]);

  // 메시지 전송 처리
  const handleSendMessage = async (message: string): Promise<boolean> => {
    if (!message.trim() || isLoading) return false;

    // 1단계: 사용자 메시지를 즉시 UI에 추가
    const userMessage = {
      role: 'user' as const,
      content: message.trim(),
      timestamp: new Date().toISOString(),
    };

    setLocalMessages(prev => [...prev, userMessage]);
    setIsLoading(true);

    try {
      console.log('=== 언어화 메시지 전송 ===');
      console.log('selectedThread:', selectedThread);
      console.log('message:', message);

      // 2단계: API 호출 - 스레드 ID 전달 (없으면 undefined)
      const response = await chatApi.chatWithVerbalization(
        message,
        selectedThread?.id // 스레드가 없으면 undefined 전달
      );

      console.log('API 응답 전체:', response);
      console.log('응답 데이터:', response.data);
      console.log('응답 에러:', response.error);

      if (response.data) {
        console.log('메시지 전송 성공');
        console.log('응답 구조:', {
          thread_id: response.data.thread_id,
          user_message: response.data.user_message,
          response: response.data.response,
          thread_updated_at: response.data.thread_updated_at
        });

        // 3단계: AI 응답을 로컬 메시지에 추가
        const assistantMessage = {
          role: 'assistant' as const,
          content: response.data.response.content,
          timestamp: response.data.response.timestamp,
          persona_name: '언어화 도우미',
        };

        setLocalMessages(prev => [...prev, assistantMessage]);

        // 4단계: 백엔드에서 새 스레드가 생성되었거나 기존 스레드가 업데이트된 경우
        // 스레드 목록과 선택된 스레드를 새로고침
        await onThreadUpdate();

        // 새로 생성된 스레드가 선택되도록 추가 처리
        if (!selectedThread && response.data.thread_id) {
          console.log('새 스레드 생성됨, 선택 업데이트 필요:', response.data.thread_id);
          // 약간의 지연 후 스레드 목록 재로드하여 새 스레드 선택
          setTimeout(async () => {
            await onThreadUpdate();
          }, 100);
        }

        return true;
      } else {
        // 실패 시 사용자 메시지 제거
        setLocalMessages(prev => prev.slice(0, -1));
        console.error('메시지 전송 실패:', response.error);
        showCopyToast(`언어화 채팅 실패: ${response.error}`);
        return false;
      }
    } catch (error) {
      // 오류 시 사용자 메시지 제거
      setLocalMessages(prev => prev.slice(0, -1));
      console.error('메시지 전송 오류:', error);
      showCopyToast(`네트워크 오류: ${error}`);
      return false;
    } finally {
      setIsLoading(false);
    }
  };

  // 새 스레드 생성 처리
  const handleCreateNewThread = async () => {
    try {
      console.log('=== 새 스레드 생성 시작 ===');

      // 1단계: 로컬 메시지 초기화 (새 대화 준비)
      setLocalMessages([]);

      // 2단계: 새 스레드 생성
      console.log('새 스레드 생성 중...');

      // 3단계: 상위 컴포넌트에 새 스레드 생성 요청
      await onCreateNewThread();

      // 4단계: 성공 메시지 표시
      showCopyToast('새 대화가 시작되었습니다!');

      console.log('새 스레드 생성 및 선택 완료');
    } catch (error) {
      console.error('새 스레드 생성 오류:', error);
      showCopyToast('새 대화 생성에 실패했습니다.');
    }
  };

  // 토스트 메시지 표시 함수
  const showCopyToast = (message: string) => {
    setToastMessage(message);
    setShowToast(true);
    setTimeout(() => setShowToast(false), 3000);
  };

  // 텍스트 복사 유틸리티 함수
  const copyToClipboard = async (text: string, successMessage: string) => {
    try {
      await navigator.clipboard.writeText(text);
      showCopyToast(successMessage);
    } catch (error) {
      console.error('복사 실패:', error);
      showCopyToast('복사 실패');
    }
  };

  // 메시지 복사 기능
  const handleCopyMessage = async (messageContent: string) => {
    await copyToClipboard(messageContent, '메시지가 복사되었습니다');
  };

  // 메시지 수정 관련 함수들
  const handleStartEdit = (messageIndex: number) => {
    setEditingMessageIndex(messageIndex);
  };

  const handleEditMessage = async (messageIndex: number, newContent: string) => {
    // 언어화에서는 메시지 수정 기능을 제공하지 않음
    // 필요시 추후 구현
    return false;
  };

  const handleCancelEdit = () => {
    setEditingMessageIndex(null);
  };

  // 액션 버튼 핸들러들 (언어화에서는 간단하게 구현)
  const handleSunAction = () => {
    showCopyToast('언어화에서는 해석 기능을 제공하지 않습니다');
  };

  const handlePersonAction = () => {
    const proceedPrompt = "이 감정이나 생각에 대해 더 깊이 탐구해보고 싶어요.";
    if (messageInputRef.current) {
      messageInputRef.current.insertText(proceedPrompt);
    }
    showCopyToast('탐구 프롬프트가 입력창에 추가되었습니다');
  };

  const handleDocumentAction = () => {
    if (selectedThread && selectedThread.messages.length > 0) {
      const lastAssistantMessage = selectedThread.messages
        .slice()
        .reverse()
        .find(msg => msg.role === 'assistant');

      if (lastAssistantMessage) {
        copyToClipboard(lastAssistantMessage.content, '마지막 응답이 복사되었습니다');
      } else {
        showCopyToast('복사할 응답이 없습니다');
      }
    }
  };

  // 텍스트 선택 핸들러
  const handleTextSelection = () => {
    const selection = window.getSelection();
    if (selection && selection.toString().trim()) {
      const selectedText = selection.toString().trim();
      setSelectedText(selectedText);
      setShowFAB(true);
    } else {
      setSelectedText('');
      setShowFAB(false);
    }
  };

  // 선택 해제 핸들러
  const handleClearSelection = () => {
    setSelectedText('');
    setShowFAB(false);
    if (window.getSelection) {
      window.getSelection()?.removeAllRanges();
    }
  };

  // FloatingActionButton 메뉴 액션 핸들러
  const handleFABMenuAction = async (action: 'sendToInput' | 'saveToVault' | 'addMemo' | 'highlight' | 'copy') => {
    switch (action) {
      case 'sendToInput':
        if (messageInputRef.current && selectedText) {
          messageInputRef.current.insertText(selectedText);
          showCopyToast('선택한 텍스트가 입력창에 추가되었습니다');
        }
        break;

      case 'copy':
        if (selectedText) {
          await copyToClipboard(selectedText, '선택한 텍스트가 복사되었습니다');
        }
        break;

      case 'saveToVault':
        // 언어화에서는 저장고 기능 미제공
        showCopyToast('언어화에서는 저장고 기능을 제공하지 않습니다');
        break;

      case 'addMemo':
        // 언어화에서는 메모 기능 미제공
        showCopyToast('언어화에서는 메모 기능을 제공하지 않습니다');
        break;

      case 'highlight':
        // 언어화에서는 하이라이트 기능 미제공
        showCopyToast('언어화에서는 하이라이트 기능을 제공하지 않습니다');
        break;
    }

    // 액션 실행 후 선택 해제
    handleClearSelection();
  };

  // 채팅 메시지 영역에 이벤트 리스너 등록
  useEffect(() => {
    const chatElement = chatMessagesRef.current;
    if (chatElement) {
      const handleMouseUp = () => {
        setTimeout(handleTextSelection, 10); // 약간의 지연을 주어 selection이 완료된 후 처리
      };

      chatElement.addEventListener('mouseup', handleMouseUp);
      chatElement.addEventListener('touchend', handleMouseUp);

      return () => {
        chatElement.removeEventListener('mouseup', handleMouseUp);
        chatElement.removeEventListener('touchend', handleMouseUp);
      };
    }
  }, []);

  // 다른 곳 클릭 시 선택 해제
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      const target = event.target as HTMLElement;
      // FAB나 메시지 영역이 아닌 곳 클릭 시 선택 해제
      if (chatMessagesRef.current && !chatMessagesRef.current.contains(target) && !target.closest('[data-fab]')) {
        handleClearSelection();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <Container>
      {/* 헤더 */}
      <HeaderSection>
        <HeaderTitle>언어화</HeaderTitle>
        <NewThreadButton onClick={handleCreateNewThread}>
          새 대화 시작
        </NewThreadButton>
      </HeaderSection>

      {/* 채팅 영역 */}
      <ChatSection>
        <ChatMessages ref={chatMessagesRef}>
          {localMessages.length === 0 ? (
            <EmptyChat>
              <EmptyIcon>💭</EmptyIcon>
              <div>
                <strong>언어화는 막연하고 뭔지 모를 감정이나 생각을 말로 표현함으로써<br />
                그것을 인식하고 만들어 가는 것입니다.</strong>
                <br /><br />
                자유롭게 당신의 생각을 말로 내뱉으세요!
                <br /><br />
                어떤 것이든 좋습니다. 지금 느끼고 있는 감정이나 생각을 편안하게 말씀해 주세요.
              </div>
            </EmptyChat>
          ) : (
            localMessages.map((message, index) => (
              <Message
                key={`${message.role}-${index}`}
                message={message}
                personas={{}} // 언어화에서는 페르소나 정보 불필요
                showActionButtons={true}
                showThreeActionButtons={false}
                onCopy={() => handleCopyMessage(message.content)}
                onEdit={message.role === 'user' ? () => handleStartEdit(index) : undefined}
                onEditSave={(newContent) => handleEditMessage(index, newContent)}
                onEditCancel={handleCancelEdit}
                isEditing={editingMessageIndex === index}
              />
            ))
          )}

          {/* 로딩 메시지 */}
          {isLoading && (
            <LoadingMessage 
              personaName="언어화 도우미"
              personaColor="#666"
            />
          )}
        </ChatMessages>

        {/* 입력창 */}
        <ChatInputSection>
          <MessageInput
            ref={messageInputRef}
            onSendMessage={handleSendMessage}
            disabled={isLoading}
            placeholder="언어화는 막연하고 뭔지 모를 감정이나 생각을 말로 표현함으로써 그것을 인식하고 만들어 가는 것입니다. 자유롭게 당신의 생각을 말로 내뱉으세요!"
          />
        </ChatInputSection>
      </ChatSection>

      {/* 토스트 메시지 */}
      <Toast show={showToast}>
        {toastMessage}
      </Toast>

      {/* FloatingActionButton */}
      <FloatingActionButton
        show={showFAB}
        onMenuAction={handleFABMenuAction}
        personaId="verbalization"
        currentInterpretation=""
      />
    </Container>
  );
};

export default VerbalizationView;
