  onClearError: () => void;
  selectedPersonaItem?: string | null; // í›ˆìŠµ ëª¨ë“œ í™•ì¸ìš©
  onSelectPersona?: (personaId: string) => void; // í˜ë¥´ì†Œë‚˜ ì„ íƒ í•¸ë“¤ëŸ¬
