  onInterpretationMessage: (message: string) => Promise<boolean>; // í•´ì„ ì±„íŒ… ë©”ì‹œì§€ í•¸ë“¤ëŸ¬
  onUpdateInterpretation?: (updatedInterpretation: {
    personaId: string;
    personaName: string;
    content: string;
    messages: Array<{
      role: 'user' | 'assistant';
