import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import MessageInput, { MessageInputRef } from '../../shared/components/MessageInput';
import Message from '../../shared/components/Message';
import SelectableMessage from './SelectableMessage';
import FloatingActionButton from '../../shared/components/FloatingActionButton';
import LoadingMessage from '../../shared/components/LoadingMessage';
import { sentenceApi } from '../api/sentenceApi';
import { chatApi } from '../../shared/api/chatApi';
import { TrainingThread, InteractionRecord } from '../../shared/types';

interface InteractionHistoryViewProps {
  personaId: string;
  personaName: string;
  onSwitchToMode?: (mode: 'interpretation' | 'proceed' | 'sentence') => void;
  onGenerateNewInterpretation?: () => void;
  onRefreshThreads?: () => void;
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  background: white;
`;

const HistorySection = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
`;

const HistoryContent = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 20px;
`;

const EmptyHistory = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
  color: #999;
  font-size: 14px;
  text-align: center;
`;

const AccordionContainer = styled.div`
  width: 100%;
`;

const AccordionItem = styled.div`
  margin-bottom: 16px;
  border: 1px solid #e0e0e0;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);
`;

const AccordionHeader = styled.div<{ $isExpanded: boolean; $threadType: string }>`
  padding: 16px 20px;
  cursor: pointer;
  background: ${props => {
    if (props.$threadType === 'interpretation') return '#f8f9fa';
    if (props.$threadType === 'proceed') return '#fff3e0';
    if (props.$threadType === 'sentence') return '#e8f5e8';
    return '#f8f9fa';
  }};
  border-bottom: ${props => props.$isExpanded ? '1px solid #e0e0e0' : 'none'};
  transition: all 0.2s ease;
  display: flex;
  justify-content: space-between;
  align-items: center;
  
  &:hover {
    background: ${props => {
      if (props.$threadType === 'interpretation') return '#f1f3f4';
      if (props.$threadType === 'proceed') return '#ffeaa7';
      if (props.$threadType === 'sentence') return '#dcedc8';
      return '#f1f3f4';
    }};
  }
`;

const ThreadInfo = styled.div`
  display: flex;
  flex-direction: column;
  gap: 4px;
`;

const ThreadTitle = styled.div`
  font-weight: 600;
  font-size: 16px;
  color: #333;
`;

const ThreadMeta = styled.div`
  font-size: 12px;
  color: #666;
  display: flex;
  gap: 12px;
`;

const ThreadTypeTag = styled.span<{ $threadType: string }>`
  padding: 2px 8px;
  border-radius: 12px;
  font-size: 11px;
  font-weight: 500;
  background: ${props => {
    if (props.$threadType === 'interpretation') return '#6c757d';
    if (props.$threadType === 'proceed') return '#ff9800';
    if (props.$threadType === 'sentence') return '#4caf50';
    return '#6c757d';
  }};
  color: white;
`;

const ExpandIcon = styled.span<{ $isExpanded: boolean }>`
  transition: transform 0.2s ease;
  transform: ${props => props.$isExpanded ? 'rotate(90deg)' : 'rotate(0deg)'};
  font-size: 14px;
  color: #666;
`;

const ThreadContent = styled.div<{ $isExpanded: boolean }>`
  max-height: ${props => props.$isExpanded ? '400px' : '0'};
  overflow: hidden;
  transition: max-height 0.3s ease;
  background: white;
`;

const MessagesContainer = styled.div`
  padding: 16px 20px;
  max-height: 300px;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

const ThreadFooter = styled.div`
  padding: 12px 20px;
  border-top: 1px solid #f0f0f0;
  display: flex;
  gap: 8px;
  justify-content: flex-end;
`;

const ActionButton = styled.button<{ $variant?: 'primary' | 'secondary' }>`
  padding: 6px 12px;
  border: none;
  border-radius: 6px;
  font-size: 12px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  
  ${props => props.$variant === 'primary' ? `
    background: #6c757d;
    color: white;
    
    &:hover {
      background: #5a6268;
    }
  ` : `
    background: #f8f9fa;
    color: #6c757d;
    border: 1px solid #e0e0e0;
    
    &:hover {
      background: #e9ecef;
    }
  `}
`;

// Î≥µÏÇ¨ ÏïåÎ¶ºÏùÑ ÏúÑÌïú ÌÜ†Ïä§Ìä∏ Ïª¥Ìè¨ÎÑåÌä∏
const Toast = styled.div<{ show: boolean }>`
  position: fixed;
  top: 20px;
  right: 20px;
  background: #6c757d;
  color: white;
  padding: 12px 20px;
  border-radius: 8px;
