const InteractionHistoryView: React.FC<InteractionHistoryViewProps> = ({
  personaId,
  personaName,
  onSwitchToMode,
  onGenerateNewInterpretation,
  onRefreshThreads,
}) => {
  const [expandedThreads, setExpandedThreads] = useState<Set<string>>(new Set());
  const [interactionRecord, setInteractionRecord] = useState<InteractionRecord | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');

  useEffect(() => {
    loadInteractionRecord();
  }, [personaId]);

  const loadInteractionRecord = async () => {
    if (!personaId) return;
    
    setIsLoading(true);
    try {
      const response = await chatApi.getInteractionRecord(personaId);
      if (response.data) {
        setInteractionRecord(response.data);
      } else {
        setInteractionRecord(null);
      }
    } catch (error) {
      console.error('ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ù Î°úÎìú Ïã§Ìå®:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleThreadToggle = (threadId: string) => {
    const newExpanded = new Set(expandedThreads);
    if (newExpanded.has(threadId)) {
      newExpanded.delete(threadId);
    } else {
      newExpanded.add(threadId);
    }
    setExpandedThreads(newExpanded);
  };

  const formatTime = (timestamp: string) => {
    const date = new Date(timestamp);
    return date.toLocaleString('ko-KR', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getThreadTypeName = (threadType: string) => {
    switch (threadType) {
      case 'interpretation': return 'Ìï¥ÏÑù';
      case 'proceed': return 'ÎÇòÏïÑÍ∞ÄÍ∏∞';
      case 'sentence': return 'Î¨∏Ïû•';
      default: return threadType;
    }
  };

  // ÌÜ†Ïä§Ìä∏ Î©îÏãúÏßÄ ÌëúÏãú Ìï®Ïàò
  const showCopyToast = (message: string) => {
    setToastMessage(message);
    setShowToast(true);
    setTimeout(() => setShowToast(false), 3000);
  };

  const handleSwitchToThread = (thread: TrainingThread) => {
    if (onSwitchToMode) {
      onSwitchToMode(thread.thread_type as 'interpretation' | 'proceed' | 'sentence');
    }
    showCopyToast(`${getThreadTypeName(thread.thread_type)} Î™®ÎìúÎ°ú Ï†ÑÌôòÎêòÏóàÏäµÎãàÎã§`);
  };

  const handleCopyThreadContent = async (thread: TrainingThread) => {
    const content = thread.messages.map(msg => 
      `${msg.role === 'user' ? 'ÏÇ¨Ïö©Ïûê' : 'AI'}: ${msg.content}`
    ).join('\n\n');
    
    try {
      await navigator.clipboard.writeText(content);
      showCopyToast('Ïä§Î†àÎìú ÎÇ¥Ïö©Ïù¥ Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§');
    } catch (error) {
      console.error('Î≥µÏÇ¨ Ïã§Ìå®:', error);
      showCopyToast('Î≥µÏÇ¨Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
    }
  };

  // Ïä§Î†àÎìúÎ•º ÏãúÍ∞Ñ ÏàúÏúºÎ°ú Ï†ïÎ†¨ (ÏµúÏã† Ïàú)
  const sortedThreads = interactionRecord?.threads.sort((a, b) => 
    new Date(b.created_at).getTime() - new Date(a.created_at).getTime()
  ) || [];

  return (
    <Container>
      <Toast show={showToast}>
        {toastMessage}
      </Toast>
      
      <HistorySection>
        <HistoryContent>
          {isLoading ? (
            <EmptyHistory>
              <div style={{ fontSize: '24px', marginBottom: '8px' }}>‚è≥</div>
              <div>ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...</div>
            </EmptyHistory>
          ) : !interactionRecord || sortedThreads.length === 0 ? (
            <EmptyHistory>
              <div style={{ fontSize: '24px', marginBottom: '8px' }}>üìã</div>
              <div>ÏïÑÏßÅ ÏÉÅÌò∏ÏûëÏö© Í∏∞Î°ùÏù¥ ÏóÜÏäµÎãàÎã§</div>
              <div style={{ fontSize: '12px', marginTop: '4px' }}>
                Ìï¥ÏÑù, ÎÇòÏïÑÍ∞ÄÍ∏∞, Î¨∏Ïû•ÏùÑ ÏÉùÏÑ±ÌïòÎ©¥ Í∏∞Î°ùÏù¥ Ïó¨Í∏∞Ïóê ÌëúÏãúÎê©ÎãàÎã§
              </div>
            </EmptyHistory>
          ) : (
            <AccordionContainer>
              {sortedThreads.map((thread) => (
                <AccordionItem key={thread.id}>
                  <AccordionHeader
                    $isExpanded={expandedThreads.has(thread.id)}
                    $threadType={thread.thread_type}
                    onClick={() => handleThreadToggle(thread.id)}
                  >
                    <ThreadInfo>
                      <ThreadTitle>
                        <ThreadTypeTag $threadType={thread.thread_type}>
                          {getThreadTypeName(thread.thread_type)}
                        </ThreadTypeTag>
                        {' '}Ïä§Î†àÎìú
                      </ThreadTitle>
                      <ThreadMeta>
                        <span>üìÖ {formatTime(thread.created_at)}</span>
                        <span>üí¨ {thread.messages.length}Í∞ú Î©îÏãúÏßÄ</span>
                        <span>üïí {formatTime(thread.updated_at)}</span>
                      </ThreadMeta>
                    </ThreadInfo>
                    <ExpandIcon $isExpanded={expandedThreads.has(thread.id)}>
                      ‚ñ∂
                    </ExpandIcon>
                  </AccordionHeader>
                  
                  <ThreadContent $isExpanded={expandedThreads.has(thread.id)}>
                    <MessagesContainer>
                      {thread.messages.slice(0, 3).map((message, index) => (
                        <div key={index} style={{ fontSize: '14px' }}>
                          <div style={{ 
                            fontWeight: message.role === 'user' ? '600' : '500',
                            color: message.role === 'user' ? '#333' : '#666',
                            marginBottom: '4px'
                          }}>
                            {message.role === 'user' ? 'üë§ ÏÇ¨Ïö©Ïûê' : `ü§ñ ${personaName}`}
                          </div>
                          <div style={{ 
                            paddingLeft: '16px',
                            lineHeight: '1.4',
                            color: '#555'
                          }}>
                            {message.content.length > 150 
                              ? `${message.content.substring(0, 150)}...` 
                              : message.content
                            }
                          </div>
                        </div>
                      ))}
                      {thread.messages.length > 3 && (
                        <div style={{ fontSize: '12px', color: '#999', textAlign: 'center' }}>
                          ... Í∑∏Î¶¨Í≥† {thread.messages.length - 3}Í∞ú Î©îÏãúÏßÄ Îçî
                        </div>
                      )}
                    </MessagesContainer>
                    
                    <ThreadFooter>
                      <ActionButton 
                        onClick={() => handleCopyThreadContent(thread)}
                      >
                        üìã Î≥µÏÇ¨
                      </ActionButton>
                      <ActionButton 
                        $variant="primary"
                        onClick={() => handleSwitchToThread(thread)}
                      >
                        üîÑ Ïù¥Îèô
                      </ActionButton>
                    </ThreadFooter>
                  </ThreadContent>
                </AccordionItem>
              ))}
            </AccordionContainer>
          )}
        </HistoryContent>
      </HistorySection>
    </Container>
  );
};

export default InteractionHistoryView;
