  personaName,
  onSendMessage,
  onEditMessage,
  isLoading,
  messages,
  onGenerateNewInterpretation,
  onSwitchToMode,
  personaId,
  onRefreshThreads,
  selectedThread: propSelectedThread,
}) => {
  const [selectedThread, setSelectedThread] = useState<TrainingThread | null>(propSelectedThread || null);
  const chatMessagesRef = useRef<HTMLDivElement>(null);
  const messageInputRef = useRef<MessageInputRef>(null);
  const [showToast, setShowToast] = useState(false);
  const [toastMessage, setToastMessage] = useState('');
  const [editingMessageIndex, setEditingMessageIndex] = useState<number | null>(null);

  // Î¨∏Ïû• ÏÑ†ÌÉù Í¥ÄÎ†® ÏÉÅÌÉú
  const [selectedSentences, setSelectedSentences] = useState<Set<string>>(new Set());
  const [highlightedSentences, setHighlightedSentences] = useState<Set<string>>(new Set());
  const [memos, setMemos] = useState<Record<string, string>>({});
  const [isSentenceModeActive, setIsSentenceModeActive] = useState(false);

  const sentenceDataHook = useSentenceData();

  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [messages]);

  // ÏÑ†ÌÉùÎêú Ïä§Î†àÎìú Î≥ÄÍ≤Ω Ïãú Ï≤òÎ¶¨ (ÎÇòÏïÑÍ∞ÄÍ∏∞ÏôÄ ÎèôÏùº)
  useEffect(() => {
    if (propSelectedThread) {
      console.log('ÏÑ†ÌÉùÎêú Ìï¥ÏÑù Ïä§Î†àÎìú Î≥ÄÍ≤Ω:', propSelectedThread.id);
      setSelectedThread(propSelectedThread);

      // Ïä§Î†àÎìúÎ≥Ñ Î¨∏Ïû• Îç∞Ïù¥ÌÑ∞ Î°úÎî©
      loadThreadSentenceData(propSelectedThread.id);
    }
  }, [propSelectedThread]);

  // Ïä§Î†àÎìúÎ≥Ñ Î¨∏Ïû• Îç∞Ïù¥ÌÑ∞ Î°úÎî© (ÎÇòÏïÑÍ∞ÄÍ∏∞ÏôÄ ÎèôÏùºÌïú Î∞©Ïãù)
  const loadThreadSentenceData = async (threadId: string) => {
      try {
      console.log('=== Ìï¥ÏÑù Ïä§Î†àÎìú Îç∞Ïù¥ÌÑ∞ Î°úÎî© ÏãúÏûë ===');
      console.log('Ïä§Î†àÎìú ID:', threadId);

      // Î∞±ÏóîÎìúÏóêÏÑú Ïä§Î†àÎìú Îç∞Ïù¥ÌÑ∞ Î°úÎî©
        const data = await sentenceApi.getThreadSentenceData(threadId);

      console.log('Î°úÎî©Îêú Î©îÎ™®:', Object.keys(data.memos).length, 'Í∞ú');
      console.log('Î°úÎî©Îêú ÌïòÏù¥ÎùºÏù¥Ìä∏:', data.highlights.length, 'Í∞ú');

      // Î∞±ÏóîÎìú Îç∞Ïù¥ÌÑ∞Î°ú ÏÉÅÌÉú ÏÑ§Ï†ï
          setMemos(data.memos);
          setHighlightedSentences(new Set(data.highlights));

        console.log('=== Ìï¥ÏÑù Ïä§Î†àÎìú Îç∞Ïù¥ÌÑ∞ Î°úÎî© ÏôÑÎ£å ===');
      } catch (error) {
      console.error('Ïä§Î†àÎìú Î¨∏Ïû• Îç∞Ïù¥ÌÑ∞ Î°úÎî© Ïã§Ìå®:', error);
        // Ïã§Ìå® Ïãú Îπà ÏÉÅÌÉúÎ°ú Ï¥àÍ∏∞Ìôî
        setMemos({});
        setHighlightedSentences(new Set());
      }
    };

  // ÌÜ†Ïä§Ìä∏ Î©îÏãúÏßÄ ÌëúÏãú Ìï®Ïàò
  const showCopyToast = (message: string) => {
    setToastMessage(message);
    setShowToast(true);
    setTimeout(() => setShowToast(false), 3000);
  };

  // ÌÖçÏä§Ìä∏ Î≥µÏÇ¨ Ïú†Ìã∏Î¶¨Ìã∞ Ìï®Ïàò
  const copyToClipboard = async (text: string, successMessage: string) => {
    try {
      await navigator.clipboard.writeText(text);
      showCopyToast(successMessage);
    } catch (error) {
      console.error('Î≥µÏÇ¨ Ïã§Ìå®:', error);
      // ÎåÄÏ≤¥ Î≥µÏÇ¨ Î∞©Î≤ï
      try {
        const textArea = document.createElement('textarea');
        textArea.value = text;
        textArea.style.position = 'fixed';
        textArea.style.left = '-999999px';
        textArea.style.top = '-999999px';
        document.body.appendChild(textArea);
        textArea.focus();
        textArea.select();
        document.execCommand('copy');
        document.body.removeChild(textArea);
        showCopyToast(successMessage);
      } catch (fallbackError) {
        console.error('ÎåÄÏ≤¥ Î≥µÏÇ¨ Î∞©Î≤ïÎèÑ Ïã§Ìå®:', fallbackError);
        showCopyToast('Î≥µÏÇ¨ Ïã§Ìå®');
      }
    }
  };

  // Î©îÏãúÏßÄ Î≥µÏÇ¨ Í∏∞Îä•
  const handleCopyMessage = async (messageContent: string) => {
    await copyToClipboard(messageContent, 'Î©îÏãúÏßÄÍ∞Ä Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§');
  };

  // Î©îÏãúÏßÄ ÏàòÏ†ï ÏãúÏûë
  const handleStartEdit = (messageIndex: number) => {
    setEditingMessageIndex(messageIndex);
  };

  // Î©îÏãúÏßÄ ÏàòÏ†ï ÏôÑÎ£å
  const handleEditMessage = async (messageIndex: number, newContent: string) => {
    if (onEditMessage) {
      const success = await onEditMessage(messageIndex, newContent);
      if (success) {
        setEditingMessageIndex(null);
        showCopyToast('Î©îÏãúÏßÄÍ∞Ä ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§');
      } else {
        showCopyToast('Î©îÏãúÏßÄ ÏàòÏ†ïÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
      }
    }
  };

  // Î©îÏãúÏßÄ ÏàòÏ†ï Ï∑®ÏÜå
  const handleCancelEdit = () => {
    setEditingMessageIndex(null);
  };

  // Î¨∏Ïû• ÏÑ†ÌÉù Ìï∏Îì§Îü¨Îì§
  const handleToggleSelect = (sentenceId: string) => {
    setSelectedSentences(prev => {
      const newSet = new Set(prev);
      if (newSet.has(sentenceId)) {
        newSet.delete(sentenceId);
      } else {
        newSet.add(sentenceId);
      }
      return newSet;
    });
  };

  const handleMemoChange = async (sentenceId: string, memo: string) => {
    console.log('=== Ìï¥ÏÑù Î©îÎ™® Ï†ÄÏû• ÎîîÎ≤ÑÍπÖ ===');
    console.log('Î¨∏Ïû• ID:', sentenceId);
    console.log('Î©îÎ™® ÎÇ¥Ïö©:', memo);
    console.log('ÌéòÎ•¥ÏÜåÎÇò ID:', personaId);
    console.log('selectedThread:', selectedThread?.id);

    // sentenceIdÎ°úÎ∂ÄÌÑ∞ Ïã§Ï†ú Î¨∏Ïû• ÎÇ¥Ïö© Ï∞æÍ∏∞
    const [timestamp, , sentenceIndex] = sentenceId.split('_');
    let sentenceContent = '';
    if (selectedThread?.messages) {
      const message = selectedThread.messages.find(m => m.timestamp === timestamp);
      if (message) {
        const sentences = message.content.split(/[\n.]+/).map(s => s.trim()).filter(s => s.length > 0);
        sentenceContent = sentences[parseInt(sentenceIndex)] || '';
      }
    }

    console.log('Ï∂îÏ∂úÎêú Î¨∏Ïû• ÎÇ¥Ïö©:', sentenceContent);

    // Ïä§Î†àÎìú ID Í≤∞Ï†ï Î°úÏßÅ Í∞úÏÑ†
    let threadIdToUse = selectedThread?.id;

    // Ïä§Î†àÎìúÍ∞Ä ÏóÜÎäî Í≤ΩÏö∞ Ìè¥Î∞± Î°úÏßÅ
    if (!threadIdToUse) {
      // Ìï¥ÏÑù Í∏∞Î∞ò ÎåÄÌôîÏù∏ Í≤ΩÏö∞ Í∏∞Î≥∏ Ïä§Î†àÎìú ID ÏÉùÏÑ±
      if (interpretation) {
        threadIdToUse = `interpretation_chat_${personaId}`;
      } else {
        console.warn('Ïä§Î†àÎìú IDÎ•º Í≤∞Ï†ïÌï† Ïàò ÏóÜÏùå, Î©îÎ™® Ï†ÄÏû• Ï§ëÎã®');
        throw new Error('Ïä§Î†àÎìú Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§');
      }
    }

    console.log('ÏµúÏ¢Ö Ïä§Î†àÎìú ID:', threadIdToUse);

    try {
      // Í∞úÏÑ†Îêú useSentenceData ÌõÖÏùò handleMemoChange ÏÇ¨Ïö©
      if (sentenceDataHook?.handleMemoChange) {
        await sentenceDataHook.handleMemoChange(sentenceId, memo, threadIdToUse, 'interpretation');
      } else {
        // Ìè¥Î∞±: ÏßÅÏ†ë API Ìò∏Ï∂ú
        await sentenceApi.createOrUpdateMemo({
          sentence_id: sentenceId,
          thread_id: threadIdToUse,
          thread_type: 'interpretation',
          content: memo,
          sentence_content: sentenceContent,
          source_message_id: `interpretation_${personaId}`,
          // Î∞±ÏóîÎìú ÏûêÎèô Ï†ÄÏû•ÏùÑ ÏúÑÌïú Ï∂îÍ∞Ä Ï†ïÎ≥¥
          persona_id: personaId,
          tags: ['interpretation', ...(personaId ? [personaId] : [])],
          source_conversation_id: threadIdToUse,
          source_thread_id: threadIdToUse,
        } as any);

        // ÏÑ±Í≥µ Ïãú Î°úÏª¨ ÏÉÅÌÉúÎèÑ ÏóÖÎç∞Ïù¥Ìä∏
        setMemos(prev => ({
          ...prev,
          [sentenceId]: memo
        }));
      }

      console.log('Ìï¥ÏÑù Î©îÎ™® Ï†ÄÏû• ÏÑ±Í≥µ');
      showCopyToast('Î©îÎ™®Í∞Ä Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§');
    } catch (error) {
      console.error('Ìï¥ÏÑù Î©îÎ™® Ï†ÄÏû• Ïã§Ìå®:', error);
      showCopyToast('Î©îÎ™® Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
      throw error; // SelectableSentenceÏóêÏÑú ÏóêÎü¨ Ï≤òÎ¶¨Ìï† Ïàò ÏûàÎèÑÎ°ù
    }

    console.log('=== Ìï¥ÏÑù Î©îÎ™® Ï†ÄÏû• ÎîîÎ≤ÑÍπÖ ÎÅù ===');
  };

  const handleDeleteMemo = async (sentenceId: string) => {
    try {
      // Î°úÏª¨ ÏÉÅÌÉúÏóêÏÑú Î©îÎ™® ÏÇ≠Ï†ú
      setMemos(prev => {
        const newMemos = { ...prev };
        delete newMemos[sentenceId];
        return newMemos;
      });
      setHighlightedSentences(prev => {
        const newSet = new Set(prev);
        newSet.delete(sentenceId);
        return newSet;
      });

      // Î∞±ÏóîÎìú API Ìò∏Ï∂ú (ÎπÑÎèôÍ∏∞, Ïã§Ìå®Ìï¥ÎèÑ Î°úÏª¨ ÏÇ≠Ï†úÎäî Ïú†ÏßÄ)
      sentenceApi.deleteMemo(sentenceId).catch(error => {
        console.warn('Î∞±ÏóîÎìú Î©îÎ™® ÏÇ≠Ï†ú Ïã§Ìå® (Î°úÏª¨ ÏÇ≠Ï†úÎäî ÏÑ±Í≥µ):', error);
      });

      showCopyToast('Î©îÎ™®Í∞Ä ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§');
    } catch (error) {
      console.error('Î©îÎ™® ÏÇ≠Ï†ú Ïã§Ìå®:', error);
      showCopyToast('Î©îÎ™® ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
    }
  };

  // Í∏∞Ï°¥ Î°úÏßÅÏùÑ useSentenceMenu ÌõÖÏúºÎ°ú ÍµêÏ≤¥
  const sentenceMenuActions = useSentenceMenu({
    personaId: personaId || '',
    threadType: 'interpretation',
    selectedThread,
    memos,
    highlightedSentences,
    setMemos,
    setHighlightedSentences
  });

  // FloatingActionButton Î©îÎâ¥ Ïï°ÏÖò Ï≤òÎ¶¨ (Î¨∏Ïû•ÏÑ†ÌÉù Î™®ÎìúÏö©)
  const handleMenuAction = async (action: 'sendToInput' | 'saveToVault' | 'addMemo' | 'highlight' | 'copy') => {
    const selectedIds = Array.from(selectedSentences);
    const selectedTexts = selectedIds.map(id => {
      // sentenceIdÏóêÏÑú Ïã§Ï†ú Î¨∏Ïû• ÌÖçÏä§Ìä∏Î•º Ï∞æÏïÑÎÇ¥Îäî Î°úÏßÅ
      const [timestamp, , sentenceIndex] = id.split('_');
      const message = selectedThread?.messages.find(m => m.timestamp === timestamp);
      if (message) {
        const sentences = message.content.split(/[\n.]+/).map(s => s.trim()).filter(s => s.length > 0);
        return sentences[parseInt(sentenceIndex)] || '';
      }
      return '';
    }).filter(text => text.length > 0);

    // ÏÉàÎ°úÏö¥ ÌÜµÌï©Îêú Î©îÎâ¥ Ïï°ÏÖò ÏÇ¨Ïö©
    await sentenceMenuActions.handleMenuAction(action, selectedIds, selectedTexts, messageInputRef);
  };

  // Î¨∏Ïû•ÏÑ†ÌÉù Î™®Îìú ÌÜ†Í∏Ä
  const handleToggleSentenceMode = () => {
    setIsSentenceModeActive(prev => !prev);
  };

  // Ìñ•ÌõÑ ÌôïÏû•ÏùÑ ÏúÑÌïú Î≤ÑÌäº Ìï∏Îì§Îü¨Îì§
  const handleSunAction = async (messageContent: string) => {
    // Ï†ÑÎã¨Î∞õÏùÄ Î©îÏãúÏßÄ ÎÇ¥Ïö©ÏùÑ Ìï¥ÏÑù Ïä§Î†àÎìúÏóê Ï†ÄÏû•
    console.log('=== handleSunAction ÏãúÏûë ===');
    console.log('personaId:', personaId);
    console.log('messageContent:', messageContent.substring(0, 100) + '...');

    if (!personaId) {
      console.error('personaIdÍ∞Ä ÏóÜÏäµÎãàÎã§');
      showCopyToast('ÌéòÎ•¥ÏÜåÎÇò IDÎ•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§');
      return;
    }

    if (!messageContent) {
      console.error('Ï†ÄÏû•Ìï† Î©îÏãúÏßÄ ÎÇ¥Ïö©Ïù¥ ÏóÜÏäµÎãàÎã§');
      showCopyToast('Ï†ÄÏû•Ìï† Î©îÏãúÏßÄ ÎÇ¥Ïö©Ïù¥ ÏóÜÏäµÎãàÎã§');
      return;
    }

    try {
      console.log('ÏµúÏ¢Ö Ï†ÄÏû•Ìï† ÎÇ¥Ïö©:', {
        personaId,
        contentLength: messageContent.length,
        contentPreview: messageContent.substring(0, 100) + '...'
      });

      // chatApiÎ•º ÌÜµÌï¥ Î©îÏãúÏßÄ ÎÇ¥Ïö©ÏùÑ Ìï¥ÏÑù Ïä§Î†àÎìúÏóê Ï†ÄÏû•
      const { chatApi } = await import('../../shared/api/chatApi');
      console.log('chatApi Í∞ÄÏ†∏Ïò¥');

      const response = await chatApi.saveCurrentAsInterpretation(personaId, messageContent);
      console.log('API ÏùëÎãµ:', response);

      if (response.data && response.data.persona_id && response.data.interpretation) {
        console.log('Ìï¥ÏÑù Ï†ÄÏû• ÏÑ±Í≥µ:', response.data);
        // Ïä§Î†àÎìú ÏÉàÎ°úÍ≥†Ïπ®
        if (onRefreshThreads) {
          console.log('Ïä§Î†àÎìú ÏÉàÎ°úÍ≥†Ïπ® ÏãúÏûë');
          onRefreshThreads();
          console.log('Ïä§Î†àÎìú ÏÉàÎ°úÍ≥†Ïπ® ÏôÑÎ£å');
        }
        showCopyToast('Î©îÏãúÏßÄÍ∞Ä Ìï¥ÏÑù Ïä§Î†àÎìúÏóê Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§');
      } else if (response.error) {
        console.error('API Ïò§Î•ò:', response.error);
        showCopyToast(`Ìï¥ÏÑù Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§: ${response.error}`);
      } else {
        console.error('ÏòàÏÉÅÏπò Î™ªÌïú ÏùëÎãµ Íµ¨Ï°∞:', response);
        // ÏùëÎãµÏù¥ ÏûàÏßÄÎßå ÏòàÏÉÅÌïú Íµ¨Ï°∞Í∞Ä ÏïÑÎãå Í≤ΩÏö∞ÏóêÎèÑ ÏÑ±Í≥µÏúºÎ°ú Ï≤òÎ¶¨
        if (response.data) {
          console.log('ÏùëÎãµÏù¥ ÏûàÏúºÎØÄÎ°ú ÏÑ±Í≥µÏúºÎ°ú Ï≤òÎ¶¨');
          if (onRefreshThreads) {
            console.log('Ïä§Î†àÎìú ÏÉàÎ°úÍ≥†Ïπ® ÏãúÏûë');
            onRefreshThreads();
            console.log('Ïä§Î†àÎìú ÏÉàÎ°úÍ≥†Ïπ® ÏôÑÎ£å');
          }
          showCopyToast('Î©îÏãúÏßÄÍ∞Ä Ìï¥ÏÑù Ïä§Î†àÎìúÏóê Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§');
        } else {
          showCopyToast('Ìï¥ÏÑù Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
        }
      }
    } catch (error) {
      console.error('Ìï¥ÏÑù Ï†ÄÏû• Ï§ë ÏòàÏô∏ Î∞úÏÉù:', error);
      showCopyToast(`Ìï¥ÏÑù Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§: ${error instanceof Error ? error.message : String(error)}`);
    }

    console.log('=== handleSunAction ÏôÑÎ£å ===');
  };

  const handlePersonAction = async (messageContent: string) => {
    // Ï†ÑÎã¨Î∞õÏùÄ Î©îÏãúÏßÄ ÎÇ¥Ïö©ÏùÑ ÎÇòÏïÑÍ∞ÄÍ∏∞ Ïä§Î†àÎìúÏóê Ï†ÄÏû•
    if (!personaId) return;

    if (!messageContent) {
      showCopyToast('Ï†ÄÏû•Ìï† Î©îÏãúÏßÄ ÎÇ¥Ïö©Ïù¥ ÏóÜÏäµÎãàÎã§');
      return;
    }

    try {
      // chatApiÎ•º ÌÜµÌï¥ Î©îÏãúÏßÄ ÎÇ¥Ïö©ÏùÑ ÎÇòÏïÑÍ∞ÄÍ∏∞ Ïä§Î†àÎìúÏóê Ï†ÄÏû•
      const { chatApi } = await import('../../shared/api/chatApi');
      const response = await chatApi.saveCurrentAsProceed(personaId, messageContent);

      if (response.data) {
        // Ïä§Î†àÎìú ÏÉàÎ°úÍ≥†Ïπ®
        if (onRefreshThreads) {
          onRefreshThreads();
        }
        showCopyToast('Î©îÏãúÏßÄÍ∞Ä ÎÇòÏïÑÍ∞ÄÍ∏∞ Ïä§Î†àÎìúÏóê Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§');
      } else {
        showCopyToast('ÎÇòÏïÑÍ∞ÄÍ∏∞ Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
      }
    } catch (error) {
      console.error('ÎÇòÏïÑÍ∞ÄÍ∏∞ Ï†ÄÏû• Ï§ë Ïò§Î•ò:', error);
      showCopyToast('ÎÇòÏïÑÍ∞ÄÍ∏∞ Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
    }
  };

  const handleDocumentAction = async (messageContent: string) => {
    // Ï†ÑÎã¨Î∞õÏùÄ Î©îÏãúÏßÄ ÎÇ¥Ïö©ÏùÑ Î¨∏Ïû• Ïä§Î†àÎìúÏóê Ï†ÄÏû•
    if (!personaId) return;

    if (!messageContent) {
      showCopyToast('Ï†ÄÏû•Ìï† Î©îÏãúÏßÄ ÎÇ¥Ïö©Ïù¥ ÏóÜÏäµÎãàÎã§');
      return;
    }

    try {
      // chatApiÎ•º ÌÜµÌï¥ Î©îÏãúÏßÄ ÎÇ¥Ïö©ÏùÑ Î¨∏Ïû• Ïä§Î†àÎìúÏóê Ï†ÄÏû•
      const { chatApi } = await import('../../shared/api/chatApi');
      const response = await chatApi.saveCurrentAsSentence(personaId, messageContent);

      if (response.data) {
        // Ïä§Î†àÎìú ÏÉàÎ°úÍ≥†Ïπ®
        if (onRefreshThreads) {
          onRefreshThreads();
        }
        showCopyToast('Î©îÏãúÏßÄÍ∞Ä Î¨∏Ïû• Ïä§Î†àÎìúÏóê Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§');
      } else {
        showCopyToast('Î¨∏Ïû• Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
      }
    } catch (error) {
      console.error('Î¨∏Ïû• Ï†ÄÏû• Ï§ë Ïò§Î•ò:', error);
      showCopyToast('Î¨∏Ïû• Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§');
    }
  };

  return (
    <Container>
      <Toast show={showToast}>
        {toastMessage}
      </Toast>

      <ChatSection>
        <ChatMessages ref={chatMessagesRef}>
          {!selectedThread || selectedThread.messages.length === 0 ? (
            <EmptyChat>
              <div style={{ fontSize: '24px', marginBottom: '8px' }}>üí¨</div>
              <div>Ìï¥ÏÑùÏóê ÎåÄÌï¥ Îçî ÏûêÏÑ∏Ìûà ÏßàÎ¨∏Ìï¥Î≥¥ÏÑ∏Ïöî</div>
              <div style={{ fontSize: '12px', marginTop: '4px' }}>
                {personaName}ÏôÄ ÎåÄÌôîÎ•º ÎÇòÎàå Ïàò ÏûàÏäµÎãàÎã§
              </div>
            </EmptyChat>
          ) : (
            selectedThread.messages.map((message, index) => 
              message.role === 'user' ? (
                <Message
                  key={`interpretation_${index}`}
                  message={{
                    ...message,
                    persona_id: undefined,
                    persona_name: undefined,
                  }}
                  personas={{}}
                  showActionButtons={true}
                  onCopy={() => handleCopyMessage(message.content)}
                  onEdit={() => handleStartEdit(index)}
                  isEditing={editingMessageIndex === index}
                  onEditSave={(newContent) => handleEditMessage(index, newContent)}
                  onEditCancel={handleCancelEdit}
                />
              ) : (
                <SelectableMessage
                  key={`interpretation_${index}`}
                  message={{
                    ...message,
                    persona_id: 'current_persona',
                    persona_name: personaName,
                  }}
                  personas={{
                    current_persona: {
                      name: personaName,
                      description: '',
                      color: '#6c757d',
                      prompt: '',
                      category: '',
                      subcategory: ''
                    }
                  }}
                  selectedSentences={isSentenceModeActive ? selectedSentences : new Set()}
                  highlightedSentences={highlightedSentences}
                  memos={memos}
                  onToggleSelect={isSentenceModeActive ? handleToggleSelect : () => {}}
                  onMemoChange={handleMemoChange}
                  onDeleteMemo={handleDeleteMemo}
                  showSentenceSelector={isSentenceModeActive}
                  showActionButtons={true}
                  onCopy={() => handleCopyMessage(message.content)}
                  onSunAction={handleSunAction}
                  onPersonAction={handlePersonAction}
                  onDocumentAction={handleDocumentAction}
                />
              )
            )
          )}

          {isLoading && (
            <LoadingMessage 
              personaName={personaName}
              personaColor="#6c757d"
            />
          )}
        </ChatMessages>

        <ChatInputSection>
          <MessageInput
            ref={messageInputRef}
            onSendMessage={onSendMessage}
            disabled={isLoading}
            placeholder={`${personaName}ÏóêÍ≤å Ìï¥ÏÑùÏóê ÎåÄÌï¥ ÏßàÎ¨∏Ìï¥Î≥¥ÏÑ∏Ïöî...`}
            onToggleSentenceMode={handleToggleSentenceMode}
            isSentenceModeActive={isSentenceModeActive}
            hasSelectedSentences={selectedSentences.size > 0}
            currentInterpretation={interpretation}
            personaId={personaId}
            onGenerateProceed={handlePersonAction}
            onGenerateSentence={handleDocumentAction}
            currentChatMessages={messages}
            onRefreshThreads={onRefreshThreads}
          />
        </ChatInputSection>
      </ChatSection>

      <FloatingActionButton
        show={isSentenceModeActive && selectedSentences.size > 0}
        onMenuAction={handleMenuAction}
        personaId={personaId}
        currentInterpretation={interpretation}
      />
    </Container>
  );
};

export default InterpretationView;
