import React from 'react';
import { Persona, Conversation } from '../types';
import {
  Sidebar,
  SidebarHeader,
  SidebarTitle,
  SidebarSubtitle,
  SidebarContent,
  PersonaItem,
  PersonaName,
  PersonaDescription,
  ConversationItem,
  ConversationPreview,
  ConversationTime,
  EmptyState,
  EmptyStateTitle,
  EmptyStateText,
} from '../styles/GlobalStyle';

interface PersonaSidebarProps {
  personas: Record<string, Persona>;
  selectedPersona: string | null;
  onSelectPersona: (personaId: string | null) => void;
  conversations: Record<string, Conversation>;
  onSelectConversation: (conversationId: string) => void;
  selectedConversation: string | null;
}

const PersonaSidebar: React.FC<PersonaSidebarProps> = ({
  personas,
  selectedPersona,
  onSelectPersona,
  conversations,
  onSelectConversation,
  selectedConversation,
}) => {
  const formatTime = (timestamp: string) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('ko-KR', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const truncateText = (text: string, maxLength: number = 60) => {
    return text.length > maxLength ? text.substring(0, maxLength) + '...' : text;
  };

  // 선택된 페르소나의 대화 목록 가져오기
  const getPersonaConversations = (personaId: string) => {
    return Object.values(conversations)
      .filter(conv => conv.persona_id === personaId)
      .sort((a, b) => new Date(b.created_at).getTime() - new Date(a.created_at).getTime());
  };

  // 가장 최근 메시지 가져오기
  const getLastMessage = (conversation: Conversation) => {
    const lastMessage = conversation.messages[conversation.messages.length - 1];
    return lastMessage;
  };

  return (
    <Sidebar width="300px">
      <SidebarHeader>
        <SidebarTitle>페르소나</SidebarTitle>
        <SidebarSubtitle>
          {selectedPersona 
            ? '선택된 페르소나와의 대화 목록' 
            : '페르소나를 선택하여 개별 대화를 시작하세요'
          }
        </SidebarSubtitle>
      </SidebarHeader>

      <SidebarContent>
        {!selectedPersona ? (
          // 페르소나 선택 화면
          <>
            {Object.entries(personas).map(([personaId, persona]) => (
              <PersonaItem
                key={personaId}
                isSelected={false}
                personaColor={persona.color}
                onClick={() => onSelectPersona(personaId)}
              >
                <PersonaName personaColor={persona.color}>
                  {persona.name}
                </PersonaName>
                <PersonaDescription>
                  {persona.description}
                </PersonaDescription>
              </PersonaItem>
            ))}
          </>
        ) : (
          // 선택된 페르소나의 대화 목록
          <>
            {/* 페르소나 정보 */}
            <PersonaItem
              isSelected={true}
              personaColor={personas[selectedPersona]?.color}
              onClick={() => onSelectPersona(null)}
              style={{ marginBottom: '20px' }}
            >
              <PersonaName personaColor={personas[selectedPersona]?.color}>
                {personas[selectedPersona]?.name}
              </PersonaName>
              <PersonaDescription>
                {personas[selectedPersona]?.description}
              </PersonaDescription>
              <div style={{ 
                marginTop: '8px', 
                fontSize: '12px', 
                color: personas[selectedPersona]?.color,
                fontStyle: 'italic'
              }}>
                클릭하여 페르소나 선택 화면으로 돌아가기
              </div>
            </PersonaItem>

            {/* 대화 목록 */}
            <div style={{ borderTop: '1px solid #e1e5e9', paddingTop: '16px' }}>
              <h3 style={{ 
                fontSize: '16px', 
                marginBottom: '12px', 
                color: '#333',
                paddingLeft: '4px'
              }}>
                대화 기록
              </h3>

              {(() => {
                const personaConversations = getPersonaConversations(selectedPersona);
                
                if (personaConversations.length === 0) {
                  return (
                    <EmptyState style={{ padding: '20px 0' }}>
                      <EmptyStateTitle style={{ fontSize: '16px' }}>
                        아직 대화가 없습니다
                      </EmptyStateTitle>
                      <EmptyStateText style={{ fontSize: '13px' }}>
                        메인 화면에서 새로운 질문을 하거나<br />
                        기존 대화를 선택해보세요
                      </EmptyStateText>
                    </EmptyState>
                  );
                }

                return personaConversations.map((conversation) => {
                  const lastMessage = getLastMessage(conversation);
                  const isSelected = selectedConversation === conversation.id;
                  
                  return (
                    <ConversationItem
                      key={conversation.id}
                      isSelected={isSelected}
                      onClick={() => onSelectConversation(conversation.id)}
                    >
                      <ConversationPreview>
                        {lastMessage.role === 'user' 
                          ? `질문: ${truncateText(lastMessage.content, 45)}`
                          : truncateText(lastMessage.content, 50)
                        }
                      </ConversationPreview>
                      <ConversationTime>
                        {formatTime(conversation.created_at)} • {conversation.messages.length}개 메시지
                      </ConversationTime>
                    </ConversationItem>
                  );
                });
              })()}
            </div>
          </>
        )}
      </SidebarContent>
    </Sidebar>
  );
};

export default PersonaSidebar;
